INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('金章宗泰和五年九月戊子', '1205.10.18', '戌时，西北方黑云间有赤气如火，次及西南、正南、东南方皆赤，中有白气贯彻，乍隐乍见。既而为雨，随作风。至二更初，黑云间赤气复起于西北方，及正西、正东、东北，住来游曳，内有白气数道，时复出没。其赤气又满中天，约四更皆散。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('金章宗泰和六年九月乙酉', '1206.10.10', '将五鼓，北方有赤白气数道，起于王良之下，行至北斗开阳、摇光之东。');
