INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES (NULL, '1404', '东方有赤气。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES (NULL, '1404', '夜，艮方有赤气。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES (NULL, '1405', '夜。艮方有赤气。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES (NULL, '1405', '坤方有赤气。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES (NULL, '1405', '夜，寅卯方有赤气。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES (NULL, '1405', '巽方有赤气，金、土星相犯。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES (NULL, '1405', '夜，艮方有赤气。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES (NULL, '1406', '西方有赤气。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES (NULL, '1411', '巽方有淡赤气。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES (NULL, '1415', '月傍有青赤气。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES (NULL, '1415', '日边有青赤气。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES (NULL, '1426', '大风。东北方有赤气。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES (NULL, '1444', '中枢院副使尹重富奴今音都致言：“我于去年十二月晦日，与文恭治共宿一房，夜半忽移我于庭中，恍惚不知所为，俄而开眼视之，则有赤气自庭中冲天，雨傍下雨，数多童僧交驰庭中。更审视之，则赤气与僧人忽不见。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES (NULL, '1507', '而殿下固拒不从，何殿下崇信佛氏，乃至是耶？近者太白昼见，夜有赤气，(大白)〔太白〕阴星。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES (NULL, '1507', '传于政院曰：“读书堂箚子云，夜有赤气，信有是乎？”回启曰：“臣等亦闻前月晦日夜，乾方有赤气如火光，南方云淡色黄。”');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES (NULL, '1509', '东南间连夜，有赤气如电。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES (NULL, '1509', '自三更至五更，北方有赤气。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES (NULL, '1511', '夜坤方有赤气，其上有白气一条。 赤气状如炬，白气状如十字，一丈许。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES (NULL, '1512', '是日初昏，北方有赤气如火。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES (NULL, '1515', '夜，艮方、巽方有赤气。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES (NULL, '1517', '内外晕之西，皆有青赤气，如弛弓外向，内晕南北，皆有珥，珥傍有白气，竝外射至外云而止。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES (NULL, '1520', '全罗道谷城县，夜有赤气浑天，山野皆明，村屋可数，良久而销。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES (NULL, '1520', '承旨尹殷弼、金希寿启曰：“去夜四更，日官来报云：‘南方有赤气甚异。’臣等起而视之，果有赤气浮空，若炬火。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES (NULL, '1520', '参赞官尹殷弼曰：“臣之直宿日四更，日官报云：‘南方有火气。’臣起而亲见之，则果有赤气如炬，若进若退，若灭而还炽，甚为惊异。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES (NULL, '1522', '夜，赤气布天。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明英宗正统十四年十月壬申', '1449', '日初出东方，有黑云五道，中高两头锐而卑，日上黑云如烟，寻发红光，散焰似火。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明英宗正统十四年十月癸亥', '1449.11.1', '夜，昏刻，西南赤黑气如火烟，须臾化苍白气，重迭六道，徐徐北行，至中天而散。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明英宗天顺二年九月辛丑', '1458', '是日，南京日入后，西方有赤气光如火影，至戌时散。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明英宗天顺二年十月己巳', '1458.11.20', '南京日入后，西南方有赤气如火影，光明烛地，至亥时散。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明英宗天顺二年十二月甲子', '1459.1.14', '夜，正北火影中见赤气一道，阔余二尺，直冲天中，约余五十丈，其形上锐，状如立枪。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明英宗天顺三年十二月甲戌', '1460.1.19', '夜，南方火影中见赤气，阔余二尺，长余十丈，其形上锐如立，直冲中天。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES (NULL, '1467', '又有赤光如星象，既而掩蚀大星几尽，二鼓乃灭。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明宪宗成化八年十二月癸酉', '1472', '夜，西南赤气一道，如枪形名曰天锋。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明宪宗成化九年三月甲午', '1473', '山东济南等府，狂风昼晦，咫尺莫辨，复有雷声起于东南，已而红光火烛地，良久乃散。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明宪宗成化九年四月丁卯', '1473', '山东大风，空中红光烛地，有顷昏黑如夜，移时渐复。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明宪宗成化十二年十二月己卯', '1476.12.25', '夜，西北方赤气一道冲天，长五丈许，状如矛锋。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明孝宗弘治二年四月戊戌', '1489', '永和县空中起火，虹光坠地，天鸣如雷。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明孝宗弘治二年八月甲辰', '1489', '晚刻，西方生赤气，南北横天。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明孝宗弘治三年一月初六日', '1490.1.26', '夜半，坎位有红光，自地起直上，森列如植，自昴星相东西半天界，色如暮霞，于中星宿皆红，鸡犬俱鸣。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明孝宗弘治三年六月壬午朔', '1490', '陜西靖虏卫大风，天地昏暗，变为红光，如大良久方息。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明孝宗弘治九年闰三月己未', '1496', '陕西平凉府，东南有星大如月，红光烛地，至西北止，既而天鼓鸣。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明孝宗弘治十四年七月戊申', '1501', '江西南城县，是夜亥刻空中火星如蛇，红光青焰自西流东，约九丈余。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明武宗正德元年正月乙酉', '1506', '夜，崇明空中有红光，曳尾如虹，起东北至西南没，声如雷。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明武宗正德元年正月辛丑', '1506', '凤阳红光发，与日同色，声如雷。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明武宗正德元年三月戊申', '1506', '是夜，山西太原府有火光大如斗，坠宁化王府殿前，空中见红光如弯弓，长六七尺，旋变黄又变为白，渐长至二十余丈，光芒亘天，移时而灭。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明武宗正德二年八月己亥', '1507.10.4', '赤光见宁夏，长五丈。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明武宗正德四年八年丙戌', '1509', '夜入星见山东益都临淄乐安等县，红光如虹起西南向东北而散，又天鼓鸣自西北往东南，声如雷。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES (NULL, '1515', '咸镜道观察使尹金孙状启：本月初九日，德原府使赵玉崑见有红白气围日三匝，又有白气一条，横贯日中，形图封上。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明武宗正德十二年闰十二月丁丑', '1518.1.17', '戌刻，江西瑞州东方有红气一道升上，寻变为白，形如曲尺，中有黑气，与外黑气如相門者，移时乃没。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明武宗正德十六年六月癸卯', '1522', '陜西抚按官奏，米脂县四月初七日酉时分，西北方有星大如斗隨有红光一道，约长三丈余，半空冉冉转动，向西北移时变白气而灭。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES (NULL, '1524', '日入后，赤气布天，自西方至于巽方。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明世宗嘉靖七年四月四日', '1528.4.22', '五鼓，有气如火光，龙形，自空至地。直立于西南，数刻方散。明嘉靖河北');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES (NULL, '1533', '辰时，东方淡云间，有黄赤气，出日上，徧指天中，长二三丈许，良久乃灭。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES (NULL, '1533', '夜，艮方有赤气。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES (NULL, '1533', '夜，月晕，白气贯晕下，青赤气横着，长一布长许。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明世宗嘉靖十三年正月初八', '1534.1.21', '夜，火光烛天，数刻乃灭。清同治江西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES (NULL, '1538', '午时，青赤气，自艮方指坤方，长五六尺许，横在日下。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES (NULL, '1538', '夜，南方赤光如火气，白气自震方，至坤方布天。昧爽时，化为黑气，自坤方而灭。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES (NULL, '1538', '夜，南方、艮方、乾方，赤光如火气。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES (NULL, '1538', '夜，北方赤光如火。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES (NULL, '1540', '忠清道洪州牧德山县，午时，距日一布长许南方，有青红气围日，又二布长许，亦如之，形色如虹。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明世宗嘉靖十九年十月', '1540.10.30-11.27', '日照县西南有气如火，黑气从中截之。清乾隆山东');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES (NULL, '1544', '辰时，自巽方至北方，赤气如云布天。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明世宗嘉靖二十四年', '1545', '赤气见西方。清康熙浙江');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES (NULL, '1547', '又有赤气出日上，其长半布长。···政院启曰：“更问于观象监，则曰：‘太阳之色，则白日之上下，圆如日形者，其色微紅，日上半布长者，亦赤气所射。’');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES (NULL, '1548', '未时，青赤气如冠，去日上一丈余，长四五尺许。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明世宗嘉靖二十九年八月二十九日', '1550.10.9', '夜，有鬼火如列炬，自西南往东北，长二，三十里，人皆见之。明嘉靖山西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES (NULL, '1552', '昧爽，赤气弥天，光照于地。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES (NULL, '1553', '日微晕。夜，火气炎炎冲天，赤光照耀，良久乃灭。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES (NULL, '1553', '自今月以来，赤气蔽天，日月无光。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES (NULL, '1555', '日出时，日上赤气直立，如一匹布许，良久乃灭。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES (NULL, '1555', '日出时，日上有赤气，如一匹布许，须臾而灭。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES (NULL, '1556', '传于政院曰：“近日雨种连绵，日出亦有赤气，雾气亘塞，已有旱徵。言于该曹，修沟壑净阡陌可也。”');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES (NULL, '1556', '日入后，赤气自乾方至艮方横天，移时乃灭。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明世宗嘉靖三十五年七月十七日', '1556.8.21', '霓夜见北方。明嘉靖浙江');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES (NULL, '1558', '日出时，日上有黄赤气，圆如日体，俄而与日俱入云中。其下又有黄赤气，距日皆五六寸许，久而乃灭。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明世宗嘉靖四十年六月', '1561.7.12-8.10', '虹昼夜见。清康熙江苏');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES (NULL, '1566', '日之东西两傍，似日形，赤气浮空。北方又青红赤气横天，良久不灭。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明穆宗隆庆二年七月丙子', '1568', '山西平阳府绛州奏：西北天裂，自丑至寅乃合。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明穆宗隆庆二年八月甲辰', '1568', '绛州西北天裂，自丑至寅乃合。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明穆宗隆庆六年正月庚申', '1572.1.17', '夜，北方有赤气如火光，良久渐散。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历十年二月癸卯', '1582.3.8', '甘肃赤白云气见。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历十年十二月丙午', '1582', '陕西甘凉地方，赤白云气匝绕半天。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历十六年九月中旬', '1588.10.30-11.8', '天初明时，西南忽有红白气如龙，亦如犬，长竟天。明崇祯上海');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES (NULL, '1592', '与门人朴廷老登金山寺楼，见赤气三道亘天北长，而西南半之。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES (NULL, '1593', '夜自一更至三更，月光明盛，客星不得看候。一更，西方有赤气。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES (NULL, '1593', '夜一更，西方有赤气，自一更至二更，客星在天仓东第三星内三寸许，形体微小。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES (NULL, '1593', '夜，四方有赤气。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES (NULL, '1595', '黄海道观察使柳永询报曰：“本月初二日初更为始，赤气一道，起于西方，状如炬火，光烛半天，俄而南方东方一时竝起，鸡鸣之后，渐次消灭；五更后，东北间， 赤气又起，状如烈焰，平明后始灭”云。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES (NULL, '1596', '平安道观察使尹承吉驰启曰：“今正月初九日午时，有白气，起自南方，围日作圆，而南方缺。又有白气，贯日向北而作圆，当日处，则其气微抹，仅得而见。又有黄赤气，挟日团聚，如日之状，去黄赤气十余丈许。又有白气，相对团聚，亦如黄赤气之状。所见极为凶惨，图其形上。”');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES (NULL, '1596', '昧爽，青赤气起于乾方天际，指其中，广可见体，长五六丈许，良久乃灭。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES (NULL, '1597', '自一更至二更，巽方有赤气，广二尺余，长十丈余。起天际指天中，其光照地。南方、坤方有赤气。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES (NULL, '1597', '夜一更、二更，有赤气。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES (NULL, '1597', '夜一更，东方、南方有赤气，月晕。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES (NULL, '1597', '夜一更、二更，南方、坤方有赤气，一更至三更有雾气。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES (NULL, '1599', '夜二更，东西南三方，有赤气如火光。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES (NULL, '1601', '忠清道观察使张晩驰启曰：“忠州牧使金顺命呈内，十二月二十五日二更，赤光自辰方始起，亥方亦有次气，相暎移时，辰方起者，自南而西回，亥方起者，自北而东回，仍以蔽天，照耀四方，或如火光、或如月辉，可见人影，久而乃消云。燕歧县监權守己呈内，十二月二十七日晩朝，日晕两珥，又有赤黄气，为冠而微，又赤黄气，在日下而微，与两珥晕气相连。又有青赤气，在冠上而向北，又有白气，出于两珥之下，向西相连，又有赤白气，自南直上，接于白气，移时渐消事。”启下礼曹。”');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES (NULL, '1601', '全罗道观察使李弘老驰启曰：“金沟县监牒呈内，今二月初二日戌时，赤气自东方始生，遍及南北西方，而北方尤甚，移时乃止。”');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES (NULL, '1601', '庆尚监司李时发驰启曰：“星州地，本月初六日初昏，辰、地巳地、未地、丑地，天际有赤气，赤气之上，又有白气一道，状如虹，长可二三丈许，自下以上，或现或微，夜半乃灭。东南赤气，尤甚炽盛，变异非常”事入启。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES (NULL, '1604', '统制使李庆濬状启：“十二月十九日夜初更，天上东西北，赤气二道，光如火焰，状如匹练，或竟天、或半天，旋起旋灭，二更而灭，变异非常事。”启下礼曹。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES (NULL, '1604', '初昏，四方阴云中有赤气，始起于巽方，焰焰如火光，焰焰中，別有一条气，如炬熛火，直立长长二三丈许。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES (NULL, '1605', '夜一更、二更，四方皆有赤气，如火色。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES (NULL, '1605', '夜一更，南方有赤气，焰焰如火光，中有一条气，如炬熛火直立，长可二尺许，或明或灭，良久而止。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES (NULL, '1605', '夜一更，乾方、东方、南方，有赤气如火光。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历三十三年十月丙辰', '1605.11.25', '夜，北方有赤白气入紫微垣，久之散。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES (NULL, '1605', '京畿监司李廷龟驰启：“水原府使李庆濬牒呈内：‘今月二十二日初更时，天上南末，有赤气一道，光如火焰；状如匹练，或经天、或半天。俄而又有一道继起，其状如初，三更乃灭，而焰光所烛之处，明若微月之色，变异非常事。”');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历三十三年十月戊午', '1605.11.27', '夜五更，北方有赤白气入紫微垣，良久渐散。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES (NULL, '1606', '申时日晕，自夜一更至二更，有赤气。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES (NULL, '1608', '日入后星文未现时，巨火如流星出艮方天中，入西方天际，状如大盆，尾长竟天声如微雷，赤光照地，移时乃灭。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES (NULL, '1611', '夜一更，东西北三方有赤气，状如火炬者五，良久乃灭。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES (NULL, '1613', '夜一更，赤气大一二围、长三四丈，状如火炬，列立北斗下者三、南方者二、东方、巽方者各一，良久乃灭。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清太祖天命乙卯三月甲戌', '1615.4.25', '寅刻，有黄色亘天，映彻上下，上御殿，至辰刻方散。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历四十四年元旦', '1616.2.17', '东方天门开……未几，西方赤气亘天。清光绪浙江');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清太祖天命三年五月甲辰', '1618', '卯刻，有青赤白气三自天下垂，覆营左右，上圆如门及军行。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES (NULL, '1618', '夜，南方有气如火光。又有赤气直立，长可三四尺，广可尺余，良久乃灭。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历四十七年二月乙亥', '1620', '大学士方从哲，题臣昨日在阁办事，午后忽见狂风大作，黄尘四起，赤气横空。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历四十七年十二月三十', '1620.2.3', '夜，四方有赤光如鲭形。清康熙湖北');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历四十八年九月二十四日', '1620.10.19', '赤气亘天。清康熙山东');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明光宗泰昌元年九月戊戌', '1620.10.19', '有赤气映天如赭。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明光宗泰昌元年九月已亥', '1620.10.20', '昧爽，赤气映天如赭，良久始散。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明光宗泰昌元年十二月丁未', '1620.12.27', '夜，西北赤气如火。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES (NULL, '1623', '日出时，赤气如柱，立于日上，阔数尺，长十余丈，良久乃灭。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES (NULL, '1624', '本月初三日夜一更，西方有赤气，状甚殊常，人皆见而惊骇，而当直官员，不为登台候察，故领监事启请治罪。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES (NULL, '1624', '昧爽，东方有气，如火光。夜东方赤气，耀于天际。北方、坤方有气，如火光。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES (NULL, '1624', '夜，青赤气，自西方指艮方，南方有气，如月光。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES (NULL, '1624', '夜坤方，有赤气，如火。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES (NULL, '1625', '夜一更，四方有赤气，如火光，雷动。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES (NULL, '1626', '夜一更月出时，青赤气如柱，自东方至西贯月，长可二丈许、广尺余。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明熹宗天启六年四月己亥', '1626.5.22', '东北方有白云，名天裂气，有赤云，名军胜气。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES (NULL, '1626', '是夜，南方有气如火，赤光照地。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES (NULL, '1626', '初昏，苍赤气一道，起自西方，直指东方。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES (NULL, '1626', '夜一更，月上有赤气直立，长三尺许，广尺余，移时乃灭。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明毅宗崇祯元年三月辛巳', '1628.4.23', '昧爽，陕西天赤如血，射窗牖皆赤。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明毅宗崇祯元年三月', '1628.4.4-5.3', '关中天赤如血，射户牖皆赤。清乾隆陕西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明毅宗崇祯戊辰三月二十五日', '1628.4.28', '五鼓，全陕天赤如血，巳时渐黄，日始出。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明毅宗崇祯元年七月二十三日', '1628.8.22', '当夜半时，风涛稍减，戍卒守烽燉者，南望海中红光百道，状若灯毯，延袤几百里，东西时倏相易转幻在一瞬间。清乾隆浙江');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明毅宗崇祯二年正月', '1629.1.24-2.22', '黑气东北亘西方，中有白气二道。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES (NULL, '1634', '日入时，黄赤气如柱，直立日上，长丈余，日没乃灭。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES (NULL, '1634', '月初出有赤气，状如炬火。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明毅宗崇祯七年十月初四日', '1634.11.24', '夜，北方虹见。清雍正安徽');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明毅宗崇祯七年十月', '1634.11.21-12.19', '北方虹见。清康熙安徽');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明毅宗崇祯八年正月甲子', '1635.3.1', '夜，有白气十三条，形如剑，其长亘天。明弘光江苏');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明毅宗崇祯八年二月丁亥', '1635.3.24', '夜，亦有白气数百条，形亦如剑，其长亦亘天。明弘光江苏');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES (NULL, '1638', '巽方有赤气三条。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明毅宗崇祯十一年十一月十九日', '1638.12.23', '东北有赤气数十条，如剑戟排列状。清道光安徽');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES (NULL, '1639', '雨雹，巽方赤气烛天。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明毅宗崇祯十二年三月', '1639.4.3-5.2', '蕲州夜有五色虹下属地，数刻许，天色变暗如蒙雾，累日不解。清顺治湖北');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明毅宗崇祯十三年十一月十二日', '1640.12.24', '更余，赤气弥天。清康熙江苏');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明毅宗崇祯十七年正月初一日', '1644.2.8', '寅刻，赤气亘天。清康熙河北');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明毅宗崇祯乙酉年十一月', '1645.12.18-1646.1.16', '夜，天南方白耀长一二丈，有数千条，出没经时。清乾隆江西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清世祖顺治三年正月壬戌', '1646.3.1', '夜，北方云中有赤光如火影，良久乃散。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清世祖顺治三年二月初三', '1646.3.19', '夜，西北方有群星，状如枪刀剑戟，赤色光芒，有一星光芒直冲斗口。清乾隆江西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清世祖顺治五年夏', '1648．夏', '赤气亘东方。清乾隆浙江');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES (NULL, '1648', '夜，东方有赤气如龙蛇。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清世祖顺治五年十二月三十日', '1648', '时一更，天垂白气如练，四布直披约数十余道，寒光下射，悸人心目。清康熙安徽');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清世祖顺治七年正月二十七日', '1650.2.27', '夜，望江西方有青气亘天。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清世祖顺治七年十二月三十日', '1651.2.19', '萧县见白气如练数十条，寒光射人。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清世祖顺治九年正月', '1652', '东昌有赤光，声如水鸭，往东南而没。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清世祖顺治九年七月二十五日', '1652.8.28', '夜，赤虹见。清嘉庆浙江');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清世祖顺治十年二月三日', '1653.3.2', '夜，四望火光烛天，大小不等，时以为青磷云。清康熙山东');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清世祖顺治十年六月丙申', '1653', '青赤气生日上下。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES (NULL, '1655', '日出时赤气一条，直指日上，长数丈许。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清世祖顺治十四年十二月二十四日', '1658.1.27', '夜，有红光三道，垂照县城中若矩，达晨始收。清康熙湖南');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清世祖顺治十五年九月', '1658.9.27-10.25', '长沙府城及江间有异气如竹林森立，高者插天，逾时乃稳。清康熙');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清世祖顺治十六年十月二十九日', '1659.12.12', '戌时，天上红白气见，如旗如枪，数十缕。清康熙河南');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙二年七月初二日', '1663.8.4', '夜，有五色祥光，起于西北。清乾隆湖南');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙六年正月二十日', '1667.2.12', '昏时，东北有火光，焰焰如焚，旋满西北，居人疑失火，互相奔视，或登高望之，见赤气亘天，逾一刻而灭。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES (NULL, '1668', '许积对曰：“有是言也。东方天坼，光同火镜。且有赤马相鬪之状，传说者甚多。次日北方有赤气，又次日有白气之异。天开，太平之象，天坼，衰乱之兆云。”');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES (NULL, '1669', '初昏青赤气一道，起自西方，直指天中，长三四尺，广尺许，良久乃灭。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙十一年八月初一日', '1672.9.21', '夜，红光满天，如火沙。清乾隆江苏');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙十二年初夏', '1673初夏', '西北方天上偶见关刀，长丈余，色赤，逾时乃稳。清乾隆湖南');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES (NULL, '1676', '赤气亘天。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES (NULL, '1679', '宪府论：“且其流陨之后，继有赤白气，横亘十余丈。”');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙二十七年四月十七日', '1688.5.16', '白云银光见翼轸间，层叠团结，中有五色气如断虹。清乾隆湖北');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙三十四年八月十四日', '1695.9.21', '西北方天门开，红光烛天。清嘉庆湖南');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙三十四年八月', '1695.9.8-10.7', '西北光红烛天。清同治湖南');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES (NULL, '1694', '诞王（谨按王）于昌德宫，前三日红光亘于东方，白气罩其上。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES (NULL, '1696', '北青、端川、利城等邑，戌時有赤气如虹，俄而雷声大震，良久而止。道臣状闻。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES (NULL, '1708', '日出时，傍有赤气，状如虹。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙五十一年六月十四日', '1712.7.17', '夜三更，虹霓出现半时。清雍正广西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES (NULL, '1712', '初昏，西方有赤气如火光，良久而灭。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙五十二年三月十八日', '1713.4.12', '子时，福山红光满处。清光绪山东');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙五十四年三月十八日', '1715.4.21', '福山夜见红光满处。清光绪山东');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙五十九年七月十六日', '1720', '荣成，莱阳有赤气自东而起，倏如匹练，亘向西北去，有声如雷。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清世宗雍正七年十二月二十八日', '1730.2.15', '夜，巴尔库尔军营，有紫气祥光，绵亘东北，历四时之久，光华绚烂。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清世宗雍正十一年七月二十三日', '1733.9.1', '晚，弥天绛霞，中青虹一道，其长竟天，良久乃散。清乾隆云南');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES (NULL, '1734', '夜，有星流于奎星上，色赤光照地。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清高宗乾隆二年十月二十五日', '1737.12.16', '夜子时，据巡城兵丁禀称，望见北面有红光，随经细看，初起东北，渐移于西，横望北方一面，天色如火，高与南山齐，中含黑气，又竖有白气四道。至三更后，其四道白气，变为数十道，而黑气则渐渐退去，至四更后，俱化无存，惟有红光，至五更后，渐觉色淡，直至日出，全消不见。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES (NULL, '1737', '东方有气如火光，月入太微东垣内。···光佐曰：“数昨晓起，见东方有红光如火焰，乃赤气也。”');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES (NULL, '1737', '及到洛下，天变孔酷，震虩之声，洊作于純阴；金木之孛，连犯于太阴，怪风拂地，赤气亘天。 ');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清高宗乾隆二年冬', '1737．冬', '赤气弥天。清乾隆安徽');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES (NULL, '1750', '丑时，（英祖之孙）生于昌庆宫之景春殿，生时红光亘霄焉，即嫔宫洪氏诞生也。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清高宗乾隆二十年八月初五日', '1755.9.10', '夜，天有光数十丈，明朗如昼。清光绪山西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清高宗乾隆三十四年七月二十六日', '1769.8.27', '戌刻，紫徽垣见红白气数条，良久乃灭。清同治河北');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清高宗乾隆三十四年十二月封印日', '1770', '夜亥时，东北有红黑气现，至四更消散。清道光新疆');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清高宗乾隆三十五年七月二十二日', '1770.9.11', '红光见，自戌至子，照耀经天，观者无不惊怖。清乾隆山西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清高宗乾隆三十五年七月二十六日', '1770.9.15', '日夕，红光照耀，上有白气随之，夜半方息。清乾隆河北');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清高宗乾隆三十五年七月二十七日', '1770.9.16', '夜，天正北，五色如霞照地。清乾隆河北');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清高宗乾隆三十五年七月二十八日', '1770.9.17', '戌时，赤光自北方起，中有白气十三道，望如火焰至天中，夜半渐退。清嘉庆山东');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清高宗乾隆三十五年七月二十九日', '1770.9.18', '夜，荣成夜见红光烛天东光有气如火，横蔽西北，亘数十丈，中含红光，森如剑戟上射。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清高宗乾隆三十五年七月', '1770.8.21-9.18', '晚，西北天赤，直贯天心，中有白气如椽林，至明乃没。清乾隆甘肃');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清高宗乾隆三十五年八月初一日', '1770.9.19', '甫夜，朱霞亘西北，光华万道，弥望无垠。清乾隆甘肃');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清高宗乾隆三十五年八月十五日', '1770.10.3', '夜，红云起西北，中有白气。清道光安徽');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清高宗乾隆三十五年八月庚子（二十七日）', '1770.10.15', '夜，玉烛见北。清嘉庆湖南');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清高宗乾隆三十六年八月九日', '1771.9.17', '夜，天赤如血，横亘北方。清嘉庆山东');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清高宗乾隆三十八年六月', '1773.7.20-8.17', '夜，北方有红气遮天。清道光河北');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清高宗乾隆四十五年九月中', '1780.10.8-18', '见天忽开红绿色。清道光贵州');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES (NULL, '1785', '供曰：“昨年腊月初七八九日，危、室、壁之前，垒壁陈二十余星，内有赤气。”');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES (NULL, '1790', '是日晓，禁林有红光烛地，日卓午虹起太庙井中，成五采，民争睹之曰：“此异瑞也。”');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES (NULL, '1790', '上问晩秀曰：“今晓禁中有红光云，有见者否？”晩秀对曰：“臣于早朝时见禁林， 红光烛天，上下照耀，至天明尚有余光。造朝之诸承旨及仆隶，莫不见而异之矣。”');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES (NULL, '1791', '去月二十七日，礼曹判书受香到陵下，夜将半，一道红光，起自丁字阁，逦迤亘绕，直抵坛所，烛地如火。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清高宗乾隆五十五年正月四日', '1790.2.17', '亥时，赤虹见于北方。清道光江苏');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清仁宗嘉庆九年九月十六日', '1804.10.19', '夜漏三下，忽如白昼，与星月光迥殊，俄顷如故，会城多人见之。清同治广东');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清仁宗嘉庆十年六月初二', '1805.7.28', '夜，西北角有星如旗，约长丈余，随风飘扬，半刻而隐。清乾隆上海');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清仁宗嘉庆十八年', '1813', '有赤眚自北渐南现，白气数十道。清光绪山西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清仁宗嘉庆二十五年十月二日', '1820.11.7', '夜，四鼓，红光烛地，时郡试诸童仰见天上金光晃荡，不定，移时始息。清同治湖南');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清宣宗道光五年九月朔', '1825.10.12', '弧星见，天上有弓引满，其色赤，逾刻即没。清道光广西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清宣宗道光九年正月初三', '1829.2.6', '夜，天形中开，半明如昼，半黑如漆。民国四川');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清宣宗道光十五年十月乙丑', '1835', '华渚虹光?');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清宣宗道光十七年六月二十八日', '1837', '嵊县有赤光如球，高数丈，三日乃灭。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清宣宗道光十七年九月', '1837.9.30-10.28', '夜，天裂，南北界划，边黑如漆，边白如雪。清同治四川');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清宣宗道光十九年九月十五日', '1839.10.21', '夜达旦，五更，通天红色。（浙江温州）');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清文宗咸丰三年三月十六日', '1853.4.23', '夜三更，天赤如血。清宣统陕西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清文宗咸丰四年三月初口日', '1854.3.29-4.7', '夜，天红如血。清光绪陕西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清文宗咸丰六年七月十八，十九日', '1856.8.18-19', '夜方半，赤虹下垂西北，如血。清光绪贵州');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清文宗咸丰九年正月二十八日', '1859.3.2', '夜，天有赤气见井分。清光绪湖北');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清文宗咸丰九年正月', '1859.2.3-3.4', '夜半，有火光约长十余里，远视有人马沸腾之状，人咸以为过阴兵。清光绪安徽');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清文宗咸丰九年七月初六日', '1859.8.4', '夜，坎方天红如火。民国河南');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清文宗咸丰九年七月', '1859.7.30-8.27', '夜，红光起于西北，亘于东北，经三夜始散。清光绪河北');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清文宗咸丰九年八月癸卯', '1859.9.2', '夜，赤气起子西北，亘于东北，天明始灭。清同治河北');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清文宗咸丰十一年六月十六日', '1861.7.23', '夜半，天中有旂，色红，抑扬飘拂，良久乃灭，人多见之。清光绪四川');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清文宗咸丰十一年十二月乙卯', '1862.1.1', '天中裂缝数十丈，横广数尺，有光烁烁，逾时乃合。清光绪四川');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清穆宗同治元年七月三日', '1862.7.29', '夜半，天忽开朗如昼，顷刻即冥。清光绪上海');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清穆宗同治元年十一月十五日', '1863.1.4', '夜半，五色云见。清光绪广东');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清穆宗同治五年九月二十八日', '1866.11.5', '夜半，红光亘天，阔数丈，光烛地如昼，须臾没。清光绪湖南');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清穆宗同治六年十二月某夜', '1867.12.26-1868.1.24', '天南北如分裂．北尽赤。民国陕西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清穆宗同治八年六月十七日', '1869.7.25', '晚，有青虹自东湾向西，半亘天。清光绪湖北');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清穆宗同治九年正月二十六日', '1870.2.25', '夜，赤气见井分。清同治江西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清穆宗同治九年十二月二十七日', '1871.2.16', '三更时，天色红，遍地如起火。清光绪山西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清穆宗同治十年八月初三日', '1871.9.17', '丑刻，东北方有红光一道冲霄而上，恍同白昼，初赤色，倏变黄变绿，其气氤氲，顷之大雨。清光绪广东');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清穆宗同治十年十二月一日', '1872.1.10', '晚，天分裂，南黄白色，北尽赤。民国陕西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清穆宗同治十年十二月初二日', '1872.1.11', '夜四更，红光满天，如日初升，自丑至寅，经时方散。清光绪山西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清穆宗同治十年十二月二十六日', '1872.2.4', '夜，自艮至坤，天赤如火，响晓乃灭。清光绪河北');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清穆宗同治十年十二月二十七日', '1872.2.5', '丑刻，赤气满天，起东北至西南，光耀如火，云内有一星如盂，蓝色，移时始灭。民国河北');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清穆宗同治十年十二月某夜', '1872.1.10-2.8', '天南北如分裂，北尽赤。清光绪陕西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清穆宗同治十一年五月十五日', '1872.6.20', '夜，红光满天。清光绪广东');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清穆宗同治十一年十二月二十六日', '1873.1.24', '夜，天赤。清光绪陕西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清穆宗同治十二年十二月', '1874.1.18-2.16', '夜，西北天色赤。清光绪山西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪二年二月初四日', '1876', '曹县见红光自天降于八里湾水中。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪十一年冬月二十一日', '1885.12.26', '夜半，弥天星彩，多成彗象，纷纷捕逐，嗤然有声。民国山西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪十六年九月某日', '1890.10.14-11.11', '夜子时，红光自天彻地，窗纸如丹砂染成洵奇观也。民国四川');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪二十二年除夕', '1897.2.1', '戌刻，西南忽见红气，阔十余丈，其光烛天。民国广东');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪二十五年二月二十八日', '1899.4.8', '夜，天发红光，约一点半钟方熄。民国广东');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪二十五年除夕', '1900.1.30', '有光如电，自西北起，日灼照耀如昼。清光绪安徽');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪三十年九月', '1904.10.9-11.6', '红白气横亘东北，移时始散。民国贵州');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪三十年十二月十一日', '1905.1.16', '夜半，见红虹自东至西，在北方见。清光绪');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪三十四年六月十四日', '1908.7.12', '初更，虹亘东西，移时始没。民国陕西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清末帝宣统元年五月', '1909.6.18-7.16', '夜，天空有光长数丈，如云，自北南下，地面皆明。民国河南');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清末帝宣统三年十月某夜', '1911.11.21-12.19', '天南北分裂，南尽黑，北尽白色，各竟天。民国陕西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('黄帝诞生前二年', '传说时代', '黄帝……母日附宝，之祁野，见大电绕北斗枢星，感而怀孕，二十四月而生黄帝于寿丘。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('周武王东伐纣', '约前11世纪', '夜济河时，云明如昼。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('周懿王元年正月', '前895', '王即位，天再亘于郑。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('周灵王二十年', '前552', '有五黑气如日。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('秦始皇三十七年', '前210', '紫气见于东南。清康熙安徽');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('汉武帝元鼎四年十月', '前114.11.6-12.5', '天子东幸汾阴。汾阴男子公孙滂洋等见汾旁有光如绛。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('汉武帝元鼎五年十一月辛巳朔', '前113.12.24', '是夜有美光。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('汉武帝元鼎五年十一月辛卯', '前112.1.3', '夜，若景光十有二明。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('汉武帝元封元年四月丙辰', '前110.5.18', '其夜若有光。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('汉成帝永始四年正月', '前13.2.7-3.6', '行幸甘泉，郊泰畴，神光降集紫殿。大赦天下。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('王莽地皇三年', '22', '及王莽篡位，忌恶刘氏，……。后望气者苏伯阿为王莽使至南阳，遥望见舂陵郭，叹日：气佳哉，郁郁葱葱然。及始起兵还舂陵，远望舍南，火光赫然属天，有顷不见。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('汉光武元年', '25', '汉光武封禅仪曰：元年封禅，昼有白气，夜有赤光。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('东汉章帝元和三年正月', '86.2.2-3.3', '车驾北巡，以太牢祠北岳山，见黄白气。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('东汉桓帝延熹九年三月癸巳', '166.4.21', '京都夜有火光转行，民相惊噪。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('东汉献帝建安二十二年', '217', '东汉献帝建安二十五年，魏文帝称尊号，改年曰黄初。或传闻汉帝见害，先主乃发丧制服……议郎杨泉候刘豹……等上言，“……臣父群未亡时，言西南数有黄气，直立数丈，见来积年，时时有景云祥风，从璇玑下来应之，此为异瑞。又二十二年中，数有气如旗，从西竟东，中天而行。……”');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('晋武帝太康元年正月', '280.2.18-3.17', '五色气贯南斗。明万历江西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('晋武帝太康七年七月', '286.8.7-9.5', '有紫气见牛斗间。清乾隆江西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('南朝宋文帝元嘉十八年七月壬辰', '441.8.14', '夜，天有光通照。元至正江苏');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('南齐武帝永明八年六月丙申', '490.8.1', '大雷雨，有黄光竟天，照地状如金。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('南朝梁元帝承圣四年十二月丙戌', '556', '赤气干北斗心。明嘉靖湖北');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('南朝陈宣帝太建十四年八月丁酉', '582.9.28', '天赤如火。宋景定江苏');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('隋文帝仁寿二年', '602', '有红光竟天。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('隋炀帝大业十三年', '617.2.11-618.1.31', '夜，有光如火。清光绪山西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('唐太宗贞观十一年七月一日', '637.7.27', '黄气竟天。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('唐太宗贞观十八年六月壬戌', '644.7.29', '有青黑气广六尺，贯于辰戌，其长亘天。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('唐昭宗天复元年五月', '901.5.21-6.18', '有三赤星，各有锋芒，在南方，既而西方、北方、东方亦如之，顷之，又各增一星，凡十六星；少时，先从北灭。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('五代后梁太祖开平二年九月乙酉', '908.10.14', '平旦，西方有气如人形甚众，皆若俯伏之状，经刻乃散。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('宋真宗景德元年七月辛亥', '1004.8.17', '黄气出壁，长五丈余。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('宋真宗景德二年正月丙寅', '1005.2.28', '黄白气贯月，黑气环之。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('宋真宗大中祥符元年正月癸亥朔', '1008.2.10', '黄气出于艮。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('宋真宗大中祥符元年七月', '1008.8.4-9.2', '西北方白云气如彗帚三十余条。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('宋真宗大中祥符二年九月戊午', '1009.9.27', '黄气如柱起东南方，长五丈许。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('宋真宗大中祥符七年五月', '1014.6.1-29', '有气出紫微为宫阙状，光烛地。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('宋真宗大中祥符七年九月', '1014.9.27-10.26', '有气出紫微为宫阙状，光烛地。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('南宋状元何桌始生之辰', '约1089', '夜半，红雾亘天。清道光四川');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('宋钦宗年间今月十一日', '约1126', '夜，南方天见赤气，直至天明，详其分野，正临都邑。（天会四年，宗望上宋钦宗再立誓约书）。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('宋高宗建炎元年秋', '1127.8.10-11.5', '斗牛间有紫气。清康熙浙江');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('宋高宗绍兴元年十二月庚辰', '1132.1.7', '夜，有黄白气。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('金章宗泰和三年十月甲辰', '1203.11.14', '申、酉间天大赤，夜将旦亦如之。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('金宣宗元光元年十一月丁未', '1222.12.7', '东北有赤云如火。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('金哀宗正大二年正月甲申', '1225.3.3', '有黄黑之祲。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('金哀宗正大二年二月甲寅', '1225.4.2', '有黄黑祲。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('宋端宗景炎二年八月己巳', '1277.9.11', '夜，天色赤。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('元泰定帝泰定元年夏', '1324．夏', '虹见九头，其色如血。清乾隆浙江');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明宣宗宣德元年八月戊寅', '1426.9.18', '昏刻，中天有青云状如杵，南有黑云状如覆船。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明宣宗宣德七年六月壬辰', '1432.7.2', '日入后，有青霞二道，东西竟天。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明宣宗宣德八年闰八月戊午', '1433.9.21', '昏刻，景星见西北方天门，三星大如碗，色青赤黄，明朗清润，良久聚成半月形。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明宣宗宣德八年闰八月丁丑', '1433.10.10', '昏刻，归邪星见东南方，色黄赤。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明武宗正德元年正月朔', '1506.1.24', '正北天裂，红光阔二丈，长数十丈，有顷光灭，自下而上若卷席然。清康熙福建');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明武宗正德元年', '1506', '是岁，宁夏左屯卫红气亘天，既而火作，城楼台堡俱烬。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明武宗正德二年八月十五日', '1507.9.21', '夜子分，异云出滹沱南，复小潭上赤光四烛，有钲鼓笙簧之音，栖鸟皆惊。清康熙河北');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明武宗正德二年八月', '1507.9.7-10.5', '有赤光腾起灵寿河畔。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明武宗正德九年', '1514', '夜漏初下，无云，有黄光来自南山，状如龙，度北山，初更乃尽。明嘉靖甘肃');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明武宗正德十一年七月初五日', '1516.8.2', '夜，巴陵东南天裂，长三丈余，红光刺人。清道光湖南');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明世宗嘉靖二年八月初四日', '1523.9.12', '夜，火光见东北隅，良久乃散。明万历福建');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明世宗嘉靖四年九月', '15l5.9.17-10.15', '赤气亘天。清道光广东');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明世宗嘉靖四年冬', '1525．冬', '吴川有赤气亘天。清道光广东');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明世宗嘉靖五年正月', '1526.2.11-3.12', '赤气横境。明嘉靖');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明世宗嘉靖三十三年十一月二十八日', '1553.12.22', '有火光如柱见东方，数夜乃灭。清乾隆江苏');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明世宗嘉靖四十年五月晦日', '1561.6.12', '赤虹亘天。清康熙广西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明世宗嘉靖四十年五月', '1561.5.14-6.12', '赤虹二道，自西北贯东南。清咸丰广东');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明穆宗隆庆二年八月甲辰', '1568.9.18', '绛州西北天裂，自丑至寅乃合。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历元年', '1573', '绛州天裂。清雍正');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历八年七月', '1580.8.10-9.8', '宁乡见天开如眼，光耀烁人。清雍正');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历二十七年八月', '1599.9.19-10.18', '昆池西有光五色，精彩射人。民国云南');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历三十五年九月', '1607.10.21-11.18', '平阳府东南见天开，光芒照耀。清乾隆山西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历三十七年', '1609', '晋宁有五色光见。清康熙');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历四十三年正月二十五日', '1615.2.22', '有气如晕，联贯弥天。清咸丰山东');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清太祖天命三年四月壬子', '1618.5.17', '有蓝、黑气二道，自西而东，横亘于天。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清太祖天命三年五月乙卯', '1618.7.19', '有红、绿、白三气，自天下垂，覆营左右，上圆如门。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历四十七年二月十九日', '1619.4.3', '酉时，气变而为红，初更乃灭。清光绪河北');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明熹宗天启四年三月', '1624.4.18-5.16', '杭、嘉各村中夜半见空中火光，若甲马驰骤，隐隐有戈战声。清康熙浙江');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明熹宗天启六年', '1626', '青红气自西南障天。清康熙陕西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明毅宗崇祯六年十月', '1633.11.2-30', '天降青红气一股，自西南。清顺治河南');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明毅宗崇祯七年正月', '1634.1.29-2.27', '遍天五色气如环，大小不一，逾时方灭。清康熙河北');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清太宗崇德六年九月辛巳', '1641.10.12', '黎明，东方有金光大如斗，内复有金光一道直如椽，冲天而起。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明毅宗崇祯十五年春', '1642．春', '赤气见。清乾隆河北');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明毅宗崇祯十六年八月十四日', '1643.9.26', '夜，黎城县星月皎洁，绝无云雷，一龙蜿蜒上升，金光闪烁，户牖皆黄．清乾隆山西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清世祖顺治二年闰六月二十九日', '1645.8.20', '黄昏时，星河明灿，忽有黑气横天，自东至西北，顷之化为长虹，久而不灭。清道光江苏');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清世祖顺治二年七月望后', '1645.9.4以后', '薄暮有青气三道，从东贯西横亘中天，逾时始灭。清道光江苏');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清世祖顺治五年十一月初二', '1648.12.15', '夜，妖星见西北，形似笏，乍伸乍缩，忽光忽暗。清康熙福建');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清世祖顺治十年闰六月二十四日', '1653', '夜，三更，红日出东北方，大如斛，夜半，月始昇灭不见。清乾隆浙江');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清世祖顺治十年十二月', '1654.1.19-2.16', '赤白虹交见。清康熙河南');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清世祖顺治十四年春', '1657．春', '夜，空中有火。清雍正陕西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙二年正月二十八日', '1663.3.7', '夜，白气如竿长数十丈，次夜，竿上又现红色一片如旗，或曰“此蚩尤旗也”。清乾隆福建');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙七年正月', '1668.2.12-3.12', '昭明星见，有赤气一道，上阔下锐如火焰。清嘉庆江西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙九年正月朔', '1670.1.21', '五色祥光烛天。清光绪广东');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙辛巳（四十年）夏', '1701．夏', '界首镇人，夜半起见火光烛天，直北尽赤，大惊骇。徐察之，则起于汜光湖中，移时乃灭，次日问之渔人，曰此珠光也，近岁频见之，其见必以夜半。清嘉庆江苏');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙四十七年', '1708', '南关内地金针，有气如虹，高数丈。清道光贵州');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清世宗雍正十二年', '1734', '夜半，火光自天而下，约数亩余，六畜皆惊。清同治河北');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清高宗乾隆十六年三月二十七日', '1751.4.22', '夜，天赤。清乾隆安徽');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清高宗乾隆十七年七月', '1752.8.9-9.7', '芮城有火光如电。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清高宗乾隆十八年九月癸丑', '1753.9.27', '东流有气如虹著天，色紫白，久而没。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清高宗乾隆十八年九月癸酉日', '1753.10.17', '戌刻，有气如虹著天，色紫白、久而没。清乾隆安徽');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清高宗乾隆五十六年六月十三日', '1791.7.13', '辰刻，空中有物如张弓者二，五色如虹，长丈余，两两相比，自东北至西南迟迟而没，天文书谓之天弧。清道光湖北');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清仁宗嘉庆三年十一月二十三日', '1798.12.29', '火光如电，百里皆见。清同治河北');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清宣宗道光十七年元旦', '1837.2.5', '约四更许，忽东南如晚霞映日，火光灼天，少顷复暗，暗又复明，如此者三天，复昏黑，逾时始曙。清同治湖南');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清宣宗道光二十三年四月', '1843.4.30．-5.28', '天有白气，夜深则有红光。清道光甘肃');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清宣宗道光二十三年秋', '1843．秋', '有气出西北，五色如带下垂。清光绪广东');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清宣宗道光二十四年三月四日', '1844.4.21', '日暮，南方光烛天，约一时灭。清光绪');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清宣宗道光二十九年闰四月己卯', '1849.6.2', '夜，天赤如血，俄大雨如注。清光绪江苏');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清文宗咸丰三年正月二十日', '1853.2.27', '寅时，天红。清光绪山西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清文宗咸丰三年六月七日', '1853.7.12', '戌刻，有白气二道，中青而下紫，一长一短，形如匹练，直达天中，由南而北，渐转西而没。清同治湖北');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清文宗咸丰三年七月初八日', '1853.8.12', '夜，有黑气如虹，自西亘东，长数十丈。清光绪四川');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清文宗咸丰五年八月', '1855.9.11-10.10', '夜，东方有赤气如旗杆形。清光绪山东');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清文宗咸丰八、九年间', '1858-1859', '东北有赤气起。清光绪四川');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清文宗咸丰九年', '1859', '河州、东川夜起红灯，近视却无。清宣统');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清文宗咸丰十一年五月', '1861.6.8-7.7', '虹屡夜见。民国四川');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清穆宗同治元年八月', '1862.8.25-9.23', '天灯见，夜静时，红光烛天，照耀如昼。清光绪山西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清穆宗同治二年正月戊申朔', '1863.8.2.18', '赤气见于东南。清同治河北');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清穆宗同治二年七月', '1863.8.14-9.12', '有赤光如旗见西方。清光绪四川');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清穆宗同治五年七月十八日', '1866.8.27', '天既暝，忽东南上闪烁有光，似电非电，云影迸裂，星斗俱沉倏变紫色，瞬息若墨而星斗复出。清光绪上海金山');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清穆宗同治十一年十月三十日', '1872.11.30', '将晚，天上有黑光如虹，自西至东，其南北界画甚分明。民国江苏');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清穆宗同治十三年夏', '1874．夏', '大姚夜出红霓。清光绪');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪十四年四月', '1888.5.11-6.9', '天梯见，其光自月讫于蔡山之颠。民国四川');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪十五年二月', '1889.3.2-30', '夜，有火光，上下起午。民国河南');
