INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('鲁釐公十六年正月戊申朔', '前645.12.24', '陨石于宋，五。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('鲁僖公十六年春', '前644．春', '（传）陨石于宋，五。陨星也。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('宋襄公兹父七年', '前644', '陨五石。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('周元王四年', '前472', '陨石于晋。');
