INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('周显王五年', '前364', '雨碧于郢。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('秦始皇三十六年', '前211', '有坠星下东郡，至地为石，黔首或刻其石曰：“始皇帝死而地分。”始皇闻之，遣御史逐问，莫服，尽取石旁居人诛之，因燔销其石。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('秦二世元年', '前209', '宫中雨金，既而顷刻皆化为石。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('汉惠帝二年', '前193', '宫中雨黄金黑锡。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('汉惠帝三年', '前192', '陨石绵诸，一。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('汉武帝征和四年二月丁酉', '前89.3.9', '雍县无云如雷者三，或如虹气苍黄，若飞鸟集棫阳宫南，声闻四百里。陨石二，黑如黳，有司以为美祥，以荐宗庙。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('汉元帝建昭元年正月戊辰', '前38.3.13', '陨石梁国，六，有光耀雷声。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('汉成帝建始四年正月癸卯', '前29.2.29', '陨石槁，四，肥累，一。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('汉成帝阳朔三年二月壬戌', '前22.4.12', '陨石白马，八。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('汉成帝阳朔三年三月', '前22.4.16-5.14', '有石陨于东郡，八。');
