INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('汉哀帝建平元年正月丁未', '前6.3.4', '陨石北地，十。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('汉哀帝建平元年正月', '前6.2.19-3.20', '有石陨于地，十六。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('汉哀帝建平元年九月甲辰', '前6.10.27', '陨石虞，二。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('汉平帝元始二年六月', '2.6.28-7.27', '陨石钜鹿，二。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('汉明帝永平年间', '58-75', '扶风有星陨为石，声闻三郡。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('汉章帝建初四年五月戊寅', '79.7.12', '古今注日：颖阴石从天墟，大如铁锧，色黑，始下时声如雷。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('汉殇帝延平元年九月乙亥', '106.10.15', '陈留雷，有石陨地四。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('汉殇帝延平元年九月', '106.10.15-11.13', '陨石陈留，四。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('汉桓帝延熹七年二月癸亥', '164.4.1', '陨石右扶风，一，鄠又陨石，二，皆有声如雷。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('汉桓帝延熹七年三月癸亥', '164', '陨石右扶风，一，鄠又陨石，二，皆有声如雷。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('汉桓帝延熹七年六月十三日', '164.7.19', '扶风有星陨为石，声闻三郡。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('魏武帝末年', '约220', '邺中雨五色石。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('魏明帝青龙三年正月乙亥', '235', '陨石于寿光县。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('晋武帝太康五年五月丁巳', '284.6.24', '陨石于温及河阳各二。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('晋武帝太康六年正月', '285.2.22-3.22', '陨石于温，三。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('刘聪建元元年正月', '314.2.1-3.2', '流星起于牵牛，入紫微，龙形委蛇，其光照地，落于平阳北十里，视之则肉，臭闻于平阳，长三十步，广二十七步。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('晋成帝咸和八年五月', '333.5.30-6.28', '星陨于肥乡，一。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('后赵石勒时', '330-333', '星陨于邺东北六十里，初赤黑黄云如幕，长数十匹，交错，声如雷震，坠地气热如火，尘起连天。时有耕者往视之，土犹然沸，见有一石方尺余，青色而轻，击之，音如磬。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('后赵石勒末年', '333', '星陨于邺东北六十里，初赤黑黄云如幕，长数十丈，交错，如雷震声，坠地气热如火，尘起连天。时有耕者往视之，土犹然沸，见一石方尺余，青色而轻，击之，声如磬。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('晋成帝咸和九年正月', '334.2.20-3.21', '陨石于凉州，二。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('前凉张骏为凉州牧时', NULL, '有石陨于破胡，焦而碎，声如击鼓，闻七百里。其处气上黑如烟，烟首如赤飙。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('晋穆帝升平元年正月丁丑', '357.2.21', '陨石于槐里，一。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('前秦符坚建元十九年', '383', '姑臧南门崩，陨石于闲豫堂。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('南朝宋后废帝元徽四年', '476', '义熙、晋陵二郡并有霹雳车坠地，如青石，草木焦死。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('南朝宋顺帝升明三年四月二十四日', '479.5.30', '荥阳郡人尹千，于嵩山东南隅见天雨石，坠地石开，有玉玺在其中，玺方三寸。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('南朝宋顺帝升明三年四月', '479.5.7-6.5', '荥阳人（尹午）于（嵩高）山东南涧见天雨石，坠地石开，有玺在其中，方三寸。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('南朝陈高祖永定三年初', '559', '（周）文育之据三陂，有流星坠地，其声如雷，地陷方一丈，中有碎炭数斗。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('北朝齐世祖河清四年三月', '565.4.16-5.15', '有物陨于殿庭，色赤，形如数斗器，众星随者如小铃。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('南朝陈后主至德三年十二月', '585.12.27-586.1.24', '有赤物陨于太极殿前，初下时，钟皆鸣。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('隋文帝开皇七年五月己卯', '587.6.16', '雨石于武安、滏阳间十余里。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('南朝陈后主祯明二年五月甲午', '588.6.25', '东冶铸铁，有物赤色如数斗，自天坠熔所，有声隆隆如雷，铁飞出墙外烧民家。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('南朝陈后主祯明二年五月', '588.5.31-6.28', '东冶铸铁，有物赤色，大如斗，自天坠熔所，隆隆有声，铁飞破屋而四散，烧人家。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('隋文帝开皇十七年', '597', '石陨于武安、滏阳间十余。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('隋文帝仁寿中', '601-604', '臣谨案：八月二十二日，仁寿宫内再雨金银之花。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('隋文帝仁寿二年', '602', '宫中再雨水银花。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('隋文帝仁寿四年', '604', '陕州又雨（水银花）。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('隋炀帝大业十一年十一月二十日', '615.12.15', '夜，有流星赤如火，自东北向西南，落贼帅卢明月营，破其橦车。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('隋炀帝大业十一年十二月戊寅', '616.1.14', '大流星如斛，坠贼卢明月营，破其冲輣，压杀十余人。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('隋炀帝大业十二年五月癸巳', '616.5.28', '大流星陨于吴郡，为石。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('唐太宗贞观八年七月', '634.7.31-8.28', '汾州青龙见，吐物在空中，光明如火，堕地地陷，掘之得玄金，广尺，长七寸。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('唐太宗贞观十八年十月内', '644.11.5-12.4', '申时，汾州、并州文水县两界天大雷震，空中云内落一石下，大如雉觜，脊高腹平，其文水县丞张孝静共汾州官同奏。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('唐高宗永徽四年八月己亥（二十日）', '653.9.17', '陨石于同州冯翊，十八，光耀，有声如雷。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('唐武后垂拱三年七月', '687.8.14-9.11', '广州雨金。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('唐武后垂拱三年七月乙亥', '687', '京师地震，雨金于广州。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('唐宪宗元和十二年九月己亥', '817', '甲夜，有流星起中天，首如瓮，尾如二百斛舡，长十余丈，声如群鸭飞，明若火炬，过月下西流，须臾，有声砻砻，坠地，有大声如坏屋者三，在陈、蔡间。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('唐昭宗乾宁四年', '897', '李茂贞遣将符道昭攻成都，至广汉，震雷，有石陨于帐前。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('唐昭宗天复十年庚午夏', '910．夏', '洪州陨石于越王山下昭仙观前，有声如雷，光彩五色，阔十丈，袁、吉、江、洪四州之界皆见光闻声，观前五色烟雾经月而散。有石长七、八尺，围三尺余，清碧如玉，堕于地上。节度相国刘威命舁入昭仙观内，设斋祈谢。七日之内石稍小，长三尺。又斋数日，石长尺余，今只及七、八寸留在观内。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('后汉隐帝乾佑三年十一月十一日', '950.12.22', '史宏肇十三日入朝，……出祸前二日昧爽，有星落于宏肇前三数步，如迸火而散。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('宋真宗天禧三年正月晦', '1019.3.8', '沈丘县民骆新田闻震，顷之，陨石入地七尺许。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('宋真宗天禧三年三月甲申', '1019.5.4', '颍州石陨出泉，饮之愈疾。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('宋英宗治平元年', '1064', '常州日禺时，天有大声如雷，乃一大星几如月，见于东南。少时而又震一声，移著西南。又一震而坠在宜兴县民许氏园中，远近皆见，火光赫然照天，许氏藩篱皆为所焚。是时火息，视地中只有一窍，如杯大，极深，下视之，星在其中荧荧然。良久渐暗，尚热不可近。又久之，发其窍，深三尺余，乃得一圆石，犹热，其大如拳，一头微锐，色如铁，重亦如之。州守郑伸得之，送润州金山寺，至今匣藏，游人到则发视。王无咎为之传甚详。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('宋神宗熙宁八年', '1075', '前一夕，大星陨于治所，枥马皆惊。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('宋高宗绍兴十三年六月', '1143', '先是，齐地数见怪异，有枭鸣于后苑，龙撼宣德门灭「宣德」二字，有星陨于平原镇。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('宋高宗绍兴二十六年七月辛酉', '1156.8.9', '夜，天雨水银，与唐志贞观广州雨金同。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('宋孝宗乾道五年七月乙亥', '1169.8.15', '武宁县龙斗于复塘村，大雷雨，二龙奔逃，珠坠，大如车轮，牧童得之。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('宋孝宗淳熙十六年三月壬寅', '1189.3.30', '陨石于楚州宝应县，散如火，甚臭腥。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('宋宁宗嘉泰二年', '1202', '有星陨于居第，遂薨。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('宋理宗端平二年春', '1235．春', '天狗坠怀安金堂县，声如雷，三州之人皆闻之，化为碎石，其色红。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('元成宗大德二年五月己酉', '1298.7.3', '抚州之崇仁，星陨为石。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('元成宗大德二年六月', '1298.7.10-8.7', '抚州崇仁县辛陂村，有星陨于地，为绿色陨石。邑人张椿以状闻。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('元英宗至治元年', '1321', '雨铁，民舍山石皆穿，人物值之多毙，谣俗号日铁雨。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('元英宗至治中', '1321-1323', '洱河东雨铁，民舍山石皆穿，人物值之多毙，号日铁雨。清雍正云南');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('元泰定帝泰定四年十月', '1327.11.14-12.13', '有大星陨于嘉县之虚初山，（化）为黑石，状如东瓜，上有点如星，击之锵然有声。人不言举之则动，言则弗动。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('元顺帝至元元年', '1335', '云南天雨铁，民舍山石皆穿，人物值之辄毙。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('元顺帝至正元年', '1341', '嘉县天雨铁，民舍山石皆穿，人物遇之多毙。明隆庆');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('元顺帝至正二年', '1342', '晋宁雨铁，伤禾苗，人物触之多毖。清康熙云南');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('元顺帝至正五年十一月甲午', '1345.12.8', '夜，一星陨于余轰后山，碎为四石；一星陨于佛奥，化为石，今尚存。清乾隆湖南');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('元顺帝至正十年正月甲戌', '1350.2.25', '棣州白昼空中有声自西北而来，距州二十里陨于地，化为石，其色黑，微有金星散布其上。有司以进，遂藏之司天监。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('元顺帝至正十年十一月冬至', '1350.12.14', '夜，陕西耀州有星坠于西原，光耀烛地，声如雷鸣者三，化为石，形如斧，一面如铁，一面如锡，削之有屑，击之有声。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('元顺帝至正十年十一月', '1350', '是月，三星陨于耀州，化为石，如斧形，削之有屑，击之有声。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('元顺帝至正十六年十月丁未（朔）', '1356.10.24', '大名路有星如火，从东南流，芒尾如曳彗，堕地有声，火焰蓬勃，久之乃息，化为石，青黑色，光莹，形如狗头，其断处如新割者，命藏于库。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('元顺帝至正十六年十一月', '1356.11.23-12.21', '大名路大名县有星如火，自东南流，尾如曳彗，坠入于地，化为石，青黑光莹，状如狗头，其断处类新割者。有司以进，太史验视云天狗也，命藏于库。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('元顺帝至正十九年四月己丑', '1359.5.24', '建宁路瓯宁县有星坠于营山前，其声如雷，化为石。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('元顺帝至正二十一年', '1361', '昆明县天落铁雨，伤禾稼，民居半圮。明隆庆');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('元顺帝至正二十三年六月庚戌', '1363.7.23', '益都临朐县龙山有星坠入于地，掘之深五尺，得石如砖，褐色，上有星如银，破碎不完。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('吴元年丁未七月（洪武元年前一年）', '1367.7.27-8.25', '空堕石如谷。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('元顺帝至正末年', '1366-1368', '天雨铁，伤禾，人物触之多毙。清康熙云南');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明太祖洪武二十六年六月十五日', '1393.7.24', '有星陨于蒲圻南团村，入地五尺，得石大如瓠，青色，形类狗头，盖狗星也。清康熙湖北');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES (NULL, '1403', '上从容言曰：“有非常事，则谓之灾变。禹任皋陶，天雨金三日，谓之祥瑞，何也？”');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明成祖永乐六年', '1408', '信阳平昌关北三里许，有大星坠地，化为石，其声轰轰，光芒射人，外则纯黑，中具五色，置之州治，至今尚存。明万历河南');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明成祖永乐十三年九月二十日', '1415.10.22', '正午，嘉定县东北白气一道，有声如雷，堕于宝山之南，获一黑石。清康熙江苏');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES (NULL, '1423', '陨石于黄海道江阴县，声如雷。遣官行解怪祭。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明宣宗元年十二月己巳', '1426', '有星大如碗，光赤，出卷舌，东行过东井坠地，有声如雷。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明宣宗宣德三年二月', '1428.2.15-3.15', '昼，有星陨于邳州民高浩家。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明宣宗宣德三年', '1428', '邳州民高浩家昼落一星，为石。（徐州府志）');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明英宗正统二年', '1437', '星陨，化为石。明嘉靖浙江');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明英宗天顺四年二月', '1460.2.22-3.22', '陕西庆阳陨石，大者四、五斤，小者二、三斤，击死人以万计，又有传石能言，可骇。（考异：明史五行志不载。二申野录同，今据之。）');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明宪宗成化六年六月壬申', '1470.7.23', '阳信雷声如啸，陨石一，碎为三，外黑内青。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明宪宗成化九年七月', '1473.7.25-8.23', '有星陨于山东莒城县马长史家门中，初堕地其光煜煜，而星体腐软如粉浆，马家人以杖抵之，没杖成穴，久而渐坚，乃成一石。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明宪宗成化十一年', '1475', '有车行夜过兰阳道者，有星坠于车中，化为石，大如鸡卵。明嘉靖河南');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明宪宗成化十二年十一月乙丑', '1476.12.11', '延绥波罗堡有星二，形如辘轴，一坠樊家沟，一坠本堡（仓），红光烛天。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明宪宗成化十三年', '1477', '县南空中陨石一，高六丈余。后胡荣刻有飞来石三字于上。清嘉庆四川');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明宪宗成化十四年六月辛亥', '1478.7.20', '山西临晋县天鸣，陨石于县东南三十余里，有声，入地三尺，大如升，其色黑。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明宪宗成化十七年七月', '1481.7.27-8.24', '星陨山东莒城马长史家，初坠地光动而体腐如粉浆，以杖筑之，杖毁，久乃坚如石。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明宪宗成化十九年', '1483', '六峰石陨。明嘉靖安徽');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明宪宗成化二十二年六月', '1486.7.1-30', '陨二星为石，上圆下平，状如杵，削之有屑，贮于库。清康熙河北');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明宪宗成化二十三年五月壬寅', '1487.5.25', '保定府束鹿县昏刻天地昏黑，空中声响如雷，寻有青气坠地，掘之得黑石二，一大如碗，一大如鸡卵。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明孝宗弘治三年二月', '1490.2.19-3.20', '陕西守臣奏：陕西庆阳县陨石如雨，大者四、五斤，小者二、三斤，击死人数万。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明孝宗弘治三年三月丁卯', '1490.4.4', '庆阳雨石，如卵如芡，作人语刺刺不休。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明孝宗弘治三年三月', '1490.3.21-4.19', '庆阳雨石无数，大小不一，大者如鹅卵，小者如芡实。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明孝宗弘治四年十月丁巳', '1491.11.15', '河南光山县有红光如电，自西南往东北，声如鼓，久之入地，化为石，大如斗。是日光州及商城亦见火星飞，其红光有声，如光山所见者。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES (NULL, '1492', '庆尚道观察使李克墩驰启曰：“去四月初一日，雷雨有石，陨于晋州，陷入地一尺，正兵姜继孙掘地得之，色如雷禊，状如茯苓，以爪刮之，隨爪落屑。”');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明孝宗弘治六年', '1493', '有星昼陨于北耿村东岳庙西，陨时有黑气，声如雷，取视乃一黑石，状似犬首，微热者久之。布政司具奏，石贮库。清乾隆河南');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES (NULL, '1495', '忠清道永春县，昼有物如鍮瓶：其色如火，自天而下，至地而灭，余光还上升，而散。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明孝宗弘治十年二月丙申', '1497.3.27', '河南修武县有黑气坠地，化为石，声如雷，状类羊首。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明孝宗弘治十年二月', '1497.3.4-4.2', '修武黑气入地，化为石，状如羊首。清乾隆河南');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明孝宗弘治十一年六月', '1498.6.20-7.18', '修武县东岳祠北又有黑气，声如雷，隐隐堕，村民李云往视之，得温黑石一枚，良久乃冷。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明孝宗弘治戊午年', '1498', '修武县东岳祠北忽有黑气，声如雷，隐隐堕地。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明孝宗弘治十二年五月戊寅', '1499.6.27', '山西朔州空中声如急雷，随有白气上腾，陨大石三。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明孝宗弘治十二年五月二十日', '1499.6.28', '朔州城北马圈头空中有声如雷，白气亘天，火光迸裂，落一石大如小车轮，入地七尺余，随有碎石迸出二、三十里外，色青黑，气如硫磺，质甚坚腻。明嘉靖');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES (NULL, '1501', '持平金熠曰：“平安道雨土、陨石，灾变不小。旱兆已萌，雨麦不熟，请禁酒。”');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明孝宗弘治十四年夏', '1501．夏', '马邑县西白日有火块自天而坠，如屋大，有声若雷，视之乃石也，其色青，入地二、三尺。明正德山西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明孝宗弘治十七年九月', '1504.10.8-11.6', '申时，西方大星坠地，其声如雷，烧园蔗。明嘉靖福建');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明武宗正德元年八月壬戌', '1506.9.2', '山东鳌山大嵩二卫，及即墨县各地震有声，夜有火光。落即墨民家化为绿石，形圆，高尺余。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明武宗正德三年', '1508', '有星陨于经堂，陨时声震如雷，入地尺许，化为石，其形光圆，中隆而旁杀，约二尺有余。清康熙广东');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明武宗正德八年五月甲午', '1513.6.29', '德庆州日中雨石，大如卵，色赤而黑，人皆拾之。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明武宗正德八年五月', '1513.6.3-7.2', '德庆州日中雨石，其色赤黑，大如拳，小似卵，人取食之。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明武宗正德八年七月甲申', '1513', '龙泉有赤弹二，自空陨于县治，形如鹅卵，跃入民居，相鬬久之。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明武宗正德九年五月己卯', '1514.6.9', '山东济南府滨州天鼓鸣，陨石。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES (NULL, '1515', '御夕讲。讲《高丽史节要》，至陨石于黄州，声如雷，州上其石。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明武宗正德十三年正月己未', '1518.2.28', '邻水陨石，一。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明武宗正德十三年十月', '1518.11.3-12.2', '星陨为石，大如斗。明嘉靖河北');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明世宗嘉靖九年', '1530', '天坠石于西乡枯逻村梁强家，倏变为金银没地，忽天开裂。清康熙广西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明世宗嘉靖十年七月', '1531.8.12-9.10', '星陨如雷，火光烛地，化为石。明嘉靖河北');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明世宗嘉靖十一年', '1532', '有星陨于紫云宫，化为石。清康熙河南');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明世宗嘉靖十二年五月丁未', '1533.5.28', '太原府祈县天鼓鸣，火流坠地为石。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明世宗嘉靖十二年五月', '1533.5.24-6.21', '祁县空中有声如雷，落一石，如拳。明万历山西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明世宗嘉靖十五年十月初八日', '1536.10.22', '郯城星陨，昼视之，则石也。明万历山东');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明世宗嘉靖十五年十月', '1536.10.15-11.13', '有星陨沂州城中。清乾隆山东');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明世宗嘉靖十六年夏', '1537．夏', '有星陨于郯城，化为石。清乾隆山东');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明世宗嘉靖十六年九月', '1537.10.4-11.2', '有星陨于郯城，成巨石。清乾隆');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明世宗嘉靖十六年', '1537', '有星陨于郯城，旦视之，则石也。清乾隆山东');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明世宗嘉靖十八年', '1539', '仙屯村田中有星陨，化为石，藏之府库，后不知所在。清康熙广东');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明世宗嘉靖十九年五月辛丑', '1540.6.14', '冀州枣强县午时天鼓鸣。夜，星陨为石，四。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明世宗嘉靖十九年', '1540', '枣强县昼星陨。时东北天鸣如雷，一星陨于城东北，枣林村一，太湖村二，俱化石。明嘉靖河北');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明世宗嘉靖十九年五月五日', '1540.6.9', '午时，太湖村忽闻天鼓，自东南连鸣三声，天晴而雷震，须臾明星陨，地裂深丈余，村人陈仲、崔洪、岳本，张雨等寻获星石四，呈县贮库，巡按以闻。清乾隆河北');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明世宗嘉靖二十年九月', '1541.9.20-10.18', '有星陨于二都民舍，化为石。清康熙湖南');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明世宗嘉靖二十二年四月丁亥', '1543', '辽东铁岭卫大火，延烧庐舍人畜，有火星如斗坠地。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明世宗嘉靖二十二年七月', '1543.8.1-29', '迅雷、暴雨、有石陨，大如升，其色赤。清康熙陕西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明世宗嘉靖癸卯年', '1543', '迅雷、暴雨，有石陨，大如升，其色赤。清顺治陕西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES (NULL, '1547', '传曰：“地震及名山雷震，则例有祭，今国都主山陨石，亦当祭乎？遣史官议于三公。”');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明世宗嘉靖三十年正月辛卯', '1551.2.7', '大风霾，昼晦，连江雨石，有声如雷。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明世宗嘉靖三十年正月', '1551.2.5-3.6', '连江雨石，其声如雷。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明世宗嘉靖三十年', '1551', '雨石于连江，有声如雷。清乾隆福建');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明世宗嘉靖三十年三月', '1551.4.6-5.5', '安庆里大石陨，有声如雷。清乾隆福建');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明世宗嘉靖三十一年秋', '1552．秋', '陨星石于城北七里，其大如斗，其声如雷。清光绪山东');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明世宗嘉靖三十二年三月十二日', '1553.3.25', '夜，三星陨于平越高坪寨，为石。明万历');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明世宗嘉靖三十九年四月', '1560.4.25-5.24', '陨石于华亭五舍镇。越数月，其石自动，一夕风雨失去。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明世宗嘉靖三十九年夏', '1560．夏', '郡西南五舍镇天陨一石。越数月，其石自动，忽一夕风雨失去。明崇祯上海');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明世宗嘉靖四十一年', '1562', '有火坠城，化大池。清顺治湖北');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明世宗嘉靖四十一年四月朔', '1562.5.3', '夜初，俄有星自乾方来，明耀若昼，坠落县堂前树上，火星纷纷下落。清乾隆河南');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES (NULL, '1563', '庆尚道山阴县北里，雷声大作，如击鼓射矢之声。人仰视之，如有物自天而下，状如飞燕。近而见之，则翻作飞鸟之形，落地有声而烟生。俄而又落，就而见，墮于积石之上者破，碎落于厚地者入地一尺许，状如石，外黑内白，与石无异。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES (NULL, '1563', '传于政院曰：“观庆尚监司书状，庆尚监司郑宗荣书状，二月十八日，山阴县陨石。则天之陨石，灾异骇怪。其石斯速上送事，下谕，又令弘文馆，考启古事。”');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明世宗嘉靖四十二年三月癸卯', '1563.4.17．', '河南怀庆府天鼓呜，有石陨，入地尺许。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明世宗嘉靖四十四年四月', '1565.4.30-5.28', '有星陨于大足县之东野，入地三尺，声如雷，色黑，形如狗头，火气逼人，经宿方散。明万历');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明穆宗隆庆二年三月己未', '1568.4.6', '保定府新城县空中有声如雷，陨石二，色黑。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明穆宗隆庆二年三月', '1568.3.29-4.26', '直隶新城县空中迅响三次，其声如雷。二圣庙前天鼓鸣三次，南面六十步天下火光一块，陷地一尺，跑出黑石一块，如碗大。许家庄亦落一星，天鼓鸣三次，火光落地，陷一孔如拳大，出黑石一块，重二斤十四两。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明穆宗隆庆二年五月', '1568.5.27-6.24', '新城星陨二，化为石，一在来达村，一在西庄村。明万历河北');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明穆宗隆庆二年六月', '1568.6.25-7.23', '静乐楼烦雁门村，昼则星落入地，掘出黑石，重千斤，奏闻。明万历山西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明穆宗隆庆二年七月', '1568.7.24-8.22', '静乐陨石。楼烦碣石村，昼星落入地，掘出黑石重千斤。清康熙');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明穆宗隆庆二年', '1568', '静乐楼烦碣石村，昼星落入地，掘出黑石重千斤。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明穆宗隆庆三年十二月初七日', '1570.1.12', '昼，星陨蒋疃集西，光芒，有声，入地，农入掘之深尺余，得一石，大如杵，其色碧，上有金星。清康熙安徽');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明穆宗隆庆六年九月十七日', '1572.10.23', '午时，南乐县属罗疃村火光入地，掘之得石，如杵形，红色，击为三，色转黑，研之转白。清咸丰河北');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明穆宗隆庆七年六月', '1573.6.29-7.28', '静乐楼烦雁门村，昼见星落入地，掘出黑石重千斤。明崇祯');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历元年', '1573', '静乐雁门村星落，入地五尺，掘出黑石重千斤，奏闻。清康熙山西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历元年', '1573', '有星陨于永平里，入地三尺，化为黑石，掘出尚热。清嘉庆陕西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历二年二月', '1574.2.21-3.22', '县西南五十里，星陨如碾，触石尽碎，其色玄黑，明星荧荧。清康熙山西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历二年', '1574', '寿阳县西，星陨如碾，触石尽碎，其色玄黑，明星荧荧。明万历山西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历三年五月癸亥', '1575.7.3', '昼，景州天鼓呜，陨星二，化为黑石。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历三年九月', '1575.10.4-11.1', '万载县有巨石自天而堕。清康熙江西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历四年十一月甲午', '1576.11.25', '是夜三更，有四星陨于费县，火光随之，天鼓鸣，质明，有红点落于费城西北，色如朱，长二里，阔一、二尺。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历四年十一月十八日', '1576.12.7', '三更时，大星向东南落，光若白昼。次日视之，有血点迹在费县西北十数里之间。明万历山东');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历四年十一月十九日', '1576.12.8', '三更时，血雷自西北起，向东南。又火星向东南落，光若白昼。次日视之，又有血点迹在城西北十数里之间。清康熙山东');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历五年九月', '1077.10.11-11.9', '演武场午后隐隐雷声，须臾自天坠下一斜角石，入土尺余，掘之，初指甲可破，俄遂坚，人讶为星陨石，今犹存。清康熙江西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历六年六月', '1577.7.5-8.2', '有星夜落州东乡，牧人识其处，掘之得物如石，色青，长九寸，广四寸，下锐上平。清乾隆江苏');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历七年', '1579', '荣河陨石于西头村，形圆，色黑。明万历山西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历九年八月', '1581.8.29-9.27', '有声如雷，陨石南郊。清康熙四川');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历九年八月末', '1581.9.23-27', '安岳县有声如雷，陨石南郊。清乾隆四川');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历九年九月', '1581.9.28-10.27', '揭阳星陨于南塘山，化为石。清康熙广东');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历九年秋', '1581．秋', '星陨于霖田都南塘山，为石，乡民得之，状似马面，秤重六斤八两，送贮县库。清乾隆广东');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历十四年四月', '1586', '兵部题保定巡抚都御史贾三近，题称大名府滑县地方近有星光坠地。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历十六年九月', '1588.10.20-11.18', '岢岚州天鼓鸣三日，至四日陨星，其声如雷，化为石，青黑色，长三尺余，形如枕。明万历山西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历十七年九月戊午', '1589.10.22', '袁州府万载县以霜降节行祭，忽闻天鼓鸣，有黑烟腾起，陨石于演武厅畔。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历十八年四月丁丑', '1590.5.9', '高邑人杨大宁耕田，见空中陨石，广厚俱方三寸，色黑。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历十九年四月辛酉（二十六日）', '1591.6.17', '遵化县提举庄、太平庄二处天鼓鸣，如炮响，空中有火光，各陨石一块，重各数斤。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历二十年二月丙辰（二十五日）', '1592.4.7', '有三星陨于闽县东南。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历二十年十二月', '1592', '礼部类奏本年灾异，甘肃诸镇浙粵诸省地震不止一隅，荣河等邑蒲解等州天鸣，适当同日雹飞，石陨。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历二十一年夏', '1593．夏', '有大星陨于新仓，化为石，声闻数十里，光彻远近。清乾隆浙江');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历二十二年六月望日', '1594.8.1', '有星陨于昭化县之三堆，初坠入地，掘三尺许，气若蒸，得黑石，如斗大。明万历');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历二十二年夏', '1594．夏', '有大星陨于新仓，化为石，声闻数十里。清康熙浙江');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历二十二年', '1594', '有星陨于西关田中，入土数尺，王希泉公庄人掘出，其色深黑，奇形异状，俨然石也，置之祖先阁。清雍正四川');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历二十三年八月二十七日', '1595.9.30', '夜半，雷震异常。诘旦，镇东楼向北一柱烟袅，掘之，其下得火石，大如球。清康熙河北');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历二十四年五月庚辰（十四日）', '1596.6.9', '顺天抚按李颐等题：夜，有坠星下抚宁，至地为石。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历二十五年八月二十七日', '1597.10.7', '巳时，天鼓鸣，有飞星带火光坠于河内县常平镇。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历二十六年', '1598', '有星陨徐河东，光耀数亩，色如磁石。知州曹士毅藏于库。清乾隆江苏');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历二十七年闰四月初二日', '1599.5.25', '据广平府永年县申，据本县赵王固村保长刘折桂、地方冯思义呈称，本月初二日未时，本村东南天落一星，声如炮响，坠地化为石，如碗大，破碎不全等因。又据，南圈子村居民许展生在园内浇麦，见一星落地，化为石，破碎不全。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历二十七年', '1599', '白昼，星陨赵王固村东，有声如雷，坠地深三尺，色碧如石，质不甚坚。明崇祯河北');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历二十八年八月丙申', '1600.10.2', '河内县天鼓鸣，有星坠常平镇北二里，掘之尺许获石，肤黑中白。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历二十八年八月二十七日', '1600.10.3', '河内县堕一流星，入地数尺，发之，去外黑皮，乃银也，重一百六十两，寄开封库。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历二十八年', '1600', '河南天鼓鸣如雷，有飞星大如升，带火光堕地，掘之尺许，获一石，外黑中白，重百六十两，寄开封库。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历三十三年九月戊子', '1605.10.28', '有星如碗，坠于南京龙江后营，光如火，至地游走如萤，移时灭。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历三十三年九月十七日', '1605.10.28', '戌时，南京龙江陆兵后营有星大于碗，其光如火，堕于阅兵后台，至地粉碎，游走如萤，移时乃灭，化为黑灰。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历三十三年秋', '1605秋', '泰州天鸣累日，声如怒涛。镇江至宜兴一带天鸣如雷。泰州留都教场星陨如灰。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历三十三年十一月', '1605.12.10-1606.1.8', '有星陨南京教场，入地无迹。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历三十五年四月', '1607.4.26-5.25', '星陨于东平村，坠地有声，入深数十尺，口窄而中甚宽，取之为难。清顺治山西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历三十七年八月', '1609.8.29-9.27', '夜，天陨星如球，在灵门内卤塘汇陈姓家。时天炎，浮桥上乘凉者共见之。次早入城往渠家相视，见大石如盘，光色闪烁，人争碎之，各取一块以归。清康熙浙江');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历三十七年', '1609', '星陨于宁海。清康熙');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历三十九年', '1611', '有星陨于西乡华张桥侧之槿木冈（落地化石，石上有孔，至今土人不敢动移。相传其地为天星岩）。清乾隆安徽');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历四十年十二月二十五日', '1613.2.14', '申时，四川顺庆府广安州无风无云，雷忽震动，坠石六块，其一重八斤，一重十五斤，一重十七斤，小者重一斤或十余两，岂有三十里之径，而仅一拳之石多哉。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历四十一年八月八日', '1613.9.21', '午时，星陨于东南者三，声如雷，一离城陨石三里许，入地丈余，重四十八斤，一五里许，重十（七）斤；一七里许，重九斤，俱坚如铁石。清乾隆山东');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历四十二年四月', '1614.5.9-6.6', '星陨，火光闪烁，空中声震如雷，自西而东，落于邑西南小崔村井旁，入地尺余，色如墨，形如杵。知县顾师曾考之，属乌鸡星，申府院。清康熙河南');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历四十三年三月戊申', '1615.3.30', '昼，星坠清丰东流邨，声如雷。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历四十四年', '1616', '星陨有声如雷，陨于县北十里许地名北沟，形如白石。清康熙河南');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历四十四年正月丁丑', '1616.2.22', '易州及紫荆关天鼓鸣，源泉有火光堕地，化为石，崩裂。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历四十五年八月', '1617.8.31-9.29', '山东武城县天降异物三块，其声如雷，重各有斤两。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历四十五年九月', '1617.9.30-10.28', '陨星三，贮府库，高尺余，如铁，黑而明。清嘉庆山东');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历四十六年十月辛酉', '1618.11.22', '昏，有星如斗，霹雳一声，陨于南京安德门外，化为石，重二十一斤，而万善乡亦报星石二块，重一百三十斤，俱存京库。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明光宗泰昌元年十月二十三日', '1620.11.16', '天鼓震地，黑云拥石，陨于荆门钟祥界泉口，其石绿豆色，硫磺气。明天启湖北');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明光宗泰昌元年十二月', '1620', '礼部类奏又言：辇毂之下屡见数日叠出，白气横天，流星坠地。然犹八月以前灾异也。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明熹宗天启元年冬月', '1621.11.13-12.12', '某日午时，有石如大磨，坠于泉口地方，入地三尺，有声。……后差人起出解上，已裂成数块，有火药气味。清康熙湖北');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明熹宗天启二年冬', '1622.冬', '荆门州陨石，入地三尺，有声。清乾隆湖北');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明熹宗天启二年', '1622', '天鸣星陨，所陨星在理刑署库，形甚勰皱，非石非铁，气如硫磺，权之可重五镒。清康熙河北');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明熹宗天启三年', '1623', '荥阳（汜水、成皋）陨星三，化为石，形类升而长，收贮县库。清顺治河南');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明熹宗天启四年四月', '1624.5.17-6.15', '星陨石枧，声如雷，其色黑。清道光江西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明熹宗天启六年五月', '1626.5.25-6.23', '天鼓鸣，星陨入地，乡人掘土得之，质坚而色黑。清康熙河北');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明熹宗天启六年', '1626', '安肃天鼓鸣，星陨入地，掘之得如石而黑者。清康熙河北');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明庄烈帝崇祯二年二月', '1629.2.23-3.24', '萧县陨星，如狗首，着地尚热。清乾隆江苏');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明庄烈帝崇祯二年', '1629', '龙山坠一星，形如狗首，落地尚热。清康熙安徽');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明庄烈帝崇祯三年四月二十七日', '1630.6.7', '星陨于大陂田中，化为石，大如碗，现存县库中。清康熙福建');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明庄烈帝崇祯九年五月辛未', '1636.6.30', '东门八里河有星声如雷陨地，气热如火，时有耕夫往掘之，土犹然沸，得一石方二尺许。明弘光江苏');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明庄烈帝崇祯九年', '1636', '通州陨星，声如雷，气热如火。清康熙江苏');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明庄烈帝崇祯九年九月丁未', '1636.10.4', '太康陨石。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明庄烈帝崇祯十二年二月', '1639.3.5-4.2', '肤施晚有众星坠于民舍，击瓦纷飞。清康熙陕西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明庄烈帝崇祯十二年四月二十三日', '1639.5.25', '莆尾城有长星陨于地，化为石。清康熙福建');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明庄烈帝祟祯十二年', '1639', '星陨（满子村天焰忽明，陨一物如石，有声，状如犬首，黑紫色，寄库，今无存）。清康熙河北');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明庄烈帝崇祯十二年', '1639', '有大石忽陨于小市街，坏民居数十楹，死者数十人。清康熙四川');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明庄烈帝崇祯十三年元旦', '1640.1.23', '汶上集天上有声如雷，薄昏星坠，形类石滚，或铁击之，随火光而散。清康熙山东');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES (NULL, '1644', '黄海道信川郡星陨为石，其声如雷。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清世祖顺治八年五月初一日', '1651.6.18', '辰刻，东长安门外陨青石一块，大如斗。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清世祖顺治八年', '1651', '上下阴山界，有赤光从天入地，两村以为奇宝，互相争讼。启视之，乃碧石一块，形如纱帽，文如蝌蚪，人莫能识。今存上阴山龙王庙。清乾隆山西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清世祖顺治十年四月', '1653.4.27-5.26', '泸州星陨化为石，大如斗。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清世祖顺治十三年二月己未（初十日）', '1656.3.5', '午刻，河南空中有声不绝，黑气大如斗，光芒如灯，坠于宁陵县，形似石，重五十三两。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清世祖顺治十三年二月', '1656.2.25-3.25', '河南宁陵县陨石。时日方午，空中有声不绝，黑气如斗，光芒如灯，坠于宁陵，如石砚，重五十三两，抚臣以闻。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清世祖顺治十三年三月', '1656.3.26-4.23', '夜，烈风，城内堕石，压居民数家。清乾隆湖北');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清世祖顺治十三年八月十三日', '1656.9.30', '午时，永安县益口畲村前，数樵牧在坐，空中坠一物，有声，形如小狗，角尾鳞爪悉具，滚地立成一窟，中有黄泥水，三日水清，莫测其底。清康熙福建');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清世祖顺治十四年五月初五日', '1657.6.16', '午时，有星陨，声如雷，夹红白云以飞，至黄冈入地三尺，化为青石，重十三斤，缴潮库后送广城。清嘉庆广东');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清世祖顺治十八年八月', '1661.8.25-9.22', '天鼓鸣，夜陨石于杨氏屋，如斗，穿茅落塌前，入地五寸，无所伤。清康熙湖南');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙二年二月十九日', '1663.3.28', '京师天鼓鸣，随陨星十一，城外七，城内四。次朝拾视，热如炽炭，大者十余斤，小者不下六、七斤。一星碎为三块，外青内白，一星缺一角。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙二年十二月初三日', '1663.12.31', '夜半，自东南来火光如斗，有声，落于西北。次日传星陨于河滩为石。清康熙山西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙四年四月十七日', '1665.5.31', '武林富阳县太平村风日晴朗，忽于巳刻天气晦黑，有大石堕地，声如雷，拆而为二，重数百斤。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙五年三月', '1666.4.4-5.3', '夜，星陨姚家坂屋后，声如雷鼓。村民晓发地中，已成石，大如斗，色青黑，腰有线欹缠，灼烁同金光。解送会城抚院，按验时大中丞蒋公令于辕门内试斫之，刀刃不能损，复令周以炭炽熔之，火尽益坚，遂收贮诸库。清康熙浙江');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙五年秋', '1666．秋', '晴空中忽霹雳，坠巨石于南乡殴田。清乾隆江西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙九年九月二十五日', '1670.11.7', '巳时，星陨一，其大如斗，从空坠落有声，屋檩摧折，入地尺许。清康熙河北');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙九年', '1670', '陨星于城内西街刘家花园，今尚存。民国河北');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES (NULL, '1672', '有石陨于永川郡，正月二十九日午时，天有微云，不见日色，忽然天中有声，始如大炮，终如隐雷。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙癸丑三月二十五日', '1673.5.11', '宋时，有星陨于定边，大径尺，色如羊肝。清康熙陕西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙十二年三月', '1673.4.17-5.15', '有星陨于定边。清康熙陕西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙十三年五月', '1674.6.4-7.3', '宁远坠二星，化为红石。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙十三年十二月', '1674.12.27-1675.1.25', '天鼓响，陨星为石。清康熙甘肃');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙十四年四月十八日', '1675.5.12', '富阳江口地名陆山者，居民夜见坠二星，如雷入地三尺，县令出视，发掘得二石，各重四斤有余，则俨然金也。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙十五年五月初九日', '1676.6.19', '青浦县瓢湖地方大星陨，坠地有声，居人争掘之，见一石，色黑，手按尚热，称之重十九斤，击碎以刀摩之，火光四射。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙十五年五月', '1676.6.11-7.10', '青浦星陨，坠地有声，居民掘之，见一黑石，按之尚热，重九十斤，击碎以刀摩之，火光四射。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙十六年十月二日', '1677.10.27', '京师星陨，朝廷遣使迹之，得巨石，有古文，人莫能辨。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙十六年十月初七日', '1677.11.1', '更余，京师人见大星自东南流入西北，大如斗，白气环绕，从上属下，堕顺天府怀柔县内，状类人首，上有星斗篆籀之文，人莫能辨。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙十七年七月十七日', '1678.9.2', '申时，有流星自西北至东南，声隐隐如雷。陨而为三，皆为石，质轻而黑，初软后坚。一在东井庄冈头田，长二尺许，围如长者四分之三，形如碓觜而椭，重八斤；一在店门前潘家坦，重六斤；一在陆杨村竹园，已碎。是时日尚高，正在祈雨，人皆见之。清乾隆安徽');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙二十年正月二十日', '1681.3.9', '海丰有星陨化为石，其形三角，重九斤。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙二十一年', '1682', '王冈在广东潮州界，一星陨为二石，大者八十余斤，小者二十余斤。小者坠于田，大者坠于鱼池中，旋跃出复坠于田，陷土二尺许。石色黑，中有金星。今贮潮州府库。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙二十二年六月二十日', '1683.7.14', '申刻，贵溪县五十一都一图地方，忽天上响声如炮，有白云一朵坠地有声，复黑气一道自地上升起，某处有黑石一颗，入地三尺，掘取验看，石色黑，有斑驳如锻炼成，嗅如锈銕气。清康熙江西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙二十四年正月初六日', '1685.2.8', '饶平星陨黄冈五丈港，声闻数十里，化为石，其大如斗，其色外青内白。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙二十四年三月初六日', '1685.4.9', '星陨黄冈五丈港数颗，声闻数十里，化为石，其大如斗，其色外青内白。黄冈巡司解府。清康熙广东');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙二十七年三月', '1688.4.1-29', '星陨于待补，入地数尺，复跃出数尺余，红软光润，禄氏藏于山中，后不知所在。清乾隆云南');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES (NULL, '1705', '金城东面，十月初一日星陨为石。道臣状闻。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙四十七年六月', '1708.7.18-8.15', '陨星有声如雷，化为石，在建德县。民国');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙四十八年十月初九日', '1709.11.1.0', '星陨罗家荒，化为石，入地深二尺，重二十余斤。清乾隆江苏');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙四十八年', '1709', '安东县星陨声如雷，百里俱震动，坠于西南邻界罗家荒，化为石，深入地二尺，重十三斤。清乾隆江苏');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙五十六年五月初四日', '1717.6.12', '雨磺，金钟山顶石压土崩，其声如雷。清康熙福建');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙五十八年十二月朔', '1720.1.10', '长畲黄昏后，闻有烈炮声自东方，少焉近南，从空坠一物，闪闪有光，大如鹅卵，铁质，甚热，作硫磺气。清乾隆江西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙五十九年四月丙午（初十）', '1720.5.16', '星陨于恩县三，各成石，两大一小，相去各十里。清乾隆');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清世宗雍正三年正月', '1725.2.13-3.13', '天铳响东南，光流入地，化为鉎铁，掘视，尚余硫磺气。清雍正广东');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清世宗雍正三年春', '1725．春', '连平天鼓鸣，陨石，状如鉎铁，气若硫磺。清光绪广东');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清世宗雍正八年正月', '1730.2.17-3.18', '清水堡夜半天有红光一道，随霹雳一声落地，下入四尺，掘之得黑石一块，如碗状，送供神师庙，尚存。清乾隆陕西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清世宗雍正八年八月', '1730.9.12-10.11', '府谷星陨，入土四尺，掘之，得一黑石。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清高宗乾隆九年四月二十六日', '1744.6.6', '有星陨于四十九都小黄沙地方，声如炮，大如斛，入地二尺许，色黑，如朽木，易碎。清乾隆江西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清高宗乾隆十三年五月四日', '1748.5.30', '洋蛮河民丁某家陨石。清乾隆江苏');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清高宗乾隆十三年五月', '1748.5.27-6.25', '暴风，雨雹，拔木，坏屋，陨石。清嘉庆江苏');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清高宗乾隆十四年十月二十九日', '1749.12.8', '双古堆集陨星一。清光绪安徽');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清高宗乾隆十六年五月十六日', '1751.6.9', '午，寿宁星坠田，水沸，有声如炮。石大三寸，高六寸，色黑。清乾隆福建');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清高宗乾隆十七年正月十二日', '1752.2.26', '夜初更，有星寸许陨于东北，其声如雷，光芒数丈，木叶遭之尽脱。清乾隆福建');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清高宗乾隆十九年三月六日', '1754.3.29', '雷声微震，有乡民见烟气缭绕属于地，复缭绕而上，掘之得一石，甚热，报官，贮于库中。清乾隆河南');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清高宗乾隆三十五年三月', '1770.3.27-4.25', '乐安空中有光如炬，掘地得一石，铁色，大如斗，叩之有声，欲舁之，不语则举，语则虽大力不能举。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清高宗乾隆三十五年', '1770', '添授乡殷坊有樵子夜入山，见空中有火光如炬，日以为常，寻迹其处，掘地得一石，大如斗，铁色，叩之有声，舁之，莫语则举，语则虽大力不能举也。清同治江西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清高宗乾隆四十年七月初四日', '1775.7.30', '午时，城南毕冲铺天落石，声如霹雳，重者百十斤，细者类砂砾。清嘉庆安徽');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清高宗乾隆四十年八月', '1775.8.26-9.24', '钜口县属吴家集陨星一，化为黑石。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清高宗乾隆四十年秋', '1775．秋', '邑城南十里许吴家集陨星一，入土化为黑石。清道光山东');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清高宗乾隆四十六年四月初七日', '1781.4.30', '陨星为石二，大者重一百九斤，陨小方沟，小者重七十余斤，陨安乐坝。清咸丰四川');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清高宗乾隆四十六年十月', '1781.11.16-12.14', '落塘汛天陨石二，入地数尺，土人锄出，金光灿然。清道光贵州');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清高宗乾隆四十七年四月八日', '1782.5.19', '陨石殿在文昌殿左侧。乾隆四十七年四月八日陨石于小方沟文、杨二姓地界，质类铜铁，数日犹暖，县尹冯某迎至于此。民国四川');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清高宗乾隆四十七年六月', '1782.7.10-8.8', '城南二十五里麻车庄莲花庵后有星陨，声如雷，旋化为石，其光荧荧，经月始散。今其石犹存。清道光安徽');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清高宗乾隆四十七年八月', '1782.9.7-10.6', '滕县星陨忠三保杨氏院中，化为石，色青白，重约百余斤，孔数百，大可容拳，小容栗。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清高宗乾隆四十九年夏', '1784.夏', '夜，露坐，星月交辉．天无纤翳，忽闻天际巨响，震耳如霹雳，如山崩，举首视之，火光烛天，大异。次日，始知党山海滨陨一星，圆围数丈，入地数尺。当时附近居民往观，光芒煜煜，不敢逼视。越数日，观者甚众，余亦往，见星青色，尚有流光，数月之后，俨然成石矣。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清高宗乾隆五十一年十一月', '1786.12.21-1787.1.18', '三星夜坠，大如斗，其声如雷，一星坠于南，一星坠于西，一星坠于澎湖海中大石上，石裂。清嘉庆台湾');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清高宗乾隆五十八年四月', '1793.5.10-6.7', '分宜陨石于田，巨声如雷，黑色。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清高宗乾隆五十八年四月晦', '1793.6.6', '二十都陨石于田，声如炮，其色黑。清道光江西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清仁宗嘉庆五年六月', '1800.7.22-8.19', '星陨于隆回乡，其声如雷，落地裂如石，碎之如钢，星光朗然。清嘉庆湖南');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清仁宗嘉庆六年七月', '1801.8.9-9.7', '有星如鹅卵，尾拖白光数十丈，自北而南，落地有声如雷，三都民见白光遗田中，掘之得黑金。清嘉庆湖南');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清仁宗嘉庆八年十二月庚寅', '1804.2.10', '有黑星降于地，大如碗。清同治江苏');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清仁宗嘉庆十年春', '1805．春', '陨石于西三，有声。清嘉庆广东');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清仁宗嘉庆十六年', '1811', '陨石于一都，凡七，大如斗，色黑。清嘉庆湖南');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清仁宗嘉庆十七年十月中旬', '1812.11.14-24', '梁苑《陨石歌》：嘉庆才七岁壬申，月恰中旬，日方过午烟云扫，多风多雨多纤尘，似雷非雷声虩虩，不及掩耳震四邻，瞻仰昊天何所有，忽然怪石落海滨，我邑下梁和下陆，飞来五、六何嶙峋，形大于升小于斗，入地尺许迹未湮，外黑而青内自白，更似铁痕个个匀，正从大造炉中出，气热如焚手难亲。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清仁宗嘉庆十七年十月二十日', '1812.11.23', '晡，有物状如鸟尾，垂如须，闪色金碧，自西南隅飞陨太平市前桥下，化为石，声若雷鸣。清光绪浙江');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清仁宗嘉庆十八年六月二十七日', '1813.7.24', '酉时，星落于嘉定歧邨．星大如斗，团圈能动，其光气直泻，许久乃息。清光绪广东');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清仁宗嘉庆十九年', '1814', '三十九都高垠村农人操耘具事于南亩，日午群集垌中，天色晴明，众方俯首芟柞，忽昏暗若暮夜，众咸惊异，企立盼望，觌面如不见，顷之光开一线，仰见黑气弥漫，从空冉冉而散，日复晴霁如前。俯视田中，顿开三穴，以石投之，其深无际，一穴水黑，其味腥，一穴水白，其味甘，一穴水清，饮之可以疗疾，里人争汲，络绎不绝，月余泥淤始塞。民国广西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清仁宗嘉庆二十年六月二十五日', '1815.7.31', '桂平鹧鸪村田间有白气两道，上冲霄汉，少时气灭，火星四散，空中有声，食顷乃止，火星坠地化如弹子，非铁非石，作硝磺气。清道光广西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清仁宗嘉庆二十一年六月十日', '1816.7.4', '星陨大荡圩田中，化为石。清道光江苏');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清仁宗嘉庆二十二年十二月', '1818.1.7-2.4', '大星陨关渡，陨声如雷，化为石，入地中，掘视之，形圆，质坚而色黑。清同治台湾');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清仁宗嘉庆二十三年三月二十日', '1818.4.24', '东安县宣上乡日方昼，有声磌然击野田，水立二、三丈，邻近耕农譁然惊顾，旋趋视之，田为之凹然陷者若池若井，上下左右计十有四，小者广寻丈，大者周迥不下两亩，旋为水没，以绳系石测之，五、六丈未见底。其田相去里许，有井泉清冽，至是二、三日忽皆混浊。是何祥也，此必星陨为之也。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清仁宗嘉庆二十三年十一月二十五日', '1818.12.22', '长星落，有声如雷，土人视其陨处成一坑，掘之得一石，长二尺余，阔尺余，形方而角圆，击碎之，中分五色。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清宣宗道光四年二月初四日', '1824.3.4', '申时，陨石于县东北插花庙保朱宅庙地方一，入地五尺余，陨石附近数十里闻响声如鼓，际天红光，黑气相属，逾刻始散。清道光安徽');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清宣宗道光九年十月十三日', '1829.11.9', '台湾北礁四更初，有星大如斗，光芒自南而北，坠于北礁外海中，声响如雷，有余音。又据渔民云：亲睹坠及海面，裂为两星，声响如炮，旋碎海中，流光四散，声犹虩虩，片刻方止。清道光');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清宣宗道光十二年四月十六日', '1832.5.15', '申初，陨石三，陨星也。一陨马斯水口许姓邨前芋田。未陨之先，声若震雷，仰见一物色甚黑，从天而下，大如斛，旋转入田土，人急掘之，约二尺许得一石，色类羊肝，通身皆凸，重数斤，嗅之腥。一陨油田黄坑，石方而长，重二十余斤，一陨横槎鱼烂湴。清咸丰广东');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清宣宗道光十三年二月', '1833.3.21-4.19', '天鼓鸣，星陨于三道关，大如轮。清咸丰辽宁');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清宣宗道光十八年正月初六日', '1838.1.31', '辰刻，东北角空中如雷声响，闻二、三百里。剑州、武连等处有红物大小堕落，入土尺许，即时掘取，如炭圆，尚热。清同治四川');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清宣宗道光十八年', '1838', '又陨石于黄岩县，大如斗。民国');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清宣宗道光十九年', '1839', '建德西溪大坞陨石，广丈余，长两倍之。清光绪');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清宣宗道光二十年', '1840', '巽方有声如雷，十字庄东岭陨石数片，其色如炭，每片约重二、三斤许。清光绪山东');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清宣宗道光二十二年六月', '1842.7.8-8.5', '二都东落陨石。清同治湖南');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清宣宗道光二十二年', '1842', '有石长三尺余，自龙神庙后山飞下，穿庙东厢一穴，入止檐下，庙宇仍完固不坏。石今尚存，似龙首形，以香火供之。清光绪广西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清宣宗道光二十五年四月', '1845.5.6-6.4', '有大石陨于东门外关帝庙侧，坏官山坟茔百余所，未伤人。清光绪四川');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清宣宗道光二十五年七月', '1845.8.3-9.1', '旧善矮陂各乡陨石，大者十余两，小者数两，其色赤，味如硫磺。光绪清广东');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清宣宗道光二十六年五月', '1846.5.25-6.23', '旧善横沥乡天鼓鸣，陨石，状如鉎铁，大者重数斤。清光绪广东');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清宣宗道光二十六年新秋后数日', '1846.8.22-26', '星堕地震。夜将半，忽有大风骤起，势如山崩地裂，时伯兄也山卧病，大侄女侍疾未寝，风起时适以开窗倾水，即见红光一团，其大如盆，赤气四射，疾若飞星，自北而南，后有流星万点，随之以落，其声如雷，地即大震，室中诸器尽皆倾覆，时闻人喊马嘶，家家扶老携幼，开门奔避，盖虑屋室之亦塌也。喧呶竟夜，天明始息，未知何祥。清光绪江苏');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清宣宗道光二十六年秋', '1846．秋', '夜，大风，有赤光自北而南，声隆隆如雷霆，流星散陨如雨，地大震。清光绪江苏');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清宣宗道光二十八年四月', '1848.5.3-31', '大姚仁和乡东南有炮声，空中落炮石三，农人捡存诸葛庙。清光绪');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清宣宗道光二十九年三月癸酉', '1849.3.28', '地震有声，星陨于迁安白塔寺山东，其黑如磐。清光绪河北');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清宣宗道光二十九年三月', '1849.3.24-4.22', '星陨于白塔寺山东，其黑如磐。清同治河北');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清宣宗道光三十年六月', '1850.7.9-8.7', '天大雷，陨石二于州之许家窪。清宣统山东');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清宣宗道光三十年九月十三日', '1850.10.17', '亥刻，思茅东南隅黑云中响震如鼓，顷刻止，复响，忽然黑云开，有径尺大红星一，随以小红星十数点，陨流于西北，距厅城五里许，乃党村何姓家，其妇方纺棉，星即坠于纺车旁入土，穿屋入土，成石，外黑亮色，锤破之，内银矿色。清道光云南');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清文宗咸丰元年五月十六日', '1851.6.15', '未刻，天上隐隐似雷声，顷间坠一石于城东北三十里昌阳坝长冲田内，水跃起丈余，突起青烟一股腾空而去。居民竭水掘石，其色青黑，状如犬首，坚似生铁，重九斤余两。清同治四川');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清文宗咸丰元年九月二十五日', '1851.11.17', '申时，天鼓鸣如雷，箕山有陨星三，色如黑石。清宣统山东');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清文宗咸丰四年十月丁亥', '1854.11', '陨石于灌溪，大如斗，色黑。清同治湖南');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清文宗咸丰六年四月', '1856.5.4-6.2', '江里莲子坝空鸣若雷，落物如车轮，色似卵黄，腥不可近。清光绪贵州');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清文宗咸丰七年五月初四日', '1857.5.26', '午刻，石陨于金鸡场等处，天鼓鸣。石陨时金光闪烁，飒然有声，落地入土尺许，力田辈取之，黑色，有硫磺气，碎之，其中色白，与他石无异。清光绪四川');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清文宗咸丰七年', '1857', '四十一都天雨石块。清同治江西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清文宗咸丰九年六月', '1859.6.30-7.29', '城南石佛方陨石数块，大如升。清同治山东');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清文宗咸丰九年八月初旬', '1859.8.28-9.6', '二更后，空中陡涌白气，长有丈余，自西转东，枪炮之声联络不绝，稍顷，状如熔锡坠落有数百点。清同治江西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清文宗咸丰九年九月二十九日', '1859.10.24', '申刻，又有天炮一声，坠落两个，其色如石，青色，重有六、七斤，在于蛟潭地方。民国福建');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清文宗咸丰十一年三月', '1861.4.10-5.9', '宿迁沭河两岸陨石数十，声如雷。清同治江苏');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清文宗咸丰十一年五月初八日', '1861.6.15', '酉刻，天大黑，西南方瞥见大星一，青紫色，状若蛇，群妖星随之，堕南禅寺寨下嘉陵江中，江水乱沸。民国四川');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清文宗咸丰十一年五月十七日', '1861.6.24', '午刻，无云而雷，陨石于六总一里李家庄，计数五，其大盈握，色黝然黑。初落时每夜有光。一献于江北大营乔，一献于知州胡，其三落于河。清光绪江苏');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清文宗咸丰十一年七月三十日', '1861.9.4', '光化陨星三，化为石。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清文宗咸丰十一年九月十五日', '1861.10.18', '夜，星斗时天上忽裂一路，逾刻始合。有一星坠落上东那窑岭，至地吐焰，亦逾刻始灭。民国广西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清文宗咸丰十一年十二月', '1861.12.31-1862.1.29', '北乡大木口月夜有物自天而下，色白，状如牛而大，坠田中激水高丈许，趋视之，陷一穴深不可测。清光绪湖南');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清文宗咸丰十一年', '1861', '有星陨于永安镇之西，质如石，色深黄，灼灼有光。清光绪江苏');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清穆宗同治元年七月十五日', '1862.8.10', '星陨及地，化为石，清光绪江西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清穆宗同治元年秋', '1862.秋', '日方午，有大星坠于西乡雷家冲田中，大如斗而圆，色白，其声匉訇，久之化为水。清光绪湖南');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清穆宗同治三年五月朔', '1864.6.4', '夜二鼓，有大星赤如血丸，坠于河州城北乡孟家沟，碰地火裂，半时方熄。清宣统');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清穆宗同治五年二月初十日', '1866.3.26', '夜，有大星赤如火，长数丈，自东坠于西南，声如雷。后闻落于颍上，成石九颗，夜有光。清同治安徽');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清穆宗同治五年三月十七日', '1866.5.1', '申刻，天微雨，忽天震如雷，匉訇不绝，仰视空际，有物如群鸟下坠，落于四城外，入地深丈余，当即持锸往掘，得其最小者数枚，质坚凝，重如金铁，灰黑色，凿破，内有金星闪烁，始悟是天所陨之石也。其大者则愈掘而入地愈深，渐至莫识其处，约共数十枚，今县署犹藏其一。清同治安徽');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清穆宗同治五年四月', '1866.5.14-6.12', '石陨于小南门外。是日午时，石将落，天晦黑大风，有声自西北来，霹雳如连珠炮，居民震恐，顷刻天日复明。清同治安徽');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清穆宗同治七年三月三日', '1868.3.26', '戌时，有星大如斗，赤色，陨于西南，其声如雷。后数日闻有石陨于太谷城西门外。清光绪山西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清穆宗同治八年六月二十二日', '1869.7.30', '南乡楼底陨赤石数块，形如三角。清光绪陕西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清穆宗同治九年三月', '1870.4.1-30', '七都岩前夜间空际有光坠地，旋转半时。次晨视之，坑深二尺余。清同治湖南');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清穆宗同治十年四月十九日', '1871.6.6', '子时，有星陨于南关外，火星自西北而东南，光芒如炬矢，激霆震，落吏书李逢春家榆树下，久之焰熄热灭，粗如礓石，圆平各半，围阔一尺五寸，中厚二寸七分，边厚一寸三分，重六斤五两，匦解制府留存。民国河南');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清穆宗同治十二年正月望日', '1873.2.12', '辰刻，梁家滩有石大如箕，轰轰然，自东北来，陨未至地，触树而碎。清光绪湖北');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清穆宗同治十二年六月十四日', '1873.7.8', '漳县马成龙川有巨声三作，闻数十里，空中坠石三块，高可四尺五寸。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清穆宗同治十二年秋', '1873．秋', '高沙团陨石数处，色黑如铁，探之炙手，有重至数十斤者。清光绪湖北');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清穆宗同治十二年十月', '1873.11.20-12.19', '罗田陨石，触地而碎。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪元年三月', '1875.4.6-5.4', '沿河雨金。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪四年九月初五日', '1878.9.30', '瓮里中槽忽有三尖形石一块从天坠落，空中如鼓乐声，经时始散。其石为居民罗五所得，人言沸腾，县令闻之，搜入衙内，后此石不知所终。民国贵州');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪四年十月十九日', '1878.11.13', '晡，有火球一，大如瓮，自大明山南飞过山北龙母岜荒村，有老松一株被其灼槁。清宣统广西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪五年十月', '1879.11.14-12.12', '黄乡雨石，大如斗，小如拳，损民舍甚多，石黑色如矿，有硫磺气。清光绪江西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪六年夏', '1880．夏', '有石二陨于化州北之莞塘，时兼雨雹，声挟风雷，坠墟田中，水喷如淬铁，遂成一潭。有农夫得其一，初热而软，少顷冻硬，形如杯珓，重约十余斤，宝而藏之。其家适有官讼，以为不祥，遂弃于野。清光绪广东');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪五、六年间', '1879-1880', '有石陨于莞塘二，形如杯珓，约重十余斤。时兼雨雹，声挟风雷，坠于垃田水泥中，遂成一潭。人以为宝，争什袭藏之，而拾得之家适有讼，又以为怪，弃之，今石不知所在。清光绪广东 ');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪六年', '1880', '五十一都陨石。民国江西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪十年六月', '1884.7.22-8.20', '邑东南朱庄陨星，化为石，色黑，大如斗。民国河南');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪十二年仲冬之月六日', '1886.12.1', '忽于杲杲赤日中闻霹雳一声，余不以为异。越数日朱受之夫子来云，是日有物堕于平望镇某家，其色白，长四尺，而形如缸，入于内宅庭中。又有白光一道，乃冉冉腾空而上，不知何物。或曰天狗下地，其声如雷，堕者盖天狗也。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪十二年冬', '1886．冬', '郧阳星陨，化为石。清宣统');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪十三年三月八日', '1887.4.1', '一区观音山忽由空中飞来一石，百人方能舁入寺内，名日飞来石，并未损伤房屋。民国云南');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪二十年正月二十二日', '1894.2.27', '皋兰陨星如火球，土人识其处，掘之，得一铁卵。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪二十年七月初十日', '1894.8.10', '陨石于城东齐庄。民国山东');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪二十一年五月初', '1895.5.24-28', '黄昏后，陨星于城南安堤。次早，村民寻之得石，作棱角形，其色黝灰，其质不甚坚凝。民国山东');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪二十四年五月二十六日', '1898.7.14', '天空有声如铜鼓，陨石于车门里。民国江苏');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪二十四年七月', '1898.8.17-9.15', '有星陨于姜堰陆姓家，光夺目，落地成石，多角形。民国江苏');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪二十八年三月某日', '1902.4.8-5.7', '陨石于荔川，四，陨星也。清光绪甘肃');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪二十八年十月十六日', '1902.11.15', '午后，有声如雷，红光下坠平乡店寨壕内。出视之，石也，淡青色，有金星。清宣统河南');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪三十年春', '1904．春', '卫滩白昼陨石粒如雨。民国江苏');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪三十一年七月', '1905.8.1-29', '雨铁米。民国福建');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪三十一年八月十五日', '1905.9.13', '未刻，云保偳子营星陨。初闻天空霹雳，有电光一道，自高处直坠于地，入土深三尺余，訇然有声，土人奔至其处，掘得一物，形似火鍊石，紫黑色，状类犬头，陨于偳子营东南里许，石今尚存。民国河南');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪三十一年秋', '1905．秋', '沈家滩雨石，色翳黑，沈家滩人多见之。民国江苏');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪三十三年七月二十八日', '1907.9.5', '瓮里新牌卫石陨，万登奎全家压死。民国贵州');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清末帝宣统元年三月初一日', '1909.4.20', '未时，有流星如火箭，自西北陨于东南，落大乘山西，形如狗头，似煤有气。民国河南');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清末帝宣统二年五月十三日', '1910.6.19', '申刻，天日晴朗，星陨为石，作霹雳声。民国四川');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清末帝宣统三年正月十九日', '1911.1.18', '巳时，白气氤氲，自西南来一物如球，坠于城东十里铺之野，噌有声吰，入地后气复上冲云际，二牧童掘之三尺许，始得其物，形如狗头，色黑，质似铁石。……现此物存学堂。民国河南');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清末帝宣统三年春', '1911．春', '见西南陨石如雨。民国');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清末帝宣统三年九月初十日', '1911.10.31', '横山乡忽闻西南有声如雷，旋见波烟一股天半飞来，陨下一石，坠于龙潭沟贾姓田中，形如土砖，色如火石。民国四川');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('春秋战国宋景公时', '前516-前469', '陨石于铸三，宋景公问于刑吏子臣日：“陨石于铸三，何也？”刑吏子臣答曰：“天之望山将崩”。（北堂书钞一百六十）');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('鸱夷二年', '前472', '国中雨石，大者方圆丈余，小者亦大于拳，雨及数里，不伤人屋，后亦无他。至今葑门内里许有大石，是其遗迹也。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('汉武帝前', '约前140', '星坠至地，则石也。河、济之间时有坠星。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('汉顺帝汉安二年', '143', '有星陨于诸暨县东北二十里，化为石。明万历浙江');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('唐太宗时', '627-649', '汾州言青龙白龙吐物在空中，有光如火，坠地陷入二尺，掘之得玄金，广尺余，高七丈。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('宋真宗在储贰时', '995-997', '忽一日因乘马出至朱雀门外，方辰时有大星落于马前，迸裂有声。真宗回东京惊恐时召司天监明天文者询之……。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('宋仁宗景祐初', '1034-1036', '忻州夜中星陨极多，明日视之皆石。闻今忻民犹有蓄之者。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('宋神宗熙宁甲寅（七年）', '1074', '陨石于安吉落石山李卫公祠之东。清乾隆浙江');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('宋神宗元丰中', '1078-1085', '世人有得雷斧、雷楔者云：雷神所坠，多于震雷之下得之。而未尝得亲见。元丰中予居随州，夏月大雷一木折，其下乃得一楔，信如所传。凡雷斧多以铜铁为之；楔乃石耳，似斧而无孔。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('宋哲宗元符二年', '1099', '县东白鹭坑陨石，如雷，形如罗汉。清康熙江西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('宋徽宗政和二年三月初四日', '1112.4.2', '陨石二于落石山李卫公庙。（落石壁间记）清康熙浙江');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('宋高宗绍兴二十九年十月', '1159.11.12-12.11', '有星自西南陨芜湖东柳家山，有声如雷，其光烛天，民走视之，一石高二尺，青黑如铁。邑令赵不吝恐惑众，命投之于水。清康熙安徽');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('宋光宗绍熙三年正月', '1192.1.17-2.14', '陨石于象山之麓。清乾隆湖北');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('宋宁宗嘉定元年五月甲子', '1208.7.10', '安福荆山民早出，忽一星坠于前，亟掘地得之，光彩灿灿，毁为数十片亦如之．清康照江西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('宋宁宗嘉定元年五月', '1208.6.15-7.14', '荆山民早出，见一星坠前，亟掘地得之，光彩灿灿，毁为数十片亦然。清同治江西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('宋宁宗嘉定十二年', '1219', '落石山……下有李卫公庙。宋嘉定己卯陨石于右庑下。清乾隆浙江');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('元泰定帝泰定二年三月己巳', '1325.5.2', '皇元泰定年岁在乙丑，暮春之初，其日己巳，风雨甚，雷轰电掣，沙扬砾飞，砰礧砢直下数十百仞，竟止衍庆右偏，栋宇像设一无所损，石舁而寘之者（元·范天辅《唐李卫公庙落石记》。清康熙浙江');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('元泰定帝泰定间', '1324-1328', '有大星陨于?嘉县之虚初山，化为黑石，状如瓜，上有点如星，击之铿然有声。人不言举之则动，言则弗动。明天启云南');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('元末甲午、乙未间', '1354-1355', '河北山东多陨石，大或屋，陷深入地丈余。洛阳有大星陨地，滚行数十丈，草木皆焦。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('元顺帝至正十六年', '1356', '迈里古思以绍兴录事司官掌总督越兵，为御史拜住所杀，……未死前三日有星大于杯盂，坠镇越门，化为石。明万历浙江');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('元顺帝至正十六年冬', '1356．冬', '有星陨，如新断星石。清雍正江苏');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明太祖洪武间', '1368-1398', '指挥徐呆厮出兵河套，地名梧桐树，一日午间，有大星坠于河中，火发延及岸上，营中军人有被伤者。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明宪宗成化十七年八月甲辰', '1481.8.26', '玉馨山有石陨于李卫公庙后殿，殿桷不损，而损桷下之楣，怪哉，何其神也（明·施善《续落石记》）。清康熙浙江');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明宪宗成化中', '1465-1487', '星陨于山东莒城县马长史家门中，初堕地其光煜煜，而星体腐软特如粉浆，马家人以杖抵之，没杖成穴，久而渐坚，乃作一石。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明孝宗弘治十六年之前数年', '1503前数年', '有星如轮，陨于山东禹城县，结石数块。明万历');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明世宗嘉靖六年正月', '1527.2.1-3.1', '庆阳府雨石，大如卵，能作人言，一人携之，惊，急掷。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明世宗嘉靖二十一年正月初一日', '1542.1.16', '辰时，有雷，峒佛子岭忽坠一石，约六尺大，震声如雷，下抵岭脚复逆转上田凡四十余步。清康熙广东');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明世宗嘉靖二十一年四月丁卯（十七日）', '1542.5.1', '全州风雨，有飞石陨柳山应泉池。清嘉庆');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('世宗嘉靖二十五年明七月', '1546.7.27-8.25', '全州有石乘风雨雷电飞入应泉井中，状如龙马。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明世宗嘉靖间', '1522-1566', '昨友俞御医桥早期，偶尔星陨身侧，铿然有声，在地尚响，视如珠砂小石，惜人人共观而不及取也。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明穆宗隆庆元年十二月', '1567.12.30-1568.1.28', '诸暨鸡冠山石堕，大如巨屋，至地震为池，复跃过溪乃止。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历二年八月', '1574.8.16-9.14', '昼瞑，空中有声如雷，地大震，侯官县方山巨石坠压田。清乾隆');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历三十一年二月二十三日', '1603.4.4', '夜，太仓草场空中火发，自上而下，焚草六万余束，既熄，视所焚者皆成巨石，大数十围，堆迭如山，斧击碎视之，真石也。问守者，云：仓中从来无石，皆积年烂草耳！不知其故。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历间', '1573-1619', '落星池在县西南二里紫国庙中。万历间，夜有大星出斗虚，自北飞坠于邑西南城隅，轰然有声，其质似石，体巨力重，入地坎深数尺，灵泉上涌，石质渐消，遂成一池，紫光熊熊，常腾水面。里人于池左立庙，以祀紫微。（魏鼎：《星池异迹记》）清光绪浙江');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清世祖顺治五年春', '1648．春', '大楼山石陨，有声如雷。清康熙安徽');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清世祖顺治五年六月', '1648.7.20-8.18', '贵池陨石。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清世祖顺治七年秋', '1650．秋', '夜，有大星落仙姑山堡垛上，近之火袭衣裙，以手指，指上皆火不热，数夜方散。清顺治甘肃');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙元年壬寅七夕', '1662.8.20', '荷山岭上有石五块，巉岩臃肿，自西南飞来，驻庵前菜畦中。清道光安徽');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙二十九年', '1690', '复陨一石于落石山李卫公庙。清乾隆浙江');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清仁宗嘉庆初年', '1796-1800', '夜间，有星如火，陨于任进士震远宅，磌然有声，光熊熊不可逼视，久之渐黯。明晨起视，见一石黝黑如铁，此石与宋治平中?亭许氏园事相似。而此石至今尚存。清光绪江苏');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清仁宗嘉庆十七年四月十七日', '1812.5.27', '辰巳刻，城东顺流下三十里有龙泉湾，有云如虹，微露头角，自天冉冉而降，将落时长丈余，铿然有声，遥见江水四分，倏忽腾空，沛然大雨。清同治四川');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清仁宗嘉庆中', '1796-1820', '又落一石于落石山李卫公庙钟旁。清同治浙江');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清仁宗嘉庆年间冬', '1796-1820．冬', '回民刘某以磨面为业。嘉庆年间冬夜忽见一星陨于磨房之侧，烛之，石也，高二尺许，下丰上锐，略似人踞坐者然，右一穴微圆而曲，从肩到趾。刘弃之屋角，群小儿每抛石击之以为戏，久之渐失本象，而刘家产亦渐凋零，谓是是石之所致，遂送铁塔寺中供之。……按此石质坚凝，色尤苍润，重二百五十斤，高二尺。近年为兵士捣毁，不复存焉。（清知县侯昌镇《落石记》）清咸丰四川');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清宣宗道光初', '1821-1825', '苏属离里镇，夜有一星堕蒯惺伯少府家，大如方桌，著地有声，火星四射，家人辈以竿触之，软如绵。至晓光敛，坚凝如石矣。竿触处一孔可贯绳索。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清宣宗道光二十二年秋', '1842．秋', '五弩出陨石，声如雷。清光绪山东');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清宣宗道光二十四年十月二十二日', '1844.12.1', '陨石大小十一，皆在安吉落石山李卫公庙后，一坏寝殿墙，一坏庙北偏屋。清同治浙江');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清穆宗同治三年七月某日', '1864.8.2-31', '黄昏时候有大星光芒如月，从西北方入东南方，有小星三随之而行，经过处陷如坑，一时许乃灭。民国福建');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清穆宗同治四年五月二十七日', '1865.6.20', '石骨墟石狗岭坠下大石一片，声闻数里。清光绪广东');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清穆宗同治十三年五月十七日', '1874.6.30', '夜，明东里金桂山雷雨大作，悬空堕一巨石，压覆草屋半头，毙一小孩，随见一物目光如炬，直下大溪，淹田禾甚夥，出龙洞沱大江去。清光绪四川');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪二十一年七月初三日', '1895.8.22', '县西南乡阎家坪村前河中，霹雳一声竖起大石一块，厚二、三尺，高一丈五、六尺。民国陕西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪某年七月某日', '1875-1904', '午后空中有声如连珠枪发，忽一星陨于淮北马家庄，其大如盂，石质粗糙，碎之中多黑斑。民国江苏');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('夏仲康五年秋九月庚戌朔', '约前二十世纪', '日有食之。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('周幽王六年十月辛卯朔', '前776.9.6', '十月之交，朔日辛卯，日有食之，亦孔之丑。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('周平王五十一年二月乙巳', '前720.1.29', '日有食之。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('鲁隐公三年二月己巳', '前720', '日有食之。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('鲁隐公三年二月', '前720.2.22-3.23', '日蚀。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('鲁桓公三年七月壬辰朔', '前709.7.17', '日有食之。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('鲁桓公十七年十月朔', '前695.10.10', '日有食之。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('鲁桓公十七年', '前695', '日食，不书日，官失之。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('鲁庄公十八年三月', '前676.3.17-4.15', '日有食之。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('鲁庄公二十五年六月辛未朔', '前669.5.27', '日有食之。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('鲁庄公二十五年唯正月朔', '前669', '日有食之。于是乎用幣于社。伐鼓于朝。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('鲁庄公二十六年十二月癸亥朔', '前668.11.10', '日有食之。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('鲁庄公三十年九月庚午朔', '前664.8.28', '日有食之。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('鲁僖公五年九月戊申朔', '前655.8.19', '日有食之。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('鲁僖公十二年春王正月庚午', '前648', '日有食之。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('鲁僖公十二年三月庚午', '前648.4.6', '日有食之。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('鲁僖公十五年五月', '前645.5.2-31', '日有食之。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('鲁文公元年二月癸亥', '前626.2.3', '日有食之。');
