INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('后唐明宗天成元年三月', '926三月', '恶星入天库。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('宋太祖建隆二年十二月己酉', '962.1.28', '（客星）出天市垣宗人星东，微有芒彗。三年正月辛未，西南行入氐宿，二月癸丑至七星没。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('宋太祖开宝四年八月辛卯', '971八月辛卯', '景星见。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('宋太祖开宝四年八月癸卯', '971八月癸卯', '景星见。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('宋太宗太平兴国八年二月甲辰', '983.4.3', '（客星）出太微垣端门东，近屏星北行。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('宋太宗端拱二年七月丁亥', '989.8.12', '（客星）出北河星西北，稍暗，微有芒彗，指西南。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('宋太宗淳化元年正月辛巳', '990.2.2', '（客星）出轸宿，逆至张，七十日，经四十度乃不见。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('宋真宗景德二年八月甲辰', '1005.10.4', '（客星）出紫微天樯侧，孛孛然如粉絮，稍入垣内，历御女、华盖，凡十一日没。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('宋真宗景德三年三月乙巳', '1006.4.3', '（客星）出东南。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('宋真宗景德三年四月二日', '1006.5.1', '五月一日司天监言：先四月二日夜初更，见大星，色黄，出库楼东、骑官西，渐渐光明，测在氐三度。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('宋真宗景德三年四月戊寅', '1006.5.6', '周伯星见，出氐南骑官西一度，状如半月，有芒角，煌煌然可以鉴物，历库楼东。八月，随天轮入浊。十一月复见在氐。自是，常以十一月辰见东方，八月西南入浊。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('宋真宗景德三年五月壬寅朔', '1006.5.30', '周伯星见。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('宋真宗景德三年十一月壬寅', '景德三年十一月壬寅', '周伯星再见。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('宋真宗景德三年', '1006', '有巨星见于天氐之西，光芒如金圆，无有识者。春官正周克明言：按天文录荆州占，其星名周伯。语曰：其色金黄，其光煌煌。又按元俞苞此星一曰德星，不其时而出。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('宋真宗大中祥符四年正月丁丑', '1011.2.8', '（客星）见南斗魁前。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('宋真宗大中祥符七年正月己酉', '1014正月己酉', '含誉星见。九月丙戌，又见，似彗有尾而不长。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('宋真宗大中祥符九年四月庚辰', '1016.5.16', '司天监言：周伯星再见。景德三年五月朔日事当参考。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('宋真宗大中祥符九年四月庚辰', '1016.5.16', '周伯星见。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('宋真宗天禧五年四月丙辰', '1021.5.25', '客星出轩辕。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('宋仁宗天圣元年二月己亥', '1023二月己亥', '奇星见。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('宋仁宗天圣二年八月丙子', '1024八月丙子', '（奇星）又见。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('宋仁宗天圣四年七月壬申', '1026七月壬申', '（奇星）又见。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('宋仁宗明道元年六月乙巳', '1032.7.15', '客星出东北方，近浊，有芒彗，至丁巳，凡十三日没。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('宋仁宗明道二年二月戊戌', '1033.8.5', '含誉星见东北方。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('宋仁宗至和元年', '1054', '日食正阳，客星出于昴。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('宋仁宗至和元年五月己丑', '1054.7.4', '客星出天关东南，可数寸，岁余稍没。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('宋仁宗至和元年五月', '1054.6.9-7.7', '客星出于昴。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('宋仁宗至和元年七月二十二日', '1054.8.27', '守将作监致仕杨惟德言：伏睹客星出见，其星上微有光彩，黄色。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('辽兴宗重熙二十三年八月', '1054.9.5-10.4', '客星出于昴。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('宋仁宗嘉祜元年三月辛未', '1056.4.6', '司天监言：自至和元年五月，客星晨出东方守天关，至是没。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('宋仁宗嘉祜元年三月', '1056.3.19-4.17', '司天监言：客星没，客去之兆也。初至和元年五月晨出东方，守天关。昼见如太白，芒角四出，色赤白，凡见二十三日。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('辽道宗咸雍元年八月丙申', '1065.9.11', '客星犯天庙。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('宋神宗熙宁二年六月丙辰', '1069.7.12', '客星出箕度中，至七月丁卯，犯箕乃散。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('宋神宗熙宁三年十一月丁未', '1070.12.25', '客星出天囷。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('宋高宗绍兴十五年五月丙辰', '1145.6.3', '客星见。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('宋孝宗淳熙二年七月辛丑', '1175.8.10', '有星孛于西北方，当紫微垣外七公之上，小如荧惑，森然蓬孛，至丙午始消。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('宋孝宗淳熙二年七月二十三日', '1175.8.11', '夜，孛星见于西方，二十七日夜消伏。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('辽道宗咸雍二十三年六月', '1087.7.3-8.1', '有星如瓜，出文昌。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('宋哲宗元祜六年十一月辛亥', '1092.1.8', '（客星）出参度中，犯掩厕星，壬子，犯九游星，十二月癸酉入奎，至七年三月辛亥乃散。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('宋高宗绍兴八年五月', '1138.6.9-7.8', '（客星）守娄，鲁分也。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('宋高宗绍兴九年二月壬申', '1139.3.23', '（客星）守亢，陈分也。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('宋高宗绍兴十五年五月丙辰', '1145五月丙辰', '客星见。六月丁亥，客星没。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('宋高宗绍兴十五年五月丁巳', '1145五月丁巳', '化为客星，其色青白，壬戌，留守张，至六月丁亥乃消。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('宋孝宗乾道二年三月癸酉', '1166.5.1', '（客星）出太微垣内五帝座大星西，微小，色青白。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('宋孝宗淳熙八年六月己巳', '1181.8.6', '（客星）出奎宿，犯传舍星，至明年正月癸酉，凡一百八十五日始灭。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('金世宗大定二十一年六月甲戌', '1181.8.11', '客星见于华盖，凡百五十有六日灭。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('宋宁宗嘉泰三年六月乙卯', '1203.7.28', '（客星）出东南尾宿间，色青白，大如填星，甲子，守尾。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('金卫绍王大安二年二月', '1210.2.26-3.26', '客星入紫薇垣，光散为赤龙。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('宋宁宗嘉定十七年六月己丑', '1224.7.11', '（客星）守犯尾宿。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('宋理宗绍定三年十一月丁酉', '1230.12.15', '有星孛于天市垣。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('宋理宗嘉熙四年七月庚寅', '1240.8.17', '（客星）出尾宿。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('宋理宗淳祐四年四月丙子', '1244.5.14', '有星出尾宿，大如太白。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('宋理宗淳祐八年', '1248', '星出河鼓，大如太白。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('元世祖至元十年三月癸酉', '1273.4.9', '客星青白如粉絮，起毕，度五车北，复自文昌贯斗杓，历梗河，至左摄提，凡二十一日。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('元成宗大德元年八月丁巳', '1297.9.14', '袄星出奎。九月辛酉朔，袄星复犯奎。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('元仁宗延祐二年冬十月丙子朔', '1315十月丙子', '客星见太微垣。十一月丙午，客星变为彗，犯紫微垣，历轸至壁十五宿，明年二月庚寅乃灭。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明太祖洪武九年六月戊子', '1376六月戊子', '有星大如弹丸，白色。止天仓，经外屏、卷舌，入紫微垣，扫文昌，指内厨，入于张。七月乙亥灭。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明太祖洪武十一年九月甲戌', '1378九月甲戌', '有星见於五车东北，发芒丈余。扫内堦，入紫薇宫，扫北极五星，犯东垣少宰，入天市垣，犯天市。至十月己未，阴云不见。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明太祖洪武十八年九月戊寅', '1385九月戊寅', '有星见太微垣，犯右执法，出端门。乙酉，入翼，彗长丈余。至十月庚寅，犯军门，彗扫天庙。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明太祖洪武二十一年二月丙寅', '1388二月丙寅', '有星出东壁。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明太宗永乐二年十月庚辰', '1404.11.14', '辇道东南有星如盏，黄色，光润而不行。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明太宗永乐六年八月丙申', '1408.9.10', '夜，有星大如盏，色青白有光，出西方。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明太宗永乐六年十月庚辰', '1408.10.24', '夜，中天辇道东南有星如盛（盏），黄色光阔（润），出而不行，盖周伯德星云。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明太宗永乐二十二年九月戊戌', '1424九月戊戌', '有星见斗宿，大如盌，色黄白，光烛地，有声如撒砂石。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明宣宗宣德五年八月甲申', '1430.9.3', '夜，客星见南河东北尺余，色青黑。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明宣宗宣德五年八月庚寅', '1430.9.9', '夜，客星现南河旁，如弹丸大，色青黑，凡二十有六日灭。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明宣宗宣德五年十月丙申', '1430十月丙申', '蓬星见外屏南，东南行，经天仓、天庾，八日而灭。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明宣宗宣德五年十二月丁亥', '1431.1.4', '有星如弹丸，见九斿旁，黄白光润，旬有五日而隐。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明宣宗宣德五年闰十二月戊戌', '1431.1.15', '钦天监奏：含誉星见九牛星傍，如弹丸大，今侯至十夕，其色愈黄白光润。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明宣宗宣德六年三月壬午', '1431.4.29', '又见（星如弹丸，见九斿旁，黄白光润）。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明宣宗宣德八年秋七月戊午', '1433七月戊午', '景星见。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明宣宗宣德八年闰八月戊午', '1433闰八月戊午', '景星三，见西北方天门，青赤黄各一，大如盌，明朗清润，良久聚半月形。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明代宗景泰三年十一月癸未', '景泰三年十一月癸未', '有星见鬼宿积尸气旁，徐徐西行。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明英宗天顺二年十一月癸卯', '天顺二年十一月癸卯', '有星见於星宿，色白，西行，至丙午，其体微，状如粉絮，在轩辕旁。庚戌，生芒五寸，犯爟位西北星，至十二月壬戌，没于东井。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明英宗天顺五年六月壬辰', '1461六月壬辰', '天市垣宗正旁，有星粉白，至乙未，化为白气而消。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明英宗天顺六年六月丙寅', '1462六月丙寅', '有星见策星旁，色苍白，入紫薇垣，犯天牢，至癸未，居中台下，形渐微。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明孝宗弘治三年十二月丁巳', '弘治三年十二月丁巳', '有星见天市垣，东南行。戊辰，见天仓下，渐向壁。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明孝宗弘治七年十二月丙寅', '弘治七年十二月丙寅', '有星见天江旁，徐行近斗，至八年正月庚戌，入危。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明孝宗弘治十年八月癸巳', '1497.9.20', '昏刻，南京客星见天厩星旁。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明孝宗弘治十二年七月戊辰', '1499七月戊辰', '有星见天市垣宗星旁，入紫薇垣东藩，经少宰、尚书，抵太子后宫，出西藩少辅旁，至八月己丑灭。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明孝宗弘治十五年十月戊辰', '1502十月戊辰', '有星见天庙旁，自张抵翼，复退至张，戊寅灭。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明武宗正德十六年正月甲寅朔', '1521正月甲寅朔', '东南有星入火，变白，长可六七尺，横互东西，复变勾屈状，良久乃散。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明世宗嘉靖十一年二月壬午', '1532二月壬午', '有星见东南，色苍白，有芒，积十九日灭。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明世宗嘉靖十三年五月丁卯朔', '1534五月丁卯朔', '有星见腾蛇，历天厩入阁道，二十四日灭。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明世宗嘉靖十五年三月戊午', '1536三月戊午', '有星见天棓旁，东行历天厨，西入天汉，至四月壬辰没。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明世宗嘉靖二十四年十一月壬午', '嘉靖二十四年十一月壬午', '有星出天棓，入箕，转东北行，逾月没。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明穆宗隆庆六年十月初三', '1572.11.8', '夜，客星见东北方，如弹丸，出阔道旁壁宿度，渐微芒有光，历十九日，壬申夜，其星赤黄色，大如盏，光芒四出。占曰：是为孛星。日未入时见，占曰：亦为昼见。……十二月甲戌礼部题奏：十月以来客星当日而见，光映异常。按是星历万历元年二月，光始渐微。至二年四月，乃没。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明穆宗隆庆六年十月', '1572.11.6-12.4', '每夜半后，有妖星见东北方，光耀如月。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明穆宗隆庆六年冬', '1572．冬', '妖星见。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历六年正月戊辰', '1578正月戊辰', '有星大如日，出自西方，众星皆西环。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历十二年六月己酉', '1584.7.11', '有星出房。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历二十年七月', '1592.8.7-9.5', '景星见于九斿。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历三十二年九月乙丑', '1604.10.10', '夜，西南方生异星，大如弹，体赤黄色，名曰客星。十二月辛酉，夜，客星随天转见东南方，大如弹片，黄色，光芒微小，在尾宿。三十三年八月丁卯夜，客星不见。自三十二年九月客星见尾分，一更时出西南方，随天西转。至十月夕伏不见。十一月五更时出东南方。今年二月，其光渐暗，至是乃灭。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历三十七年', '1609', '有大星见西南，芒刺四射。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历四十六年九月乙卯', '1618九月乙卯', '东南有白气一道，阔尺余，长二丈余，东j至轸，西入翼，十九日而灭。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明熹宗天启元年四月癸酉', '1621.5.22', '赤星见于东南方。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明熹宗天启七年十二月朔', '1628.1.7', '景星见。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明毅宗崇祯四年三月', '1631.4.2-30', '太微垣有星大如月，磨荡不定。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明毅宗崇祯八年', '1635', '西北隅有大星如月，未及申时，即出光芒紫赤，侵入井度。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清世祖顺治九年十一月庚寅', '1652十一月庚寅', '异星苍白气见於参，西北行入毕。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙三年十月己未朔', '1664十月己未朔', '有星茀於轸，见东方；丁卯，尾长七八寸，苍色，指西南；丁亥，尾长三尺余，指西北，逆行至翼；十一月戊戌，尾长五尺余，指北方，至张；庚子，至井；癸卯，往西北行至昴；乙巳，尾指东北，至胃；庚戌，至娄，尾指东，青色；十二月壬戌，至奎，体小，尾长八尺余。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙四年二月己巳', '1665二月己巳', '东南方有异星见於女；甲戌，尾长七寸，指西南，苍白色；丁丑，尾长尺余，往东北顺行至虚；辛巳，至室，体渐大，尾长八尺余；乙酉，至壁，尾长五尺余。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙七年正月甲子', '1668正月甲子', '西南白光，长六尺余，尾指东南，占曰天枪；二月乙亥，渐长至四丈余，尾扫天苑、九斿、军井；丁亥，没。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙十二年二月癸巳', '1673二月癸巳', '异星见於娄，大如核桃，色白，尾长尺余，指东方；甲午，仍见。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙十五年正月戊子', '1676.2.18', '异星见于天苑东北，色白。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康煦十六年三月癸卯', '1677.4.28', '东北方有异星见于娄，体色光明润泽，尾长尺余，指西南，占曰含誉。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙十六年三月甲辰', '1677三月甲辰', '含誉星见。九月丙戌，又见，似彗有尾而不长。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙二十二年闰六月庚戌', '1683闰六月庚戌', '异星见於五车北八毂东，色白，往西南逆行；戊辰，入五车。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙二十五年七月庚寅', '1686七月庚寅', '异星见东方，近地平，色白，东行不急；丁酉，凡行十六度，至柳，微有尾迹；壬寅，至星，渐没。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙二十七年十月己酉', '1688.11.2', '异星见奎，色白，凡三夜。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙二十九年八月乙酉', '1690.9.29', '异星见箕，色黄，凡二夜。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清世宗雍正元年九月己丑', '1723九月己丑', '异星见弧矢下，色白，体微，芒长尺余，指西北，逆行至井。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清高宗乾隆二年六月丁卯', '1737六月丁卯', '异星出右更东，色白，属娄，向西南行；丙子，仍见。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清高宗乾隆七年正月丁亥', '1742正月丁亥', '异星见东南方；戊子，出地二十七度余，大如弹丸，色黄，尾长四尺余，指西南，属丑宫，在天市垣徐星外，逆行四旬余不见。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清高宗乾隆十三年三月癸丑', '乾隆十三年三月癸丑', '异星见东方，大如榛子，色黄，尾长二尺余，向西南指，在离宫第三星南，顺行至四月甲寅朔，行三度，尾长尺余，体小光微；壬戌，至腾蛇；乙丑，至王良；丙寅，不见。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清高宗乾隆十四年五月甲寅', '1749五月甲寅', '瑞星见东方，大如鸡卵，形长圆，色黄白，光莹润泽，行不急，出天津，入芻藁，占曰含誉。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清高宗乾隆十四年五月', '1749.6.15-7.13', '瑞星见，大如鸡子，其色黄白，光莹润泽。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清高宗乾隆二十四年十一月戊辰', '乾隆二十四年十一月戊辰', '异星见东南方，在井第四星下，大如榛子，色苍黄，向西北行；癸酉，行四度，在胃，微有尾迹；十二月丁丑朔，全消。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清高宗乾隆三十五年闰五月己酉', '1770闰五月己酉', '异星见东南方，在天弁第一星西，大如弹丸，色苍黄；癸丑，向北行三十二度；乙丑，不见。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清高宗乾隆五十四年六月', '1789.7.22-8.21', '景星见。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清嘉庆三年七月', '1798.8.12-9.9', '有星现于大火之次，光如皎月，众小星环之。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清文宗咸丰十一年七月下旬', '1861.8.26-9.4', '紫官内有星如镫。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清穆宗同治二年二月', '1863.3.19-4.17', '客星入毕。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清穆宗同治六年七月二十八日', '1867.8.27', '寅刻，东方出景星如小月，次辰不见。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪二年五月初二三', '1876.5.24-25', '两夜，仁怀县有星自东出，若启明，明灭不常，夜半始大明。');
