INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('梁武帝中大通五年正月己酉', '529', '长星见。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('梁武帝中大通五年十月辛丑', '529', '彗出南斗，长一尺余，东南指，渐长一丈余。十一月乙卯，至娄灭。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('北魏孝庄帝永安三年七月甲午', '530.8.29', '有彗星晨见东北方，在中台东一丈，长六尺，色正白，东北行，西南指；丁酉，距下台上星西北一尺而晨伏；庚子，夕见西北方，长尺，东南指，渐移入氐；至八月己未，渐见；癸亥，灭。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('东魏孝静帝天平二年', '535', '有星孛于太微，历下台，及室壁而灭。');
