INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('梁武帝大同五年十月辛丑', '539.11.17', '彗出南斗，长一尺余，东南指，渐长一丈余。十一月乙卯，至娄灭。');
