INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('北齐武成帝河清四年三月', '565.4.16-5.15', '彗星见。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('北齐武成帝河清四年三月戊子', '565.4.21', '彗星见。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('北周武帝保定五年六月庚申', '565.7.22', '彗星出三台，入文昌，犯上将，后经紫宫西垣入危，渐长一丈余，指室、壁。后百余日，稍短，长二尺五寸，在虚、危灭。');
