INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('北周武帝天和三年七月己未', '568.9.3', '客星见房，渐东行，入天市，犯营室，至奎，四十余日乃灭。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('北齐后主天统四年七月', '568.8.9-9.6', '七月，孛星见房心，白如粉絮，大如斗，东行。八月，入天市，渐长四丈，犯瓠瓜，历虚危，入室，犯离宫，九月，入奎，至娄而灭。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('北周武帝建德三年二月戊午', '574.4.4', '客星大如桃，青白色，出五车东南三尺所，渐东行，稍长二尺所；至四月壬辰，入文昌；丁未，入北斗魁中，后出魁，渐小。凡见九十三日。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('陈宣帝太建六年四月庚子', '574.5.16', '彗星见。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('北周武帝建德三年四月乙卯', '574.5.31', '星孛于紫宫垣外，大如拳，赤白，指五帝座，渐东南行，稍长一丈五尺；五月甲子，至上台北灭。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('北周武帝建德三年四月丁巳', '574.6.2', '有星孛于东北紫宫垣，长七尺。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('陈宣帝太建七年四月丙戌', '575.4.27', '有星孛于大角。');
