INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('隋炀帝大业三年正月丙子', '607.2.28', '长星竟天，出于东壁，二旬而止。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('隋炀帝大业三年二月己丑', '607.3.13', '彗星见于东井、文昌，历大陵、五车、北河，入太微，扫帝座，前后百余日而止。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('隋炀帝大业三年三月辛亥', '607.4.4', '长星见西方，竟天。干历奎娄、角亢而没；至九月辛未，转见南方，亦竟天，又干角亢，频扫太微帝座，于犯列宿，唯不及参、井。经岁乃灭。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('隋炀帝大业三年五月癸酉', '607.6.25', '有星孛于文昌上将，星皆动摇。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('隋炀帝大业四年九月戊寅', '608.10.22', '彗星出于五车，扫文昌，至房而灭。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('隋炀帝大业十一年六月', '615.7.2-30', '有星孛于文昌东南，长五六寸，色黑而锐，夜动摇，西北行，数日至文昌，去宫四五寸，不入，却行而灭。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('隋炀帝大业十三年六月', '617.7.9-8.6', '有星孛于太微五帝座，色黄赤，长三四尺所，数日而灭。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('隋炀帝大业十三年九月', '617.10.6-11.3', '彗星见于营室。');
