INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('唐太宗贞观八年', '634', '有星孛于虚、危，历于氐，百余日乃灭。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('唐太宗贞观十三年三月乙丑（二十二日）', '639.4.30', '有星孛于毕、昴。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('唐太宗贞观十五年六月己酉（十九日）', '641.8.1', '有星孛于太微，犯郎位。……秋七月甲戌，孛星灭。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('唐太宗贞观十五年', '641', '将东幸，行至洛阳，而彗星见，乃止。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('唐高宗龙朔三年八月癸卯', '663.9.29', '彗星见于左摄提。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('唐高宗乾封二年四月丙辰', '667.5.24', '有彗星于东北，在五车、毕、昴间，乙亥不见。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('唐高宗总章元年四月丙辰', '668.5.18', '有彗星见于毕、昴之间，……。乙亥，彗星灭。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('唐高宗总章元年四月', '668.5.17-6.14', '彗见五车。……屋虽孛而光芒小，……星孛于东北，……二十二日星灭。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('唐高宗上元二年十月壬午', '675.11.4', '星孛于角，亢之南，长五尺。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('唐高宗上元二年十月', '675.10.24-11.22', '彗见于角、亢南，长五尺。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('唐高宗上元二年十二月壬午', '676.1.3', '有彗星于角、亢南，长五尺。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('唐高宗上元三年（仪凤元年）七月二十一日（丁亥）', '676.9.4', '彗见东井，指南河、积薪，长三尺余，渐向东北，光芒益?，长三丈，扫中台，指文昌，经五十八日而灭。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('唐高宗上元三年七月', '676.8.15-9.12', '彗起东井，指北河，渐东北，长三丈，扫中台，指文昌宫，五十八日方灭。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('唐高宗永隆二年九月', '681', '是夜，彗星见。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('唐高宗永隆二年（开耀元年）九月丙申（一日）', '681.10.17', '彗星见于天市，长五尺。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('唐高宗永淳二年（弘道元年）三月丙午（十八日）', '683.4.20', '彗见五车北，二十五日而灭。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('唐则天皇后文明元年七月二十二日（辛未）', '684.9.6', '西方有彗，长丈余，凡四十九日灭。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('唐则天皇后文明元年七月', '684.8.16-9.14', '彗星见西北方，长二丈余，经三十三日乃灭。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('唐中宗景龙元年十月壬午（十八日）', '707.11.16', '彗见于西，月余而灭。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('唐中宗景龙二年七月七日（丁酉）', '708.7.28', '星孛胃、昴之间。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('唐中宗景龙三年八月壬辰（八日）', '709.9.16', '有星孛于紫宫。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('唐睿宗延和元年六月', '712.7.9-8.6', '有彗星自轩辕入太微，至大角灭。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('唐睿宗太极元年（先天元年）七月四日（辛未）', '712.8.10', '彗入太微。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('唐玄宗开元十八年六月甲子（十一日）', '730.6.30', '彗星见于五车。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('唐玄宗开元十八年六月癸酉（三十日）', '730.7.9', '有星孛于毕、昴。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('唐玄宗开元二十六年三月丙子（八日）', '738.4.1', '有星孛于紫微垣中，历斗魁十余日，因阴云不见。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('唐肃宗乾元三年（上元元年）四月丁巳（二十七日）', '760.5.16', '夜，彗出东方，在娄、胃间，长四尺许。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('唐肃宗乾元三年（上元元年）闰四月辛酉朔', '760.5.20', '彗出西方，其长数丈。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('唐肃宗上元元年闰四月二十一日', '760.6.9', '妖星见于西方，长数丈，至五月灭。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('唐代宗大历元年十二月己亥', '767.1.22', '彗起匏瓜，其长尺余，犯宦者星。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('唐代宗大历五年四月己未（二十七日）', '770.5.26', '夜，彗起五车，长三丈。……五月己卯夜，彗起北方，其色白。……六月己未，彗星始灭。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('唐代宗大历五年五月己卯', '770', '有彗星出于北方。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('唐代宗大历五年五月癸未', '770', '彗隨天东行，近八谷。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('唐代宗大历五年六月己未', '770', '彗星始灭，赦天下见禁囚徒。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('唐代宗大历七年十二月丙寅', '773.1.17', '长星出于参。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('唐宪宗元和八年', '813', '是岁，先旱后水，京师谷价腾踊，彗星为变，举选皆停，人情杂然流议，郑注奸谋，日闻于外。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('唐宪宗元和十年三月', '815.4.13-5.12', '有长星于太微，尾至轩辕。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('唐宪宗元和十二年正月戊子', '817.2.17', '夜，彗出毕南，长丈余，指西南，凡三日，南近参旗而没。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('唐穆宗长庆元年正月戊午（二十一日）', '821.2.26', '夜，星孛于翼。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('唐穆宗长庆元年正月己未', '821.2.27', '有星孛于翼。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('唐穆宗长庆元年正月丁卯', '821.3.7', '星孛于辰，近太微西垣南第一星。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('唐穆宗长庆元年六月', '821.7.3-8.1', '有彗星出于昴。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('唐文宗大和二年六月甲辰', '828', '夜，彗西出摄提南，长二尺。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('唐文宗大和二年七月甲辰', '828.9.3', '有彗星于右摄提。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('唐文宗大和八年九月辛亥', '834.10.9', '夜，彗起太微，近郎位，西指，长丈余，西北行，凡九夜，越郎位西北五尺灭。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('唐文宗大和八年九月庚申', '834.10.18', '夜，彗出东方，长三尺，辉耀甚伟。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('唐文宗大和八年九月', '834.10.7-11.4', '（郑注）召对之夕，彗出东方，长三尺，光耀甚紧。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('唐文宗开成二年二月丙午', '837.3.22', '夜，彗出东方，长七尺，在危初，西指。辛酉夜，彗长丈余，直西行，稍南指，在虚九度半。壬戌夜，彗长二丈余，广三尺，在女九度，自是渐长阔。三月乙丑夜，彗星长五丈，歧分两尾，其一指氐，其一掩房。丙寅夜。彗长六丈，尾无歧，在亢七度。戊辰夜，彗长八丈有余，西北行，东指，在张十四度。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('唐文宗开成二年三月乙丑', '837', '夜，彗星长五丈，歧分两尾，其一指氐，其一掩房。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('唐文宗开成二年五月壬午', '837.6.26', '客星如孛，在南斗天籥旁。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('唐文宗开成二年八月丁酉', '837.9.9', '彗出虚、危之间。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('唐文宗开成三年十月十九日', '838.11.9', '彗见，长二丈余，二十日夜，长二丈五尺；二十一日夜，长三丈；二十二日夜，长三丈五尺，并在辰上，西指轸、魁。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('唐文宗开成三年十月乙巳', '838.11.11', '有彗星出于轸。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('唐文宗开成三年十一月乙卯', '838.11.21', '有彗星于东方，在尾、箕，东西亘天；十二月壬辰不见。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('唐文宗开成四年正月癸酉', '839.2.7', '有彗星出于羽林。闰月丙午，出于卷舌。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('唐文宗开成四年正月三十日', '839.2.17', '有彗见于室南，历壁、奎、娄、胃等宿，至闰二月十三日，又见于卷舌北。凡三十有三日。后二十四日，二十五日阴，二十六日晴，已灭。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('唐文宗开成四年十二月壬申', '839', '蚩尤旗见。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('唐文宗开成五年二月庚申', '840.3.20', '有彗星出于室、壁。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('唐文宗开成五年十一月戊寅', '840.12.3', '有彗星出于东方。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('唐武宗会昌元年七月', '841.7.22-8.19', '彗复出室壁之间。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('唐武宗会昌元年十一月壬寅（六日）', '841.12.22', '夜，彗起于室，凡五十六日而灭。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('唐宣宗大中六年三月', '852.3.25-4.22', '有彗星出于觜、参。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('唐宣宗大中十一年九月乙未', '857.9.22', '彗出于房初度，长三尺。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('唐懿宗咸通五年三月', '864.4.10-5.9', '彗星出于娄，司天监以为瑞星。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('唐懿宗咸通五年五月己亥', '864.6.21', '有彗星出于娄。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('唐懿宗咸通九年正月', '868.1.29-2.26', '有彗星出于娄、胃。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('唐懿宗咸通十年八月', '869.9.10-10.8', '有彗星出于大陵。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('唐懿宗咸通十年十二月', '870.1.6-2.4', '有小孛星气经历分野。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('唐懿宗咸通十三年九月', '872', '蚩尤旗见。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('唐僖宗乾符四年五月', '877.6.15-7.14', '有彗星。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('唐僖宗光启元年', '885', '有彗星于积水、积薪之间。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('唐僖宗光启二年五月丙戌', '886.6.13', '有星孛于箕、尾。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('唐僖宗光启二年五月', '886.6.6-7.5', '星孛于箕尾，历北斗摄提。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('唐昭宗大顺二年四月庚辰', '891.5.12', '有彗星入于太微。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('唐昭宗景福元年五月', '892.5.30-6.27', '蚩尤旗见，初出有白彗，形如发，长二尺许，经数日，乃从中天下，如匹布，至地如蛇。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('唐昭宗景福元年十一月', '892.11.23-12.22', '有星孛于斗、牛。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('唐昭宗景福元年十二月丙子', '892.12.28', '天搀出于西南；己卯，化为云而没。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('唐昭宗景福二年四月乙酉', '893.5.6', '有彗星入于太微。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('唐昭宗乾宁元年正月', '894.2.10-3.10', '有星孛于鹑首。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('唐昭宗乾宁三年十月', '896.11.9-12.7', '有客星三，一大二小，在虚、危间，乍合乍离，相随东行，状如门，经三日而二小星没，其大星后没。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('唐昭宗光化二年五月', '899', '西北长星竟天，扫太微、文昌、帝座诸宿，全忠方谋篡代，而妖星谪见。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('唐昭宗天复二年正月', '902.2.11-3.12', '客星如桃，在紫宫华盖下，渐行至御女。丁卯，有流星起文昌，抵客星，客星不动；己巳，客星在杠，守之，至明年犹不去。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('唐哀帝元祐二年四月庚子', '905.5.18', '有彗星出于西北；甲辰，出于北河。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('唐哀帝元祐二年四月甲辰', '905.5.22', '夜，彗起北河，贯文昌，其长三丈，在西北方。五月乙酉夜，西北彗星长六七十丈，自轩辕大角及天市西垣，光辉猛怒，其长竟天。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('唐哀帝元祐二年五月', '905.6.6-7.4', '西北长星竟天，扫太微，文昌、帝座诸宿。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('唐哀帝元祐三年四月', '906.4.26-5.25', '彗出西北，扫文昌、轩辕、天市。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('后梁太祖乾化二年四月壬申', '912.5.13', '彗出于张。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('后梁太祖乾化二年四月甲戌', '912.5.15', '夜，彗见于灵台之西。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('后梁太祖乾化二年五月丁亥', '912', '以彗星谪见，诏两京见禁囚徒大辟罪以下，递减一等，限三日内疏理讫闻奏。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('后唐庄宗同光元年十月戊戌', '923.12.8', '彗星见舆鬼，长丈余。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('后唐明宗天成三年十月庚午', '928.12.13', '夜，西南有孛，长丈余，东南指，在牛宿五度。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('后唐末帝清泰元年十一月丁未', '934.12.19', '彗出虚、危，扫天垒及哭星。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('后唐末帝清泰三年九月己丑', '936.9.21', '彗出虚、危，长尺余，形细微，经天垒、哭星。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('后唐末帝清泰三年十月丁巳', '936.10.19', '夜，彗星出虚危，长尺余。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('南唐烈祖昇元元年十二月丙午', '938.1.31', '有星孛北方。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('南唐烈祖昇元五年八月', '941.8.25-9.23', '有星孛于天市，长数尺，七十日没。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('辽会同四年九月壬申', '941.10.8', '有星孛于晋分。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('后晋高祖天福六年九月壬午', '941.10.18', '夜，有彗星出于西方，长二丈余，在房一度，尾迹穿天市垣东行，逾月而灭。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('后晋高祖天福六年九月壬子', '941', '彗出于西，扫天市垣。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('后晋高祖天福六年九月', '941.9.24-10.22', '有彗星长丈余。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('后晋高祖天福八年十月庚戌', '943.11.5', '夜，有彗见于东方，西指，尾长一丈，在角九度。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('南唐元宗保大元年十月庚戌', '943.11.5', '有星孛于东方。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('后汉高祖天福十二年闰七月丁丑', '947.9.12', '有彗出于张，旬日而灭。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('南唐元宗保大五年闰七月丁丑', '947.9.12', '夜，有彗出东方，近浊，其尾迹近侧扫少微及长垣，至八月壬辰乃没。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('周太祖显德三年正月壬戌', '956.3.13', '夜，有彗孛于参角，其芒指于东南。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('南唐元宗保大十四年二月壬戌', '956', '有星孛于参，芒东南指。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('宋太祖建隆二年十二月己酉', '962.1.28', '客星出天市垣宗人星东，微有芒彗。三年正月辛未，西南行入氐宿，二月癸丑至七星没。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('南唐后主乙亥岁三月丁巳', '975', '彗出五车，色白，长五尺，夏六月转见西方，犯太微，六十日灭。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('宋太祖开宝八年六月甲子', '975.8.3', '彗出柳，长四丈，辰见东方。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('宋太祖开宝八年六月', '975.7.12-8.9', '彗星出柳，长数丈。元江苏');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('宋太宗即位', '976', '会将祀南郊，彗星见，宰相赵普召镐问之。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('宋太宗太平兴国八年二月甲辰', '983.4.3', '客星出太微垣端门东，近屏星北行。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('宋太宗端拱元年六月二日', '988.7.18', '彗星见于井鬼，不数日彗灭。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('宋太宗端拱二年六月十八日', '989.7.23', '彗见积水西，光芒长五尺，行拂右摄提星，至今月（七月）十九日隐大西方，计见四十日。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('宋太宗端拱二年七月丁亥', '989.8.12', '客星出北河星西北，稍暗，微有芒彗，指西南。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('宋太宗端拱二年七月戊子', '989.8.13', '又出东井积水西，青白色，光芒渐长，辰见东北，旬日夕见西北，历右摄提，凡三十日至亢没。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('宋太宗端拱二年七月', '989.8.4-9.2', '彗星出东井，丸三十余日。元江苏');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('宋太宗端拱二年十月辛未', '989', '以定难军节度使赵保忠同平章事。以岁旱、彗星谪见。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('宋太宗端拱三年秋', '990', '彗星见。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('宋太宗淳化元年正月辛巳', '990.2．Z', '客星出轸宿，逆至张，七十日，经四十度乃不见。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('宋真宗嗣位', '998', '会彗星见，拜疏请责躬以答天戒，再召见便殿。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('宋真宗咸平元年正月十三', '998.2.12', '彗出营室北，光芒尺余，……二十六日彗星灭。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('宋真宗咸平元年正月甲申', '998.2.23', '彗出营室北。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('宋真宗咸平元年正月甲寅', '998', '有彗孛于井鬼，大如杯，色青白，光芒至四尺余。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('宋真宗咸平元年二月癸巳', '998', '吕端等言彗出之应，当在齐、鲁分。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('宋真宗咸平六年十一月甲寅', '1003.12.24', '有星孛于井、鬼。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('宋真宗景德二年八月辛丑', '1005.10.1', '有星孛于紫微。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('宋真宗景德二年八月甲辰', '1005.10.4', '客星出紫微天棓侧，孛孛然如粉絮，稍入垣内，历御女、华盖，凡十一日没。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('辽圣宗开泰三年正月丁酉', '1014.2.12', '是夕，彗星见西方。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('宋真宗大中祥符七年正月二十二日', '1014.2.24', '夜，有星出东方，光芒二尺余，司天言：含誉星也。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('宋真宗大中祥符七年九月丙戌', '1014.9.29', '含誉星又见，似彗，有尾而不长。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('宋真宗天禧二年六月辛亥（二十日）', '1018.8.4', '彗出北斗魁。……七月丁亥，彗没。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('宋真宗天禧三年六月辛亥', '1019', '彗出北斗魁第二星东北。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('宋真宗天禧五年四月丙辰', '1021.5.25', '客星出轩辕。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('宋仁宗明道元年六月乙巳', '1032.7.15', '客星出东北方，近浊，有芒彗，至丁巳，凡十三日没。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('宋仁宗明道二年二月戊戌', '1033.8.5', '含誉星见东北方。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('宋仁宗景祐元年八月壬戌', '1034.9.20', '有星孛于张、翼。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('宋仁宗景祐元年十二月己未', '1035.1.15', '夜，有星出外屏，有芒气。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('宋仁宗庆历元年八月庚辰', '1041.9.1', '夜，东方有白气，长十尺许，在星宿度中，至十日，长丈余，冲天相，居星宿大星南，九十余日没。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('宋仁宗皇祐元年二月丁卯', '1049.3.10', '彗出虚，晨见东方，西南指，历紫微至娄，凡一百一十四日而没。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('宋仁宗嘉祐元年七月', '1056.8.14-9.11', '彗出紫微垣，长丈余。八月癸亥，是夕彗灭。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('宋英宗治平三年三月三日', '1066.3.31', '彗星晨见于东方，在营室，自是日见，然天多阴云不辨。至十六日又见。……二十七日夕，见于西方，疾行至张，遂止不行。四月六日后，顿然行缓，避于列曜，将渐隐伏。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('宋英宗治平三年三月己未', '1066.4.2', '彗出营室，晨见东方，长七尺许，西南指危洎坟墓，渐东速行近日而伏；至辛巳，夕见西南，北有星无芒彗，益东方，别有白气一，阔三尺许，贯紫微极星并房宿，首尾入浊，益东行，历文昌、北斗贯尾；至壬午，星复有芒彗，长丈余，阔三尺余，东北指，历五车，白气为歧横天，贯北河、五诸侯、轩辕、太微五帝座内五诸侯及角、亢、氐、房宿；癸未，彗长丈五尺，星有彗气如一升器，历营宿至张，凡一十四舍，积六十七日，星气孛皆灭。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('宋英宗治平三年春三月庚申', '1066.4.3', '彗星晨见于室。辛巳，彗星见于昴，如太白，长丈有五尺。壬午，孛于毕，如月。五月乙丑，彗至张而没。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('辽道宗咸雍二年三月壬午', '1066.4.25', '彗星见于西方。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('辽道宗咸雍二年三月', '1066.3.29-4.26', '彗见西方。庚辰晨见于室，本大如月，长七尺许。辛巳昏见于昴，如太白，长丈有五尺。壬午孛于毕，如月，至五日没。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('宋神宗熙宁五年', '1072', '以彗出西方，遂诏罢之。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('宋神宗熙宁八年', '1075', '彗星见，诏求直言。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('宋神宗熙宁八年十月', '1075', '彗出东方，诏求直言，及询政事之未协于民者。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('宋神宗熙宁八年十月乙未', '1075.11.17', '彗出轸。丁未，彗不见。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('宋神宗元丰初', '1078', '彗出东方，求直言，太史谓有兵变，帝命宦者视卫士饮食。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('宋神宗元丰三年七月二十五日', '1080', '而太史占言，谪见乾象，乃孟秋癸未，彗出西方。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('辽道宗大康五年十二月丙午', '1080.1.6', '彗星犯尾。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('宋神宗元丰三年七月癸未', '1080.8.10', '彗出西北太微垣郎位南，白气长一丈，斜指东南，在轸度中，丙戌，向西北行，在翼度中，戊子，长三尺，斜穿郎位，癸卯，犯轩辕，至丁酉入浊不见，庚子晨，复出于张度中，至戊子，凡三十有六日，没不见。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('宋哲宗元祐六年十一月辛亥', '1092.1.8', '客星出参度中，犯掩厕星，壬子，犯九游星，十二月癸酉入奎，至七年三月辛亥乃散。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('辽道宗寿隆三年八月乙巳', '1097.10.2', '彗星见西方。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('宋哲宗绍圣中', '1094-1097', '禁中火，彗出西方，次升请修德求言，以弭天变。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('宋哲宗绍圣四年八月己酉', '1097.10.6', '彗出西方。……九月戊辰，彗灭。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('宋哲宗绍圣四年九月二日', '1097', '乃仲秋之夕，彗出西方。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('辽道宗寿昌三年八月', '1097.9.9-10.7', '彗出氐，斜指天市垣，光芒三尺余，越三夕，长丈余，扫巴星。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('宋哲宗元符元年十二月', '1098', '彗星见，乾顺赦国中。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('宋哲宗元符二年正月', '1099', '以彗出西方，罢。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('宋徽宗崇宁五年', '1106', '长星见，蔡京斥居浙西，时事小便。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('宋徽宗崇宁五年正月戊戌', '1106.2.10', '彗出西方，其长竟天。……三月丙申，诏星变已消，罢求直言。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('宋徽宗大观四年五月丁未', '1110.5.29', '彗出奎、娄。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('宋徽宗大观四年五月二十二日', '1110', '今彗出东方，兹为大异，永思厥咎，朕甚惧焉。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('宋徽宗大观四年', '1110', '有星孛于中宫，长数丈，始出王良、造父间，遂历阁道，逆行入紫宫，几遍扫垣内外座，已退，俄又进扫帝座者再，前后三十余日乃灭。元江苏');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('宋徽宗政和元年七月六日', '1111.8.12', '有彗星出紫微垣，历七星，其色白，长丈余，自是至八月十四日夜灭。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('宋徽宗宣和五年正月五日', '1123.2.2', '彗出西方，由奎贯娄、胃、昴、毕。……至二十五日彗没。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('宋钦宗靖康元年六月壬戌', '1126.7.19', '彗出紫微垣。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('宋钦宗靖康元年八月庚子', '1126.8.26', '诏以彗星避殿减膳……。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('宋钦宗靖康元年闰月戊午', '1127.1.11', '夜有白气出太微，彗星见。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('宋钦宗靖康元年十二月壬戌', '1127.1.15', '彗出紫微垣东北，长数丈，拂帝座，扫文昌。彗见，有白气出太微微垣。元江苏');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('宋高宗绍兴元年九月', '1131.9.23-10.22', '长星见。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('宋高宗绍兴元年十二月戊寅', '1132.1.5', '（彗星）见于胃。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('宋高宗绍兴二年八月甲寅', '1132.10.7', '彗出胃。……九月甲戌，彗没。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('宋高宗绍兴二年八月二十六日', '1132.10.6', '太史言：是夜四更，彗出位宿度内，如火木星，……夜来初更奏：又犯土司空星，……至九月十七日彗星消。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('宋高宗绍兴二年初', '1132', '是夕，彗出东南。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('宋高宗绍兴八年七月辛亥', '1138.9.3', '彗出东方。……八月丁丑，彗灭。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('宋高宗绍兴十四年', '1144', '彗星见坤宫，五十余日而灭，占其分在夏国。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('宋高宗绍兴十五年四月八日', '1145', '太史奏，彗出东方，朕甚惧焉。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('宋高宗绍兴十五年四月戊寅（三日）', '1145.4.26', '彗星出东方。……癸巳，彗没。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('宋高宗绍兴十六年', '1146', '彗星出西方，浚将极论时事，恐贻母忧。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('金熙宗皇统五年四月丙申', '1145.5.14', '彗星见于西北，长丈余，至五月壬戌始灭。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('宋高宗绍兴十五年正月八日', '1145', '太史奏彗出东方，朕甚惧焉，已避殿減膳，侧躬省愆。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('宋高宗绍兴十六年十一月庚寅', '1146.12.29', '彗星见西南危宿。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('宋高宗绍兴十六年十二月戊戌（三日）', '1147.1.6', '彗见西南方，乙巳，灭。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('金熙宗皇统七年正月辛未', '1147.2.8', '彗星出东方，长丈余，凡十五日灭。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('宋高宗绍兴十七年正月乙亥', '1147.2.12', '妖星出东北方女宿内，小如岁星，光芒长五丈，二月丙寅始消。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('宋高宗绍兴二十六年七月丙午', '1156.7.25', '彗星见东井，约长一丈，光芒二尺，癸丑，又犯五诸侯。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('宋高宗绍兴二十六年七月丁未（八日）', '1156.7.26', '彗出井，丙辰，彗灭。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('宋高宗绍兴二十六年七月九日', '1156', '太史奏，彗出东方，朕甚惧焉。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('宋高宗绍兴三十一年六月庚申', '1161.7.13', '彗出角。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('宋高宗绍兴三十一年六月己巳', '1161.7.22', '彗星见北斗天权星东北，太史妄称为含誉。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('宋孝宗淳熙二年七月辛丑', '1175', '有星孛于西方。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('金宣宗贞祐三年八月壬午', '1215', '长星见西方。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('宋宁宗嘉定十一年五月癸未', '1218', '蚩尤旗见，其长竟天。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('宋宁宗嘉定十四年三月庚寅', '1221.3.30', '长星见。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('金宣宗兴定六年（元光元年）八月己卯', '1222.9.10', '彗星见西方。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('宋宁宗嘉定十五年八月甲午', '1222.9.25', '有彗星出于氐。……九月壬戌，彗星没。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('宋理宗绍定三年十一月丁酉', '1230', '有星孛于天市垣。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('金哀宗正大七年十二月庚寅', '1231', '有星出天津下，大如镇星而色不明，初犯辇道，二日见于东北，在织女南；乙未，入天市垣，戊申方出；癸丑，历房北，复东南行，入积薪，凡二十五日而灭。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('金哀宗天兴元年闰九月己酉', '1232.10.17', '彗星见东方，色白，长丈余，弯曲如象牙，出角、轸南行，至十二日长二丈，十六日月烛不见。二十七日五更复出东南，约长四丈余，至十月一日始灭，凡四十有八日。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('宋理宗绍定五年闰月庚戌', '1232.10.18', '彗星出于角。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('宋理宗绍定五年闰九月', '1232.10.16-11.13', '彗星见东方，十月己未始消。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('宋理宗嘉熙四年正月辛未', '1240.1.31', '彗星出营室。甲午，彗星犯王良第二星。三月辛未，彗星消伏。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('宋理宗宝祐五年', '1257', '彗星出东方。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('宋理宗景定五年', '1264', '以彗星出，梦鼎言政上下恐惧交修之日，乞解机政，又不许。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('宋理宗景定元年七月', '1260', '彗出柳，光烛天，长数十丈，自四更见东方，日高始灭。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('宋理宗景定五年七月甲戌', '1264.7.26', '彗星出柳。己卯，彗星退于鬼。辛巳，彗星退于井。戊戌，彗星退于参。八月戊午，彗星消伏。甲子，彗星复见于参。辛未，彗星化为霞气。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('元世祖至元元年七月甲戌', '1264.7.26', '彗星出舆鬼，昏见西北，贯上台，扫紫微、文昌及北斗，旦见东北，凡四十余日。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('宋理宗景定甲子七月', '1264.7.25-8.22', '七月不记日，夜四鼓闻天西有声如大火炮之震，起登城上烟波亭视之，有星如帚，长丈六七尺，光焰焰然，是谓彗出柳在午。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('元世祖至元十年三月癸酉', '1273.4.9', '客星青白如粉絮，起毕，度五车北，复自文昌贯斗杓，历梗河，至左摄提，凡二十一日。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('元世祖至元十四年二月癸亥', '1277.3.9', '彗星出东北，长四尺余。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('元世祖至元三十年', '1293', '有星孛于帝座。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('元世祖至元三十年十月庚寅', '1293.11.7', '彗星入紫微垣，抵斗魁，光芒尺许，凡一月乃灭。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('元成宗大德二年十二月甲戌', '1299.1.24', '彗出子孙星下。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('元成宗大德五年八月庚辰', '1301.9.16', '彗出井，历紫微垣至天市垣，凡四十六日而灭。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('元成宗大德八年三月乙丑', '1304.4.18', '去岁十二月庚戌，彗星见，约盈尺，在室十一度入紫微垣，至是灭，凡七十四日。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('元仁宗皇庆二年春', '1313', '彗星出东井。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('元仁宗皇庆二年三月丁未', '1313.4.13', '彗出东井。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('元仁宗延祐二年十月丙子朔', '1315.10.29', '客星见太微垣。十一月丙午，客星变为彗，犯紫微垣，历轸至壁十五宿，明年二月庚寅乃灭。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('元顺帝至元三年四月甲戌', '1337.5.4', '有星孛于王良，至七月壬寅没于贯索。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('元顺帝至元三年五月丁卯', '1337.6.26', '彗星见于东北，大如天船星，色白，约长尺余，彗指西南，至八月庚午始灭。……八月庚午彗星不见，自五月丁卯始见，至是凡见六十三日，自昴至房，凡历一十五宿而灭。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('元顺帝至元六年二月己酉', '1340.3.24', '彗星如房星大，色白，状如粉絮，尾迹约长五寸余。彗指西南，渐向西北行。三月辛巳彗星不见，自二月己酉至三月庚辰，凡见三十二日。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('元顺帝至正十一年十一月辛亥', '1351.11.24', '孛星见于奎宿。癸丑，孛星见于娄宿。甲寅，孛星见于胃宿。乙卯亦如之。丙辰，孛星见于昴宿。丁巳，孛星微见于毕宿。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('元顺帝至正十一年十一月癸丑', '1351.11.26', '有星孛于娄宿。甲寅，孛星见于胃宿。乙卯、丙辰，亦如之。丁巳，孛星微见于毕宿。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('元顺帝至正十六年八月甲戌', '1356.9.21', '彗星见张宿，色青白，彗指西南，长尺余，至十月戊午始灭。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('元顺帝至正二十年三月戊子朔', '1360.3.18', '彗星见东方。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('元顺帝至正二十二年正月', '1362.1.27-2.24', '彗星见，长二丈余，三月灭于昴。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('元顺帝至正二十二年二月乙酉', '1362.3.5', '彗星见于危宿，光芒长丈余，色青白。丁酉，彗星犯离宫西星，至二月终，光芒长二丈余。三月戊申，彗星不见星形，惟有白气，形曲竞天，西指扫大角。壬子，彗星行过太阳前，惟有星形，无芒，在昴宿，至戊午始灭。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('元顺帝至正二十二年四月丙子朔', '1362.4.25', '长星见，其形如练，长数十丈，在虚、危之间，后四十余日乃灭。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('元顺帝至正二十二年六月辛巳', '1362.6.29', '彗星见紫微垣，光芒长尺余，东南指，西南行。戊子，彗星光芒扫上宰。七月乙卯，彗星灭迹。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('元顺帝至正二十二年', '1362', '京师有白气如小索，起危宿，长五百丈，扫太微。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('元顺帝至正二十三年三月辛丑朔', '1363.3.16', '彗星见东方，经月乃灭。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('元顺帝至正二十五年七月', '1365.7.19-8.17', '彗星见东方。明嘉靖');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('元顺帝至正二十六年九月庚子', '1366.10.25', '孛星见于紫微垣北斗权星之侧，色如粉絮，约斗大，往东南行，过犯天棓星。辛丑，孛星测在尾十八度五十分。壬寅，孛星测在女二度五十分。癸卯，孛星测在女九度九十分。甲辰，孛星测在虚初度八十分。乙巳，孛星出紫微垣北斗权星、玉衡之间，在于轸宿，东南行，过犯天棓，经渐台、辇道，去虚宿、垒壁阵西方星，始消灭焉。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('元顺帝至正二十六年九月辛丑', '1366.10.26', '孛星见东北方。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('元顺帝至正二十八年正月庚寅', '1368.2.7', '彗星见于昴、毕之间。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明太祖洪武元年正月庚寅', '1368.2.7', '彗星见于昴、毕。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('元顺帝至正二十八年三月庚寅', '1368.4.7', '彗星见于西北。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明太祖洪武元年三月辛卯', '1368.4.8', '夜，彗星出。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明太祖洪武元年三月二十七夜至四月初一', '1368.4.14-4.18', '彗星驻于天西，夜至二更没。明成化广东');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明太祖洪武元年三月', '1368.3.19-4.17', '彗星见南斗。清康熙广东');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明太祖洪武元年四月己酉', '1368.4.26', '夜，彗星没。先是，三月辛卯，彗出昴北大陆、天船间，芒长约八尺余。辛丑，指文昌，近五车。是夜，没于五车北．');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明太祖洪武六年四月', '1373.4.23-5.22', '彗星三入紫微垣。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明太祖洪武九年六月戊子', '1376.6.22', '有星大如弹丸，白色。止天仓，经外屏、卷舌，入紫微垣，扫文昌，指内厨，入于张。七月乙亥灭。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明太祖洪武九年七月乙亥', '1376.8.8', '客星没。先是六月戊子，客星大如弹丸，白色，止天仓。至癸巳，益有光。甲午，经外屏。壬寅，经卷舌，入紫微垣。庚戌，扫文昌，指内厨。壬子，扫文昌。是月癸亥，入于张，至是夕始灭。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明太祖洪武十一年九月甲戌', '1378.9.26', '有星见于五车东北，发芒丈余。扫内阶，入紫微宫，扫北极五星，犯东垣少宰，入天市垣，犯天市。至十月己未，阴云不见。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明太祖洪武十一年九月己丑', '1378.10.11', '客星扫天弁，西南行入尾。先是，甲戌，见于五车东北。丁丑，入紫微，戊寅，发芒长丈余，扫内阶。庚辰，入紫微宫，扫北极五星。壬午，犯东垣少宰。甲申，入天帝垣。丁亥，入天市。自是至十月己未，云阴不见。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明太祖洪武十五年七月二十日（丁卯）', '1382.8.29', '晚，彗星出西北。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明太祖洪武乙丑四月', '1385.5.10-6.7', '彗星见，扫翼，复扫天庙。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明太祖洪武十八年十月戊寅', '1385.10.23', '彗星扫天庙。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明太祖洪武二十二年六月辛巳', '1389', '彗星见紫微侧，在牛度九十分，色白，光约长丈余，东南指，西北行。戊子，彗光扫上宰。七月乙卯灭。明万历浙江');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明太祖洪武二十二年七月辛巳', '1389.8.6', '彗见紫微旁，乙卯灭。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明太祖洪武二十四年四月丙子', '1391.5.23', '夜，彗星二：一入紫微垣阊阖门，犯天床；一犯六甲，扫五帝座。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES (NULL, '1402', '彗星光芒尤大。');
