INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明宣宗宣德五年十月丙申', '1430.11.14', '夜，有蓬星色白如粉絮，见外屏南，渐东南行，经天仓、天庾，凡八日始灭。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明宣宗宣德六年四月戊戌', '1431.5.15', '昏刻，彗出东井，芒角蓬勃，长五尺余。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明宣宗宣德六年四月', '1431.5.12-6.9', '有星孛于东井。明万历');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明宣宗宣德七年正月壬戌', '1432.2.3', '夜，有彗出东方，光芒长丈余，尾扫天津，渐东南行，十日始灭。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明宣宗宣德七年正月戊子', '1432.2.29', '夜，复有彗出西方，十有七日始灭。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES (NULL, '1433', '彗星始见贯索度内。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明宣宗宣德八年闰八月壬子', '1433.9.15', '昏刻，有彗出天仓旁，光芒长丈，己巳，入贯索，扫七公。己卯，入天市垣，扫晋星，又二十有四日灭。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明宣宗宣德八年闰八月庚午', '1433.10.3', '昏刻，有彗出贯索内，尾长五尺余，渐西南行，凡三十有四日始灭。');
