INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明英宗正统四年闰二月己丑', '1439.3.25', '夜，东南张宿旁生彗星，大如弹，色白，西行。丁酉夜，彗星芒长五尺余，西北行，扫酒旗庚子夜，彗星西北行，长七尺余，犯鬼宿。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明英宗正统四年六月戊寅', '1439.7.12', '彗星见毕宿，光芒长一丈余，指西南，至七月二十五日乃没。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES (NULL, '1444', '彗星见。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明英宗正统九年七月庚午', '1444.8.6', '昏刻，太微东垣彗星光芒长一丈，渐长。至闰七月初二日，入角宿没。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明英宗正统十四年十二月壬子', '1449.12.20', '晓刻，彗星见于天市垣市楼星旁。癸丑晓刻，彗星见于尾十二度，色苍白，芒长尺余，扫正西。乙卯晓刻，彗星见于尾十一度，长二尺余。丙辰晓刻，彗星见尾宿十一度，西行，至乙亥夜不见。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES (NULL, '1450', '彗星始不见。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明英宗景泰元年正月壬午', '1450.1.19', '夜，彗星出天市垣外，扫天纪星。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明英宗景泰三年三月甲午朔', '1452', '有星孛于毕。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明英宗景泰七年四月壬戌', '1456.5.27', '夜，彗星见东北方，在胃宿，光芒长二丈，尾指西南。五月癸酉夜，彗星光芒渐长丈余，指西南。戊子昏刻，彗星见西北方，在柳宿，尾长九尺余，扫犯轩辕星。甲午昏刻，彗星见张宿，光芒七尺余，尾扫太微西垣，往西南行。六月壬寅夜，彗星入太微西垣内上将星旁，芒长五尺余。');
