INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明英宗景泰七年七月间', '1456.8.1-30', '昼漏当申刻之末，彗星如洗帚状，微见于西方，至酉刻以后，渐长如扫帚，人呼日扫帚星，日既没，其长竟半天，如此两月而灭。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明英宗景泰七年十月', '1456.10.29-11.27', '昼漏当申刻之末，彗星如帚洗状，至酉刻以后，长如扫帚，星日既没，其长竟半天。清康熙浙江');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明英宗景泰七年十二月甲寅', '1457.1.14', '夜，彗星复见于毕宿，光芒长五寸，徐徐东南行，光芒渐长，自是日至于癸亥。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明英宗天顺初', '1457', '未几，亨西征还，适彗星见。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明英宗天顺元年五月丙戌', '1457.6.15', '夜，彗星见于危宿，状如粉絮，色青白，拂拂摇动，至丁亥夜东行一度，微芒长五寸，指西南；六月癸巳夜，彗星见于室宿，芒角长丈余；己亥夜，彗星犯壁宿，尾指东壁二星；乙巳夜，彗星犯天大将军；壬子夜，彗星经犯卷舌第三星；七月戊寅夜，彗星犯井宿；辛卯夜，彗星犯水位南第二星；八月甲午，上躬祷于昊天上帝，词日：兹者彗星出现于今两月，光芒尚在，垂变不消。……');
