INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明英宗天顺二年十一月癸卯', '1458.12.24', '夜，有客星见于星宿，色白，西行；至丙午夜，其体微大，状如粉絮，在轩辕之旁；庚戌夜，客星生芒，可五寸，经犯爟位西北星；十二月壬戌夜，客星没于井宿。');
