INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明英宗天顺五年六月壬辰', '1461.7.30', '夜，天市垣宗正星旁有异星见，色粉白，至乙未夜，化为白气而消。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明英宗天顺五年六月戊戌', '1461.8.5', '夜，彗星见东方，光芒长三尺余，尾指西南；七月庚子夜晓刻，彗星在井四度，光芒长一尺余，尾指西南；丙午夜晓刻，彗星仍见井宿，至丙寅夜灭。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明英宗天顺六年六月丙寅', '1462.6.29', '夜，客星见于策星旁，色苍白；己巳夜，客星入紫微垣内；乙亥夜，客星入犯天牢星；癸未夜，客星居中台星下，形渐微。');
