INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明宪宗成化四年九月癸未', '1468', '夜，客星色苍白光芒长三丈，余尾指西南，变为彗星。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明宪宗成化四年九月丁卯', '1468', '吏部等衙门太子少保吏部尚书李秉等以彗星见，俱引咎乞免诏之曰玄象示警朕惕然戒惧，卿等正当竭诚修职，共回天意不许罢归。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明宪宗成化四年九月己未', '1468.9.18', '夜，客星见星五度，东北行；癸亥夜，客星色苍白，光芒长三丈余，尾揩西南，变为彗星；戊辰，彗星晨见东北方；己巳，彗星昏见西南方；庚午昏刻，彗星犯三公星；辛未昏刻，彗星犯北斗摇光星；丁丑昏刻，彗星犯七公西第四星；壬午昏刻，彗星入天市垣；十月乙巳，彗星出天市垣，其体渐小；甲寅，彗星犯天屏西第一星；十一月庚申夜，彗星灭。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明宪宗成化四年九月戊辰', '1468.9.27', '（按明昭代典）彗星晨见东北方。己巳，彗星昏见西南方。九月丁丑昏刻，彗星犯七宫西等四星。壬午昏刻，彗星入天市垣。十月甲寅，彗星犯天屏西第一星。十一月戊午夜，彗星灭。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明宪宗成化四年九月癸酉', '1468.10.2', '（按大政纪）夜，客星色苍白，光芒长三丈余，尾指西南，变为彗星。丁丑昏刻，彗星犯七公等四星。壬午昏刻，彗星入天市垣。十月甲寅，彗星犯天屏西第二星。十一月戊午，彗星灭。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明宪宗成化四年九月', '1468.9.16-10.15', '彗星见。');
