INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明孝宗弘治十五年十月戊辰', '1502.11.28', '客星见张宿天庙星旁。己巳，客星行至翼宿。癸酉，客星复在张宿。十一月戊寅夜，客星灭。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES (NULL, '1506', '有星孛于紫(薇)〔微〕。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES (NULL, '1506', '彗星见紫微垣内，长数丈许，尾指东南，向西而没，经旬以灭。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明武宗正德元年七月庚子', '1506', '夜青州府诸城县，彗星见，有光流东南约长三尺。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明武宗正德元年七月己丑', '1506.7.31', '夜，有星见北方紫微西蕃外，如弹丸，色苍白。壬辰夜，又见其星，有微芒，连三夕，数见参、井之间。至戊戌夜，芒渐长二尺，偏扫如帚，徐徐西北至文昌。壬寅夜，彗星光芒长五尺许，扫犯下台上星。癸卯昏刻，彗星入太微垣。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明武宗正德元年八月癸酉', '1506', '以彗星见，遣英国公张懋驸马都尉蔡震祭告天地，宗庙。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明武宗正德四年', '1509', '有彗星见于西方。明嘉靖安徽');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明武宗正德八年', '1513', '彗星见，自夏至秋乃灭。清康熙河北');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明武宗正德十一年', '1516', '彗星见于东井。清乾隆贵州');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明武宗正德十三年八月', '1518.9.5-10.4', '彗星见。清道光广西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明武宗正德十五年正月一日', '1520.1.20', '彗星见。明嘉靖浙江');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明武宗正德十五年正月', '1520.1.20-2.18', '彗星见。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明世宗嗣位', '1522', '彗星见，应诏陈八事。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明世宗嘉靖二年六月', '1523', '有星孛于天市。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明世宗嘉靖四年十月', '1525.10.16-11.14', '彗见西方，逾年始没。清乾隆福建');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明世宗嘉靖七年闰十月', '1528.11.12-12.11', '彗星见。清乾隆广东');
