INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明世宗嘉靖十一年冬', '1532．冬', '彗星见。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明世宗嘉靖十一年', '1532', '彗星见东方，其指西南丈余，至冬尽乃没。明嘉靖福建');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明世宗嘉靖十二年六月壬午', '1533', '大学士张孚敬等，以彗星见欲自陈引去。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明世宗嘉靖十二年六月辛巳', '1533.7.1', '见于五车，芒长五尺余，尾指西南。壬午，彗星复出毕、昴之间，光芒稍见。己亥夜，彗星扫大陵及天大将军，芒长丈余。七月甲寅，彗星扫阁道，行犯滕蛇，至八月二十八日而灭。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明世宗嘉靖十二年六月', '1533.6.22-7.21', '彗星复见毕昴间，乞避位，不许。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明世宗嘉靖十二年八月', '1533.8.20-9.17', '彗星复出东井，扫太微垣，至于十二月。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明世宗嘉靖十三年五月丁卯朔', '1534.6.12', '夜四更，客星见于螣蛇，历天厩，入阁道，至二十四日而灭。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明世宗嘉靖十三年八月', '1534.9.8-10.6', '彗星见。清康熙广西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明世宗嘉靖十五年三月戊午', '1536.3.24', '夜，客星见于天禧棓星旁。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明世宗嘉靖十五年三月', '1536.3.22-4.19', '彗星见东南。清雍正浙江');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明世宗嘉靖十五年四月壬辰', '1536.4.27', '初，客星见于天棓旁，渐东行，历天厨西，入天汉，至是日（原文作始日）始没。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明世宗嘉靖十六年八月', '1537.9.4-10.3', '彗星见于西南，月余始灭。清顺治河南');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明世宗嘉靖十六年', '1537', '丰熙……嘉靖丁酉卒。先是，彗星见文昌。明嘉靖浙江');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明世宗嘉靖十七年九月', '1538.9.23-10.22', '有彗星见西方。清康熙浙江');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明世宗嘉靖十八年四月庚戌', '1539.4.30', '夜，彗星见，芒长三尺许，光指东南，扫轩辕北第八星，旬日始灭。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明世宗嘉靖十八年四月', '1539.4.18-5.17', '彗星见。明万历江西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明世宗嘉靖十八年八月', '1539.9.12-10.11', '彗又出西方。明天启浙江');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明世宗嘉靖二十四年十一月壬午', '1545.12.26', '夜，客星出天棓，入箕宿。越三日，转东北方，逾月始没。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明世宗嘉靖二十五年二月', '1546.3.2-31', '彗星见北方，长丈余，凡三十余日乃灭。清乾隆福建');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明世宗嘉靖三十三年四月', '1554.5.2-30', '彗星见，出北斗口，尾长亘天，五日而灭。明隆庆江苏');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明世宗嘉靖三十三年五月癸亥', '1554.6.23', '夜，彗星见北斗天权星旁。六月辛未夜，彗星渐西北行，犯文昌。己丑夜，彗星行入近浊，始没。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明世宗嘉靖三十三年五月', '1554.5.31-6.29', '彗星见北斗天权星旁。清康熙安徽');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明世宗嘉靖三十四年春', '1555．春', '彗光烛于北斗。明万历江西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明世宗嘉靖三十四年十月', '1555.10.15-11.13', '彗星出昴，北行渐至斗。清顺治山西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明世宗嘉靖三十四年十二月二十日', '1556.1.31', '彗星始见于东南，旋入西北，月余而没，芒长数十丈。清顺治甘肃');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明世宗嘉靖三十五年正月庚辰', '1556.3.1', '夜，彗星见于进贤星旁，芒长尺，西南指，渐长至三尺余，历扫大微垣次相星，又东北行，入紫微垣，扫天床星。至四月二日始灭。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明世宗嘉靖三十五年二月', '1556.3.11-4.10', '彗星见北方，长丈余，凡三十余日乃灭。明嘉靖福建');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明世宗嘉靖三十五年三月', '1556.4.10-5.8', '彗星见。明隆庆江苏');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明世宗嘉靖三十五年七月十四日', '1556.8.18', '夜，彗星见于西方。清康熙广西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明世宗嘉靖三十五年七月', '1556.8.5-9.3', '彗星见，长数尺，月终乃灭。明天启云南');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明世宗嘉靖三十五年', '1556', '彗星见。明嘉靖');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明世宗嘉靖三十六年九月戊辰', '1557.10.10', '彗星见于天市垣内列肆星旁，芒长尺余，东北指，至十月二十三日始灭。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明世宗嘉靖三十六年', '1557', '彗星见东北。明万历江苏');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明世宗嘉靖四十三年八月内', '1564.9.5-10.4', '彗星见西方，月余方灭。清康熙山东');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明世宗嘉靖四十三年冬', '1564．冬', '彗星夜见西南，月余方灭。清乾隆云南');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明世宗嘉靖四十三年', '1564', '彗星夜见西南，经月乃灭。清康熙');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明世宗嘉靖四十五年', '1566', '彗星见东南。清康熙河南');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明世宗嘉靖四十六年九月二十六日', '1567.10.27', '晓，白气见东南，半月而灭。寻有星孛于东方，渐移而北，光长数丈，直亘天中。清康熙江苏');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明世宗嘉靖四十六年十月', '1567.11.1-11.30', '夜半，有白气数丈，起于东南，月余，彗星抵鬼斗，皆现于卯日，经于铃链二星，乃柘之分野。清乾隆河南');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明世宗嘉靖四十六年', '1567', '彗星见东方，尾长亘天。清康熙湖北');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明穆宗隆庆三年九月三十', '1569.11.8', '夜，旄头星见西方，长五六尺。清康熙广东');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明穆宗隆庆三年十月辛丑', '1569.11.9', '彗星见于天市垣，色苍白，芒指东北，长二尺余，至二十日灭。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明穆宗隆庆三年', '1569', '彗星出尾分。清康熙河北');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明穆宗隆庆五年九月', '1571.9.19-10.18', '彗星见。明崇祯河北');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明穆宗隆庆五年', '1571', '彗星见于西北，长丈余。清乾隆河南');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明穆宗隆庆六年八月', '1572.9.7-10.6', '彗星见。清乾隆广东');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明穆宗隆庆六年十月', '1572.11.6-12.4', '彗星见西方。清乾隆甘肃');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历元年六月', '1573.6.29-7.28', '彗星见。清康熙广东');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历元年九月', '1573.9.26-10.25', '夜，有星孛于西南方，氛气散漫，高约十丈余，上锐下阔，经三月方灭。清康熙福建');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历二年十二月', '1574.12.13-1575.1.11', '火星守奎宿，有尾，光芒长丈余，如彗星，一月乃散。明天启江苏');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历四年七月', '1576.7.26-8.28', '彗星见。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历四年八月', '1576..248-9.21', '孛星见。清康熙福建');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历四年秋', '1576.7.26-10.21', '彗星见。清康熙广东');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历四年十月', '1576.10.22-11.19', '彗见西南方，经两旬始灭。明万历湖南');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历四年', '1576', '彗星见。清康熙广东');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历五年', '1577', '适彗出西南，长竟天');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历五年夏', '1577．夏', '彗星光十余丈，阔大，经月乃消。明万历山东');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历五年七月二十七', '1577.8.9', '有星如白气，长数丈，自西南方现，直指东北，至十一月渐小而没。清康熙福建');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历五年七月终', '1577.8.13', '彗星见西南方，犯斗度，至十二月没。清康熙浙江');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历五年八月二十七日', '1577.9.8', '有星吐白气，长数丈，至十月乃没。清乾隆');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历五年八月三十日', '1577.9.11', '彗星见于西南，光芒直射于车，形如簪，至十二月初旬方散。清康熙安徽');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历五年八月', '1577.8.14-9.11', '彗星见于西，长丈余，经四旬没。明崇祯湖南');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历五年九月四日', '1577.10.14', '彗星见西方，形如拖练，长约十余丈，由尾历箕越斗度牛，至十一月十九日止。清乾隆江西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历五年九月十八', '1577.10.28', '彗星见，光芒垂丈余，直射西南。清雍正广西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历五年九月二十三日', '1577.11.2', '申时，彗星见西方，尾长五、六丈，白气亘天，至十一月渐暗不见。清康熙广东');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历五年九月二十五', '1577.11.4', '彗出长庚。明万历安徽');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历五年九月二十七', '1577.11.6', '彗星见西方，大如盏，尾长竟天，末大如旗，寒焰射人，月余灭。明万历福建');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历五年九月二十八', '1577.11.7', '彗星见，光芒烛天，月余始息。明万历河北');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历五年九月二十九', '1577.11.8', '彗星见西南，光芒三丈余，东指凡半日，至十一月十五日始灭。清嘉庆江苏');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历五年九月晦', '1577.11.9', '彗星见于西南方，其尾如帚，长丈余，直指南方，渐北行至天中渐小，十二月乃灭。清康熙江西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历五年九月', '1577.10.11-11.9', '彗星见于西南，长十数丈，尾向东南，至十一月终乃没。明天启安徽');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历五年秋', '1577.秋', '彗星见于西方，长数丈，形如帚，月余不灭。明万历山西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历五年十月朔', '1577.11.10', '彗星见斗、牛间，长数十丈。清顺治安徽');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历五年十月戊子', '1577.11.14', '彗星见西南，光明大如盏，芒苍白色，长数丈，繇尾、箕，越斗、牛，直逼女宿。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历五年十月', '1577.11.10-12.8', '彗星见西方，大如车轮，气焰上冲如喷状，甚可畏，逾年不散，后芒渐微，而长竟天，且移入吴越分井。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历五年十一月一日（冬月朔）', '1577.12.9', '彗星见西方，约长数十丈，数旬始没。清乾隆四川  ');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历五年冬', '1577.冬', '彗星见于西南，白气如练，至春始灭。明万历山东');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历五年', '1577', '彗星从东南方起，长亘天。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历六年三月', '1578.4.7-5.6', '彗星复见。清康熙广西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历六年九月', '1578.10.1-lO.29', '彗星见西南，有光如帚，长二丈，色白。明万历江苏');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历六年秋', '1578．秋', '彗见西南方，尾散如帚，其长竟天，五十余口方灭。清康熙广东');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历六年十月', '1578.10.30-11.28', '彗星见西方。清康熙甘肃');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历六年', '1578', '彗星见西南方，光芒三，四丈。明万历安徽');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历七年八月', '1579.8.22-9.20', '彗星见西方，光烛天。清雍正云南');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历七年九月', '1579.9.21-10.19', '彗星见，在翼、轸间。清康熙浙江');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历七年', '1579', '彗星见楚雄西。明天启云南');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历八年八月庚申', '1580.10.1', '夜，有异星见于东南方，每夜形体渐长，有光芒，占为彗星，至十一月望后始退。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历八年九月辛卯', '1580.10.21', '彗星见西方。清康熙浙江');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历八年九月二十七日', '1580.11.4', '彗星见月余。清乾隆山西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历八年九月', '1580.10.9-11.6', '彗星见，在翼、轸间。明万历浙江');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历八年十一月', '1580.12.7-1581.1.4', '彗星自西北方现。清康熙福建');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历八年冬', '1580．冬', '彗见月余。清康熙山西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历八年', '1580.1.16-1581.2.3', '彗星见于西南。明万历湖南');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历九年三月', '1581.4.3-5.2', '彗星见紫微垣，尾光芒射西北。明天启江苏');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历九年夏', '1581．夏', '夜，一星如帚，长二丈余。清康熙湖南');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历九年七月', '1581.7.30-8.28', '彗星见，尾东指，竟天。清康熙江苏');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历九年八月', '1581.8.29-9.27', '彗见西方，火光烛地，匝三旬乃没。明天启');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历九年十一月', '1581.11.26-12.25', '彗星见西南，长丈余，数月乃息。清乾隆陕西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历九年冬', '1581．冬', '脱练星出牛、斗间，长数尺，渐长至数丈，月余乃灭。清康熙河南');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历九年', '1581', '彗星见于西北。明万历山东');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历十年四月丙辰', '1582.5.20', '夜，彗星见于西北，尾指五车，历二十余日始灭。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历十年四月', '1582.4.22-5.21', '彗见澂江。明天启云南');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历十年五月甲子', '1582', '礼科都给事中石应岳等，题窃见四月末旬，彗星出于五车前。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历十年六月丁亥', '1582.6.22', '朔三日，彗出五车口，柱星以南。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历十年九月', '1582.9.17-10.17', '彗星见，长竟天，其色苍，尾指西北，凡四十九夜乃灭。清乾隆');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历十年秋', '1582．秋', '彗光横亘于奎、娄之分者弥月。明万历江西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历十年', '1582', '彗星东见，岁余方灭。明万历山西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历十一年', '1583', '彗星见。清康熙');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历十二年正月', '1584.2.12-3.11', '彗星又见。清康熙云南');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历十二年四月', '1584.5.10-6.7', '彗星见。清康熙');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历十二年', '1584', '彗见大理西。明天启云南');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历十二年丙午', '1584', '陝西甘凉地方赤白云气，匝绕半天又西北有彗星形如匹练。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历十三年九月戊子', '1585.10.13', '彗星出羽林傍，形如弹丸，尾长尺许，指东北，其色苍白；后每夕移东行，芒体渐缩小。十月癸酉，彗星灭不见。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历十三年', '1585', '彗星见东方。清康熙云南');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历十四年', '1586', '彗星经天。清乾隆河南');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历十五年六月中', '1587.6.16-26', '东北彗星昼见，自气亘天，若大刀状。清乾隆湖南');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历十六年三月', '1588.3.26-4.24', '彗星见西南，数丈，三阅月始没。清康熙福建');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历十六年九月乙卯', '1588.10.24', '东南有白气一道，阔尺余，长二丈余，东至轸，西入翼，十九日灭。清同治湖南');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历十六年十月乙丑', '1588', '彗星出于氐，长丈余，指东南，渐指西北，入亢度扫北斗、文昌、五车逼紫宫右，十一月甲辰灭。清同治湖南');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历十八年', '1590', '彗见东南，经旬乃没。明天启云南');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历十九年三月丙辰（二十日）', '1591.4.13', '西北有星如彗，长尺余。历胃、室、壁，长二尺。闰三月丙寅朔，入娄。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历十九年三月', '1591.3.25-4.22', '有星如彗，历胃、室、壁，闰三月入娄。清乾隆山东');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历十九年闰三月丙寅朔', '1591.4.23', '彗入娄。癸酉，大学士申时行题：三月二十日一更后，西北方有星如彗，尾长尺余，钦天监云，在胃宿度，近又在东北方室壁宿度，尾长约二尺。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历十九年闰三月丁丑', '1591.5.4', '以彗星见，敕修省。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历十九年闰三月', '1591.4.23-5.22', '彗星在西北方室、壁度，尾长二尺。明崇祯上海');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历十九年七月', '1591.8.19-9.16', '彗见东井，逆行入紫微。清乾隆广西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历二十年七月', '1592.8.7-9.5', '彗见如弹。清乾隆安徽');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历二十年', '1592', '彗星见。明天启云南');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历二十一年', '1593', '时两宫灾，彗星見。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历二十一年七月乙卯', '1593.7.30', '夜五鼓，彗星见于井度，长三尺余。乙亥，彗星逆行，入紫微垣，犯华盖星。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历二十一年七月', '1593.7.28-8.25', '彗星见。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历二十一年八月朔', '1593.8.26', '夜五鼓，彗星见北方，十日始灭。清道光福建');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历二十一年八月', '1593.8.26-9.24', '彗星见。清光绪');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历二十一年八月癸未', '1593', '大学士王锡爵密奏：臣连夜仰观乾象，见彗星已入紫微垣。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历二十一年十二月', '1594.1.21-2.19', '彗出天津。清康熙江苏');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历二十四年四月', '1596.4.27-5.26', '沁州彗星见，光芒烛天，至七月始灭。明崇祯');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历二十四年六月己酉', '1596.7.9', '彗星见东北方，芒指西南。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历二十四年七月丁丑', '1596.8.5', '彗星见西北方，如弹丸大，苍白色，芒指东南，入翼宿度，约长尺余，往西北行入。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历二十四年七月', '1596.7.25-8.23', '彗星见于西北。清康熙广西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历二十四年', '1596', '彗见西北上台南。明崇祯河北');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历三十年八月甲寅', '1602', '是夜，五更有长星，头大红色，尾尖白色。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历三十二年秋', '1604．秋', '彗星见于天中，其色暗淡不现，长可二三尺，越六日始消。清乾隆广东');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历三十五年五月', '1607.5.26-6.23', '每夜西南天起一星，光亘五尺，至八月巾始无。明崇祯江苏');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历三十五年夏', '1607．夏', '彗星见。清康熙');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历三十五年夏、秋', '1607．夏、秋', '夜，有星见西南，光芒盈五尺，阅两月乃灭。清康熙浙江');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历三十五年七月庚申', '1607.9.20', '夜，彗见东井，二尺余。八月丁丑夜，彗历于房。壬午夜，彗自房历于心。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历三十五年七月', '1607.8.22-9.20', '彗星见，长数尺，月终乃灭。清道光云南');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历三十五年八月辛酉朔', '1607.9.21', '彗星见于井度，色苍白，尾指西南，约长二尺，渐往西北。丁丑，彗历于房。壬午，彗历于心。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历三十五年八月戊寅', '1607', '今彗星复见东井。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历三十五年八月', '1607.9.21-10.20', '妖星夜见东北，半月后昏见西南，遂灭。清康熙山西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清太祖丁未九月丙申', '1607.10.21', '彗星见于东方，指辉发国，八夜方灭。先是彗出西方，逾月。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清太祖丁未九月六日', '1607.10.26', '夜，有气从星出，向东直冲辉发国，七、八夜方没。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历三十五年九月', '1607.10.21-11.18', '彗星见轸。清乾隆广西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历三十五年九月丙申', '1607', '长星出东方指辉发，八夕乃灭。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历三十五年', '1607', '彗星见，昏东晨西。清康熙湖北');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历三十九年十月朔', '1611.11.5', '夜半，彗星见东南方，长三、四丈。日出渐没，旬余乃止。民国');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历三十九年', '1611', '彗星见南方。清乾隆广东');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历四十年九月', '1612.9.25-10.24', '彗星见。清康熙湖北');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历四十年秋', '1612秋', '有星出灵谷东，炳首一星大与金木同体，尾喷小星数万，如帚。清康熙江西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历四十年', '1612', '彗星见。清康熙河南');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历甲寅正月', '1614.2.9-3.9', '彗星见义乌西北。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历四十二年', '1614', '彗星见西北角。清嘉庆浙江');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历四十三年八月', '1615.8.24-9.22', '彗星见，形如帚，自气冲天，粗如瓮，长二丈，一月方息。清乾隆山西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历四十三年十月', '1615.11.21-12.19', '蚩尤星见。夜，，有星在东南，妖光喷薄，如元宵火树状，高数十丈，复有黑气一道，直侵紫垣，七日乃灭。清康熙河北');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历四十四年秋', '1616．秋', '东方白虹自地起，如匹练，子夜绵亘数丈，冲斗虚，经月不散。清康熙山东');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历四十四年十月', '1616.11.9-12.8', '白气见女、牛，贯于斗口，夜见东南，长十余丈，如练，两月始没。清康熙安徽');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历四十四年冬', '1616．冬', '彗见，自东井入紫微垣，南指有光。清康熙山西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历四十四年', '1616', '彗星见，越东北，竟天，百余日未泯。清顺治河南');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历四十五年五月', '1617.6.3-7.2', '彗星见，其形如箕。清康熙山西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历四十五年六月', '1617.7.1-31', '中天一星，忽变长，光数丈，其一头若悬一灯。清光绪浙江');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历四十五年七月', '1617.8.1-8.30', '夜半后，南天有白气上冲，长数丈，广数尺，头锐东指，状如刀，月余而灭。明崇祯江苏');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历四十五年八月', '1617.8.31-9.29', '五鼓彗星见，形如帚。清康熙山西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历四十五年九月', '1617.9.30-10.28', '初，东北有白气弥漫者数夜，至是彗星见，长三丈许，白气时在四面旋遶，每夜三更出，五更收，至十一月方不见。清顺治山西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历四十五年', '1617', '彗星见于州东南，经月不散。清康熙广东');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历四十六年正月', '1618.1.26-2.24', '白彗出氐、亢。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历四十六年三月', '1618.3.26-4.24', '彗出于虚。清乾隆');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历四十六年春', '1618．春', '彗星出东南，冲紫微垣，五十日始没。清乾隆山西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历四十六年四月', '1618.4.25-5.23', '长星见东南，长二丈，广尺余，十有九日而灭。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历四十六年六月二十六日', '1618.8.16', '东南有帚星。清康熙山西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历四十六年六月', '1618.7.22-8.19', '彗星见。清康熙山西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历四十六年夏', '1618．夏', '夜，东北彗星现，芒射经天。清康熙山东');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历四十六年七月', '1618.8.20-9.18', '白气见东方或云刀星，至十一月杪方没。清康熙福建');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历四十六年秋分', '1618.9.21', '夜，东方有云赤白色，形如刀，长丈余，累月方消。清乾隆');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历四十六年八月', '1618.9.19-10.17', '彗出东南，尾扫西北，八十余日灭。清顺治河北');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历四十六年九月二十日', '1618.11.6', '黎明，东南有白气冲天，日出不见，凡十余日。清嘉庆江苏');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历四十六年九月二十五', '1618.11.11', '东南天际起白光如虹，长四、五丈，广四、五尺。明崇祯江苏');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历四十六年九月二十六（辛亥）', '1618.11.12', '晚，白气见东南，半月而灭。寻有星孛于东方，渐移西北，光长数丈，直亘天中。明崇祯江苏');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历四十六年九月癸丑', '1618.11.14', '广州彗星见，出辰分角、亢度，其尾冲指奎、娄、壁度。先数夜有白气自东亘西，如刀形，与彗星并见，锋芒如帚，两月乃灭。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清太祖天命三年九月二十九日（甲寅）', '1618.11.15', '寅时，东南有一道白气，自地冲天，形如大刀，约长十五丈，宽丈余。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历四十六年九月乙卯', '1618.11.16', '有长星见于东南，其星自气一道，形如匹布，阔尺余，长二丈余，东至轸，西入翼，凡一十九日而灭。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历四十六年九月杪', '1618.11．中', '东南方苍白气一道，阔尺余，约长丈二尺每五更出现，浃旬始灭。清康熙江西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历四十六年九月', '1618.10.18-11.16', '化彗若蚩尤旗出角，数日乃灭。明天启江苏');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历四十六年秋', '1618．秋', '蚩尤旗见东方，每夜白气亘天，东西约三丈余，经月不散。有彗星长丈余，见于东北，光射中天。清顺治山东');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神寒万历四十六茎乎秋、冬', '1618.秋.冬', '彗星见，半载余乃止。清顺治浙江');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历四十六年十一月', '1618.11.17', '白气见于东方，形如匹布，弯曲如刀，其长亘天，月余乃没。明万历');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历四十六年十月四日', '1618.11.20', '大星大如拳，光长二丈余，日出不见，至十一月终方隐。明崇祯江苏');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历四十六年十月七日', '1618．11.23', '彗星出东方，长一丈。清康熙河北');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历四十六年十月乙丑', '1618.11.26', '彗星出于氐，如鸡弹大，长丈余，色苍白。尾指东南，后十数日，转指西北，扫犯太阳守星，入亢七度一十分。又三日、渐往西北方行，尾扫北斗、天璇、天玑、文昌、五车等星，逼紫垣，在亢六度，至次月十九日始灭。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历四十六年十月初旬', '1618.11.17-26', '有一巨星五更出现，长尺余，状似刀形，光影直射，至十二日乃止。清雍正广西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清太祖天命三年十月十一日（丙寅）', '1618.11.27', '五更时，东南更有白气自星出，约宽五尺，直冲明国，至十四日后不见。其出气之星每夜向北渐移，至二十九日直越北斗柄，自此以后不见。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历四十六年十月丁卯', '1618.11.28', '蚩尤旗见东方。民国河北');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历四十六年十月十三', '1618.11.29', '东南彗星见，如牛角形，数月方退。清雍正河北');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历四十六年十月二十二', '1618.12.8', '夜五更，冻方彗星如鸡子大，其状苍白色，尾指西北太（原文“大”）阳守星，渐往西北行，入亢宿七度一十分，起自西南。清康熙河北');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历四十六年十月壬申', '1618', '未几，白气现于东南，彗星出于氐宿。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历四十六年十月', '1618.11.17-12.16', '三鼓后东北方有了白光一道，直冲西南，亘数十丈，形如刀剑，锋芒可畏，天明方隐，如是者经月。明天启浙江');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历四十六年十一月', '1618.12.12-1619.1.16', '有星孛于轸，星赤如气如刀，长丈余，次年正月始灭。明崇祯广东');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历四十六年十二月', '1619.1.16-2.13', '彗星见东南，自丑时起至黎明，经一月余方晦。清康熙广东');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历四十六年冬', '1618．冬', '蚩尤旗长竟天，彗见东方。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历四十六年', '1618', '白彗出。明天启江苏');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历四十七年正月秒（杪）', '1619.2.中', '彗星见东方，长数百尺，月下射射，末曲而锐，未几而见于东北，又未几见于西。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历四十七年三月', '1619.4.14-5.13', '蚩尤旗见于遵义西方，经月乃灭。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历四十七年春', '1619．春', '彗星见，初时午，后射子，入北斗，逾二旬方灭。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历四十七年六月', '1619.7.11-8.9', '有彗星出东方，形如刀戟，申出戌没。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历四十七年八月', '1619.9.8-10.6', '长星、蚩尤旗并出东南翼轸楚分，至十月终乃没。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历四十七年秋', '1619．秋', '东南有白气，约三丈许，两月始消。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历四十七年十月', '1619.11.6-12.5', '每鸡鸣时，东北有白气如刀形，亘数百丈，半载方灭。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历四十七年', '1619', '天东北彗星见，自气亘天，若大刀状。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历四十八年（光宗泰昌元年）五月', '1620.6.1-29', '彗星见卯位。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历四十八年（光宗泰昌元年）夏', '1620．夏', '彗星现。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历四十八年（光宗泰昌元年）七月', '1620.7.29-8.27', '夜见白色，长数丈，在东北起如彗，西南指，或时下属地，至八月灭。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历四十八年（光宗泰昌元年）秋', '1620．秋', '有星如偃月刀，二更出自东方，天晓乃没，如是三月。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明光宗泰昌元年十月', '1620.10.25-11.23   ', '白气竞天，四旬乃灭。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明光宗泰昌元年十一月', '1620.11.24-12.23', '东方有白气，形如大刀，鸡鸣后见之，经数月不见。  ');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明光宗泰昌元年冬 ', '1620．冬', '彗星见，如蚩尤旗，两月余始灭。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明光宗泰昌元年', '1620', '东方白气烛天，经月不散。  ');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明熹宗天启元年春', '1621．春', '有白气起于翼、轸之次，初如彗，冬渐大，自东北亘天至西北，阅十月始没。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明熹宗天启元年', '1621', '白气起于冀、轸之野，初如彗，久之渐大，自东亘天至西北，如蚩尤旗，占三度半，至十月方没。 ');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明熹宗天启二年二月', '1622.3.12—4.10', '彗星见。');
