INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明熹宗夫启七年二月   ', '1627.3.17-4.15', '彗星见。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明熹宗天启七年 ', '1627', '彗星见。');
