INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明毅宗崇祯元年', '1628', '每夜半有彗星见，其芒长丈。 ');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明毅宗崇祯三年   ', '1630', '彗星见。');
