INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明毅宗崇祯十一年冬', '1638', '以彗见，停刑。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明毅宗崇祯十二年八月', '1639.8.29-9.26', '彗星入北斗。   ');
