INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明毅宗崇祯十二年秋', '1639．秋', '彗星见参分。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明毅宗崇祯十二年十月朔', '1639.10.26', '彗星见。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明毅宗崇祯十二年十月丙戌', '1639.10.27', '彗星见。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES (NULL, '1639', '有星孛于参星左足上，代星东。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明毅宗崇祯十二年十月', '1639.10.26-11.24', '彗星见。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明毅宗崇祯十二年十月庚寅', '1639', '中书舍人陈龙正上言：皇上因彗星求直言，并谕停刑。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明毅宗崇祯十二年冬', '1639．冬', '彗星出。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明毅宗崇祯十二年冬月', '1639.11.25-12.23', '夜，有彗星入北斗。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明毅宗崇祯十二年', '1639', '彗星见，诏停刑。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明毅宗崇祯十三年三月乙未', '1640', '户科给事中左懋第言：去岁彗星见，下诏停刑，而彗即夕消，何今日之不应也。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明毅宗崇祯十三年十月丙戌', '1640', '彗星见。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明毅宗崇祯十三年', '1640', '有白气一道，从东南方贯天者三月。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明庄烈帝崇祯十四年冬', '1641．冬', '彗星见。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明毅宗崇祯十六年八月', '1643.9.13-10.12', '有彗星见，长如匹练，经三旬乃没。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明毅宗崇祯十六年十月', '1643.11.11-12.10', '彗星见西方，白色，长数尺，阔尺，上下如一，月余乃没。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清世祖顺治元年三月', '1644.4.7—5.5', '东南蚩尤旗见。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清世祖顺治元年', '1644', '有星见于东方，形如长刀。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清世祖顺治二年六月二十七日', '1645.7.20', '夜，有星形如长刀。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清世祖顺治六年九月二十八日', '1649.11.2', '彗星见于东方，长三尺。每夜更初见，有顷即伏，至次月初三日乃灭。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清世祖顺治七年正月二十六日', '1650.2.26', '夜，崑山西方有白气如练，十余日始灭；萧县白气见西方，二十余日始灭。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清世祖顺治七年十一月', '1650.11.24-12.22', '彗星见。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清世祖顺治九年七月', '1652.8.4-9.2', '彗星见东南，至八月杪方没。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清世祖顺治九年十月', '1652.11.1-30', '彗星见，尾长二丈。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清世祖顺治九年十一月庚寅', '1652.11.22', '异星苍白气见于参，西北行入毕。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清世祖顺治九年十一月', '1652.12.1-30', '彗星见东南，二十余日乃灭。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清世祖顺治九年冬    ', '1652．冬', '有星朦胧如被掩状，行甚速，逾月乃灭。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清世祖顺治九年', '1652', '彗星见，长如匹练，月余乃散。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清世祖顺治十年十一月', '1653.12.20-1654.1.18', '彗星见南方。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清世祖顺治癸巳冬', '1653．冬', '蓬星出参之玉井，斜行没于天船。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清世祖顺治癸巳', '1653', '彗星丈长，南走。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清世祖顺治十四年十一月初三', '1657.12.7', '夜半，彗星见于东方，三夕始灭。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清世祖顺治十四年冬', '1657．冬', '彗星见东方，三夕没。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清世祖顺治十六年冬', '1659．冬', '彗星犯北斗。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清世祖顺治十八年正月初四日', '1661.2 .2', '夜，彗星见东南，指西北。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清世祖顺治十八年正月初八日   ', '1661.2.6', '彗星见，自东南扫至西北。    ');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清世祖顺治十八年正月', '1661.1.2-2.28', '彗星见。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙元年十一月', '1662.12.11-1663.1.8', '彗星见东南。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙二年十月朔', '1663.10.31', '彗星见。十一月经天，至十二月晦乃止。三年二月朔复见，越旬乃已。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙二年十月', '1663.10.31-11.29', '彗星见于翼轸之分，芒如帚，长二丈余，十二月消。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙二年十一月初旬', '1663.11.30-12.8', '彗星见，凡数月。是月又白气经天，见西方。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙二年冬', '1663．冬', '彗星见东南方，芒如帚，长二三丈。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙二年', '1663', '彗星见于轸，月乃灭。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙三年二月', '1664.2.27-3.26', '彗星出自东方，约长二、三丈，色白，尾指东南，又转西南，年余始灭。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙三年四月 ', '1664.4.26-5.24  ', '彗星见西南，七月又见东南。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙三年五月', '1664.5.25-6.23', '彗星见东北，长数十余丈，亥升辰没。后昏现中天。其芒东指。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙三年六月', '1664.6.24-7.22', '彗星见于东南。    ');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙三年夏 ', '1664．夏', '彗星见。   ');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙三年七月 ', '1664.8.21-9.19', '彗星见。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙三年八月', '1664.9.20-10.18', '彗星见，有白气如帚，自西北起，竟东南天半。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙三年九月初旬', '1664.10.19-28', '有星光气如烛，长尺余，随星而上，五鼓见，每夜如帚，至冬末，其光渐短，然犹有四、五寸许，渐转渐早，遂以晚见，至次年春月乃不见。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙三年九月二十六日', '1664.11.13', '彗星见南方，尾长八尺。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙三年九月二十七日', '1664.11.14', '彗星见轸九度，至十二月逆行十三宿，至娄度方灭。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙三年九月', '1664.10.19-11.17', '彗星见');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙三年秋', '1664．秋', '彗星见，星长二丈余，越数旬，始不见。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙三年十月己未朔', '1664.11.18', '彗星见轸宿度。丁亥，彗星见翼宿度，指西北方。十一月丁酉，彗星犯张宿。庚子，彗星犯井宿。丙午，彗星犯胃宿，尾指东北方。十二月壬戌，彗星在奎宿度，形渐小。丁卯，刑科给事中杨雍建疏言：本年十月初旬彗星见，及今五十余日，历一十三宿，……。四年二月己巳，彗星见女宿度。乙酉，彗星在壁宿一度三十分，尾长五尺余。三月己丑，彗星行入奎宿。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙三年十月初二', '1664.11.19', '彗星见于东方。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙三年十月初旬', '1664.11.18-11.27', '彗星子夜见于翼轸，直抵娄宿，经五十余日，酉行，历十有三宿。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙三年十月初十', '1664.11.27', '彗星见，至十一月不灭，光芒所射，随时转旋，或时长二，三尺，或至丈许。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙三年十月望', '1664.12.2', '彗星见东南，光芒如帚，历旬不灭。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙三年十月二十四日', '1664.12.11', '彗星见东北方，光数丈，至十一月，光渐短，月终方灭。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙三年十月二十六日', '1664.12.13', '彗星见，逾月乃没。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙三年十月', '1664.11.18-12.16', '彗星见于东南，至本年十二月间始没。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙三年十一月朔', '1664.12.17', '彗星出翼、轸分野，尾向西北，初长二丈余，渐减五、六尺，历五十余日，至娄而灭。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙三年十一月初五', '1664.12.21', '夜半，彗星出东南，上指数丈，光芒如帚，至十四日辛丑，彗芒下指东北，直至月终，渐缩而光淡。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙三年十一月初旬', '1664.12.17-26', '彗星见东南，指西北，长二丈许，旬余始灭。    ');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙三年十一月', '1664.12.17-1665.1.15', '彗星出于轸度，长数丈，气白如练，西北指，逆行，见四十余日，至娄度而不见。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙三年十二月  ', '1665.1.16-2.14', '彗星见，经六十日乃没。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙三年冬', '1664．冬', '孛星夜见，白气丈余，经月乃散。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙三年', '1664', '彗星见。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙四年正月十六日', '1665.3.1', '彗星见。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙四年正月中旬', '1665.2.25-3.4', '彗星见西北。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙四年二月初', '1665.3.17-21', '彗再见，旋没。  ');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙四年二月', '1665.3.17-4.13', '彗星见，阅三月乃灭。');
