INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙五年十月二十四日', '1666.11.20', '夜，彗星见于东南，长数丈，迨至中天，象白虹，冲于西北。');
