INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙七年正月二十日', '1668.3.2', '夜，彗星出自西方，长数丈，数夜乃没。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙七年正月二十一日', '1668.3.3', '有气见于西方，初见红，次见白，下有黑气，形如疋布，初昏时见，至三更余，夜夜见之，至三十日乃灭。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙七年正月二十四日', '1668.3.6', '夜，有白气如练，亘西南，直指东北，数日乃散。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙七年正月二十五日', '1668.3.7', '西南有白气如练，史所谓白眚也。二月初殁。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙七年正月二十六日', '1668.3.8', '西方白气如虹，从乾至坤；末锐而昂，十余日始灭。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙七年正月甲子（二十七日）', '1668.3.9', '西南白光，长六尺余，尾指东南，占曰天枪；二月乙亥，渐长至四丈余，尾扫天苑、九游，军井；丁亥，没。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙七年正月二十八日', '1668.3.10', '丁卯，彗星见，光芒下指，长数丈。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙七年正月二十八、九两日', '1668.3.10-11', '初昏，有白气如枪，长二、三丈，见长庚星上，随星转没。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙七年正月二十九日', '1668.3.11', '白气亘天，西出东指，越二十二日始灭。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙七年正月末旬', '1668.3.3-12', '有彗星如疋布著天，见于轸宿，阅三月余，历十五宿而始没。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙七年正月  ', '1608.2.1-3.12', '广平见白气亘天，西出指东，越二十日方灭；内丘夜见白气如银河，经五、六日方灭；温江有白气，自西直亘数十丈，下锐上阔，光如银，形如竹，经四昼夜方散；威县见白气亘天。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙七年二月二十三日', '1668.4.4', '夜，白气见于庚方，指东南，形如匹练。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙七年二月二十四日', '1668.4.5', '白气见西方。初昏白气光灼，尖直如枪，久而不幻，乃渐化。连见三昏，盖欃枪是也，末曲。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙七年二月二十七日', '1668.4.8', '初更，白气亘天，夜半方没，如此十数日。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙七年二月  ', '1668.3.13-4.10  ', '广州有白气如枪，长十余丈，四十日乃灭；武邑夜白气亘无，夜半始散；唐山见白气亘天。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙七年三月二十八日', '1668.5.8', '白气见于西南，彗长数十丈，经十余日不散。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙七年春', '1668．春', '夜，有白气亘天。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙七年五月', '1668.6.9-7.8', '有星见于东方，形如剑。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙七年六月十七日', '1668.7.25', '戌时初刻，彗星见。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙七年七月初七日', '1668.8.14', '彗星经天，越三月乃没。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙七年七月二十五日', '1668.9.1', '彗星见。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙七年七月二十六日', '1668.9.2', '西方白气如虹，从乾至坤，十余日始灭。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙七年七月', '1668.8.8-9.5', '高邑夜见白气如疋布，亘西方。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙七年八月 ', '1668.9.6-10.5', '昏，彗星孛于西方，月余乃伏。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙七年秋', '1668．秋', '白虹横亘天上，随月而没。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙七年十一月', '1668.12.4-1669.1.1', '长星见西方，光芒竟天，十日乃散。');
