INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙八年六月', '1669.6.28-7.29', '彗星见，经旬乃灭。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙八年', '1669', '彗星芒入斗口。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙九年', '1670', '彗星吐芒，亘天，入斗口。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙十年', '1671', '彗星夜见西方，长丈余。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙十一年四月十六日', '1672.5.12', '有长星如带亘天。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙十一年四月二十日', '1672.5.16', '白气横天，三昼夜乃止。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙十一年冬', '1672．冬', '彗星见西南，长丈余，数月乃灭。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙十一年', '1672', '西南长星射天河。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙十二年正月庚辰', '1673.2.25', '西北至东南，苍白气经天如匹布。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙十二年正月', '1673.2.17-3.17', '彗星出于西方，长一丈，凡一月。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙十二年二月癸巳', '1673.5.9', '异星见于娄，大如核桃，色白，尾长尺余，指东方；甲午，仍见。');
