INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙十三年十月', '1674.10.29-11.26', '西南彗星见。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙十三年', '1674', '彗星见于西方。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙十四年三月', '1675.3.26-4.24', '彗星屡见。夜昏有白气见东南，芒烟射斗。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙十五年十月', ' 1676.11.6-12.4', '彗星见，光芒丈余，经旬不灭。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙十五年冬', '1676．冬', '彗星见西北，五旬始散。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙十六年三月癸卯', '1677.4.28', '东北方有异星见于娄，体色光明润泽，尾长尺余，指西南，占曰含誉。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙十六年三月甲辰', '1677.4.29', '含誉星见。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙十七年七月', '1678.8.17-9.15', '彗星见东南。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙十七年', '1678', '西南一星，上带白气长丈余，形如扫帚，冲入北斗，月余而没。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙十八年七月二十八日', '1679.9.2', '有星见于西方，白气竞天，历尾箕，至牛、女，逾月乃没。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙十八年八月', '1679.9.5-10.4', '蓬州蚩尤旗见，凡四十日。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙十八年十月', '1679.11.3-12.2', '彗星见，芒如白练竞天之半，月余乃没。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙十八年十一月朔日', '1679.12.3', '彗星起西方，色苍白，渐长。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙十八年十一月', '1679.12.3-1680.1.1', '西方有一星如帚，有一小星黑浑而白，气自内喷出，扫及奎、娄，经月乃灭。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙十八年冬', '1679．冬', '彗星彗星晨见东方，长数丈，两月余始灭。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙庚申年六月', '1680.6.26-7.25', '有星孛于西南，其形如剑，长数十丈，经月乃隐。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙十九年七月', '1680.7.26-8.23', '彗星见于西方，数月乃灭。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙十九年八月朔', '1680.8.24', '长星竞天如白练。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙十九年闰八月', '1680.9.23-10.21', '彗星见西南递东北，两阅月而没。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙十九年九月', '1680.10.22-11.20', '彗星见西方，有气如练，长五六丈。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙十九年十月初一', '1680.11.21', '夜，有星出乾方，其白气炎芒贯天，长数百丈，月余而灭。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙十九年十月初二', '1680.11.22', '夜，彗星见东方，尾如帚，十余夜乃灭。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙十九年十月初三（戊子）', '1680.11.23', '夜，西南方白气亘天，半月不散，每夜移至正西而息。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙十九年十月十三日', '1680.12.3', '彗星见东方，数夜乃隐。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙十九年十月', '1680.11.21-12.20', '全椒见白气于西方，月余始灭。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙十九年（庚申）十一月丙辰朔', '1680.12.21', '彗星见，诏求直言。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙十九年十一月初二日', '1680.12.22', '镇洋西方见白气亘天，长数丈、移时乃灭；临淄见白气自西而东。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙十九年十一月戊午至辛酉', '1680.12.23-26', '西南苍白气一道，宽尺余，锐指东北，长三丈余，渐长至四丈余。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙十九年十一月初三日', '1680.12.23', '西方出白气如练，直指东北方，初六夜长星见，光芒数十尺，如剑，仍指西北，经一月始退。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙十九年十一月初四日', '1680.12.24', '温州夜见白气如练，长十余丈，月余始灭。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙十九年十一月初五日', '1680.12.25', '彗星夕见西南递东北，白光亘天，经两月始灭。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙十九年十一月初', '1680.12.21-25', '西南方有星两头皆锐。（揭阳县）');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙十九年十一月十三日', '1681.1.2', '天布星见西南。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙十九年十一月二十三日', '1681.1.12', '一星见南方，长亘天，阔如匹布，旬日乃隐。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙十九年十一月', '1680.12.21-1681.1.19', '蚩尤旗见，自西北竞东南，月余乃灭。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙十九年冬', '1680．冬', '星有芒，经天如白练，月余乃散。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙十九年', '1680', '有彗星自东北至西南，其形如箒。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙二十年春', '1681．春', '彗星见西方。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙二十年五月', '1681.6.16-7.14', '彗星再见，芒微而短，七月方没。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙二十年六月二十一日', '1681.8.4', '夜，望江见白气亘天，至八月十一日方灭。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙二十年七月初七', '1681.8.20', '西方酉位一星光焰冲天，其焰白，光若剑，插入北斗之形如是。申酉月，每夜光焰惊人。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙二十年八月', '1681.9.12-10.10', '蚩尤旗又见。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙二十年九月', '1681.10.11-11.9', '彗星见。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙二十年十月', '1681.11.10-12.9', '白气见西方，长数丈，历数宿，经数十日始灭。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙二十年十一月初四', '1681.12.13', '酉时，西方白气亘天如匹练，至十二月十五乃灭。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙二十年十一月', '1681.12.10-1682.1.8', '山阳见白气亘天，一月始灭；汉中西方见白气亘天如练。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙二十年冬', '1681．冬', '白气如匹练，渐长丈余，至竟天，直射帝座，一月方灭。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙二十年', '1681', '彗星见西北。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙二十一年七月十五日', '1682.8.17', '彗星再现于甲卯方，至二十夜不灭。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙二十一年七月己巳', '1682.8.26', '彗星见井宿度，尾长二尺余。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙二十一年（壬戌）七月二十七日', '1682.8.29', '有星孛于西方，长竟天。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙二十一年七月末旬', '1682.8.23-9.1', '彗星东西并见。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙二十一年七月', '1682.8.3-9.1', '彗星见。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙二十一年八月初一日', '1682.9.2', '彗星昼见，十一日始灭。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙二十一年八月初八日', '1682.9.9', '彗星见于角，次于房，经月乃灭。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙二十一年八月', '1682.9.2-30', '彗星见，日夕见于西方，长丈余。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙二十一年九月', '1682.10.1-29', '有彗星见于西。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙二十一年秋', '1682．秋', '有彗星出西北。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙二十一年', '1682', '有星孛于西方，长竟天。 ');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙二十二年闰六月庚戌', '1683.8.2', '异星见于五车北八谷东，色白，往西南逆行；戊辰，入五车。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙二十二年', '1683', '彗星长竟半天，直抵参井。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙二十三年三月', '1684.4.15-5.13', '有彗星见于西南，数月始散。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙二十三年五月甲申', '1684.6.26', '异星见太微垣，东属轸，色白明大，往东北顺行；乙酉，行四度余，至右摄提下。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙二十四年八月', '1685.8.30-9.27', '西方彗星见，白气如练，长数十丈，至五更散。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙二十五年七月庚寅', '1686.8.26', '异星见东方，近地平，色白，东行不急；丁酉，凡行十六度，至柳，微有尾迹；壬寅，至星，渐没。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙二十五年七月', '1686.8.19-9.17', '有星见于南方，光芒长数尺，数日乃消。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙二十八年十月二十九日', '1689.12.10', '申时，有星吐白气于东南。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙二十八年十月', '1689.11.12-12.11', '彗星见东南方。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙二十八年十一月', '1689.12.12-1690.1.9', '彗星见。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙二十八年冬', '1689．冬', '思明府见白气一道，自东南起直冲数丈，月余始息。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙三十四年九月朔', '1695.10.8', '晨，有星见于东南方，吐芒形如扁桃，经旬渐灭。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙三十四年九月十五日后', '1695.10.22', '五更时，有彗星现，自东天起自天河，至天色已明其影方没。至十月初八夜尚未息。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙三十六年正月二十九', '1697.2.20', '夜，有星如箒在天中，尾向西南。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙三十六年二月', '1697.2.21-3.22', '彗星见东方。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙三十六年', '1697', '彗见东，数夜没。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙三十八年', '1699', '彗星见西方，如匹练，长贯于东，连三夜始没。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙三十九年九月中旬', '1700.10.22-31', '东南白气如练，直指西北，六七日始灭。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙三十九年九月', '1700.10.12-11.10', '江夏见白气如练，六七日始灭。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙四十年', '1701', '彗星见。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙四十一年正月二十九日', '1702.2.25', '酉时，彗星见。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙四十一年二月甲寅', '1702.2.28', '酉时，西南白云一道，长二丈余，宽尺余，穿天仓、天苑，入地平，至丁巳，长三丈余。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙四十一年二月初三日', '1702.2.29', '有白气长丈余，自西起，约更余而灭。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙四十一年二月', '1702.2.27-3.27', '沛县见白气于西方。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙四十一年', '1702', '长星见于西方。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙四十七年七月', '1708.7.29-8.26', '夜，有星如彗，初出自西方，白霞如线，渐大而冲西南。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙四十八年五月', '1709.6.8-7.6', '彗星见卯位。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙四十九年', '1710', '彗星见。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙六十一年八月', '1722.9.11-10.9', '彗星见于南方。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙六十一年', '1722', '彗星见于南方。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清世宗雍正元年七月', '1723.8.1-30', '彗星见于东。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清世宗雍正元年九月己丑', '1723.10.11', '异星见弧矢下，色白，体微，芒长尺余，指西北，逆行至井。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清世宗雍正元年九月十八', '1723.10.16', '彗星出牛、女间，至二十四灭。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清世宗雍正元年九月', '1723.9.29-10.28', '夜，有星见南方，尾如帚，数夜乃灭。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清世宗雍正二年', '1724', '彗星见。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清世宗雍正四年', '1726', '彗星见。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清世宗雍正十三年', '1735', '彗星见于西，其尾长尺余，自夏至冬，后不见。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清高宗乾隆二年六月丁卯', '1737.7.8', '异星出右更东，色白，属娄，向西南行；丙子，仍见。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清高宗乾隆六年二月', '1741.3.15-4.15', '彗星见西方，长丈余，七月复见，至十二月始灭。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清高宗乾隆六年四月', '1741.5.15-6.12', '西北有星，吐光如彗，九月移近西南，及冬而没。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清高宗乾隆六年七月', '1741.8.11-9.9', '彗星见西方，长丈余。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清高宗乾隆六年八月', '1741.9.10-10.9', '彗星见，至十月灭。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清高宗乾隆六年十二月十八日', '1742.1.24', '有星如彗芒角，起西北隅，上冲室尾，历次年二月初乃止。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清高宗乾隆七年正月上元后', '1742.2.19后', '彗星见于东北，至二月上旬乃伏。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清高宗乾隆七年正月丙戌', '1742.3.2', '异星见于斗宿之次，在天弁第二星之上，其色黄白，向西北逆行，四十余日隐伏。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清高宗乾隆七年正月丁亥', '1742.3.3', '异星见东南方；戊子，出地二十七度余，大如弹丸，色黄，尾长四尺余，指西南，属丑官，在天市垣徐星外，逆行四旬余不见。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清高宗乾隆七年正月', '1742.2.5-3.6', '彗星见东北。');
