INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清高宗乾隆八年十二月十八日', '1744.2.1', '彗星见西方，尾向东，白色，光长丈余，十数日始没。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清高宗乾隆八年十二月二十三日', '1744.2.6', '奎宿度内有星黄赤，本大如小酒杯，末长约六、七寸，大如笔管，二十四至二十八联夜阴雨，暗黑不见。及二十九夜又见之，则进于壁宿度内，末扫东方，其长丈余，大如帚，来年正月天将硗时，犹有见之者。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清高宗乾隆八年十二月癸酉', '1744.2.7', '谕，“昨召见大学士陈世倌，据奏近日彗星见。……”');
