INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清高宗乾隆二十四年十一月戊辰', '1760.1.9', '异星见东南方，在井第四星下，大如榛子，色苍黄，向西北行，癸酉，行四度，在胃，微有尾迹；十二月丁丑朔，全消。');
