INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清高宗乾隆三十三年', '1768', '彗星见东方，竟年不灭。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清高宗乾隆三十四年夏', '1769．夏', '彗星见。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清高宗乾隆三十四年六月', '1769.7.3-8.1', '彗见东方，七月没。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清高宗乾隆三十四年七月二十三日', '1769.8.24', '子时，东有星如彗指西。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清高宗乾隆三十四年七月二十五日', '1769.8.26', '夜，东南一星，吐白气如虹，长数丈，月余渐没。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清高宗乾隆三十四年七月二十七日（甲辰）', '1769.8.28', '东南方彗星见，星尾指向西南。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清高宗乾隆三十四年七月', '1769.8.2-30', '夜，彗星出东北，长丈许，西南射。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清高宗乾隆三十四年八月', '1769.8.31-9.29', '有星晨见于东，光二丈余。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清高宗乾隆三十四年九月', '1769.9.30-10.28', '彗星见于东方，连夜不灭。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清高宗乾隆三十四年秋', '1769．秋', '彗星见。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清高宗乾隆三十四年十月', '1769.10.29-11.27', '据钦天监奏；七月内彗星见，光照西南，经日度遂灭。今又见西南，光照东北。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清高宗乾隆三十四年', '1769', '彗星出，其长数丈。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清高宗乾隆三十五年闰五月己酉', '1770.6.26', '异星见东南方，在天弁第一星西，大如弹丸，色苍黄；癸丑，向北行三十二度；乙丑，不见。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清高宗乾隆三十五年夏', '1770．夏', '彗星见于东方，长四丈许。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清高宗乾隆三十五年', '1770', '彗星见于东方，长四丈许，按此星光如匹练，盖长星也。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清高宗乾隆三十五年八月', '1770.9.19-10.19', '彗星见。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清高宗乾隆三十五年秋', '1770．秋', '彗星见，光芒竟天，数月乃没。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清高宗乾隆三十五年十一月乙丑', '1771.1.8', '彗星见东南方，长尺余；丙寅，在柳第二星下；戊辰，色苍白，尾指东南，每日向西北行十余度；庚午，微暗；辛未，全消。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清高宗乾隆三十五年十一月', '1770.12.17-1771.1.15', '彗星见于柳宿之次，在第一星之下，色苍白，尾迹长尺余，指南，每夜向北行十余度，七日隐伏。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清高宗乾隆三十八年四月', '1773.5.21-6.19', '有星如帚，长丈余，见司境东北角地。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清高宗乾隆三十八年寎月', '1773.3.22-4.21', '有星大于鸡子，首指东北，尾指西南，光芒散溢，形宛如帚，长数丈，三更起，五更没，至八月始敛。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清高宗乾隆三十八年八月', '1773.9.16-10.15', '彗星见。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清高宗乾隆四十一年七月', '1776.8.14-9.12', '长星见，形如匹练，光芒五、六丈，横亘东南，三更始出，五更不见，如是者计十余夜。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清高宗乾隆四十三年六月', '1778.6.24-7.23', '彗星长数十丈。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清高宗乾隆四十三年', '1778', '彗星长数十丈。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清高宗乾隆四十六年七月', '1781.8.19-9.17', '崖州彗星见。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清高宗乾隆四十六年', '1781', '彗星见。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清高宗乾隆四十九年冬', '1784．冬', '彗星昏见于西南方。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清高宗乾隆六十年七月', '1795.8.15-9.12', '他郎彗星见于西北，至十一月方灭。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清仁宗嘉庆元年五月', '1796.6.5-7.4', '彗星见于东方。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清仁宗嘉庆五年十月', '1800.11.17-12.15', '彗星见于西北。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清仁宗嘉庆五年', '1800', '自夏而秋，彗星见于西南。   ');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清仁宗嘉庆十年七月', '1805.8.24-9.22', '有星孛于西，数月方没。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清仁宗嘉庆十一年六月', '1806.7.16-8.13', '彗星见。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清仁宗嘉庆十二年七月初', '1807.8.4-8', '天市垣东藩燕宿，有光如风吹蜡炬，铄如金，历九月止。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清仁宗嘉庆十二年七月', '1807.8.4-9.1', '彗星见西方，至十月始没。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清仁宗嘉庆十二年八月望后  ', '1807.9.16后', '有星孛于西方市垣，至十月望后始伏。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清仁宗嘉庆十二年八月二十九日', '1807.9.30', '晚，莆田彗星见于西方，苦长尺余，其光动摇，初更即隐，每夜如是，至九月二十九日方灭。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清仁宗嘉庆十二年', '1807', '彗星见西方。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清仁宗嘉庆十三年八月', '1808.9.20-10.19', '彗星见西北，长数丈，白气亘天，数月不灭。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清仁宗嘉庆十三年九月', '1808.10.20-11.17', '彗星见。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清仁宗嘉庆十三年冬', '1808.12.22-1809.1.11', '彗星见于西方，经月始散。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清仁宗嘉庆十四年七月', '1809.8.11-9.9', '彗星见。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清仁宗嘉庆十四年八月', '1809.9.10-10.8', '福鼎彗星见西北方。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清仁宗嘉庆十四年十月朔日', '1809.11.8', '彗见西方，长数丈，经月始已。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清仁宗嘉庆十四年', '1809', '彗星见。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清仁宗嘉庆十五年七月初六夜', '1810.8.5', '彗星如帚天河边，射入西，长丈余。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清仁宗嘉庆十五年八月', '1810.8.30-9.28', '有星如帚，见于西方，数月方没。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清仁宗嘉庆十五年', '1810', '彗星见。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清仁宗嘉庆十六年三月', '1811.3.24-4.22', '彗星见，起西北，浙南，徘徊牛、斗间，秋、冬过天汉。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清仁宗嘉庆十六年春', '1811．春', '彗星见西南，经月乃隐。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清仁宗嘉庆十六年六月二十三日', '1811.8.11', '夜，西北有星，芒溢三四丈，倏敛倏放，光焰如火，直指斗柄之第三星，黄昏即起；后复有星见东北，五更始起，光芒也钜，遥射斗宫，至十一月俱灭。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清仁宗嘉庆十六年六月', '1811.7.20-8.18', '彗星见。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清仁宗嘉庆十六年夏初 ', '1811．夏初', '是年彗星见，光长丈余，自夏初至秋末没。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清仁宗嘉庆十六年七月初', '1811.8.19-23', '彗星见斗台间，约长两丈，阔三尺，其光丝丝如发，光中望天上列星如隔薄纱一层，每夜浙移东南，百余日而隐。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清仁宗嘉庆十六年七月', '1811.8.19-9.17', '彗星见于北斗，前芒指紫微垣而退，循天河而行，至婺女而灭。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清仁宗嘉庆十六年八月初六', '1811.9.23', '彗星见于西北方，至十一月始灭。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清仁宗嘉庆十六年八月既望', '1811.10.2', '初昏，星又孛于斗柄，指太微垣，西行渡汉越河鼓，至十月乃伏。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清仁宗嘉庆十六年八月十六', '1811.10.3', '夜，彗星见，初出于天市垣，芒丈余，赤色，次晚变淡白色，状如帚，长尺余，渐移，至十一月渡过天汉、河鼓星侧，次年正月没。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清仁宗嘉庆十六年八月十七', '1811.10.4', '有彗星见于西，尾指东南，越月乃隐。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清仁宗嘉庆十六年八月二十一日', '1811.10.8', '数日每夜，彗星见西方。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清仁宗嘉庆十六年八月二十五日', '1811.10.12', '夜，莆田彗星见西北隅，光芒丈余，两月乃灭。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清仁宗嘉庆十六年八月', '1811.9.18-10.16', '彗星见于西方。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清仁宗嘉庆十六年九月', '1811.10.17-11.15', '彗星见于西方，月余方灭。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清仁宗嘉庆十六年九月中', '1811.10.27-11.5', '有星见于西方，光焰丈余，经月渐敛。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清仁宗嘉庆十六年秋', '1811．秋', '有星见于西方，光芒数丈，其形如帚，数夜方灭。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清仁宗嘉庆十六年', '1811', '彗星见，光芒丈余，自夏初至秋末始没。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清仁宗嘉庆十七年春', '1812．春', '彗星见于北斗之右，渐移而南，长数丈，至秋末始没。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清仁宗嘉庆十七年六月', '1812.7.9-8.6', '彗星见，长五尺许。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清仁宗嘉庆十七年夏', '1812．夏', '有妖星出，降娄陬訾之次，数月始灭。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清仁宗嘉庆十七年七月', '1812.8.7-9.5', '彗星见西南方，入天市垣，两月始灭。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清仁宗嘉庆十七年七月下旬', '1812.8.27-9.5', '欃枪星见于西方，由北而南，数月始没。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清仁宗嘉庆十七年七、八月间', '1812.8.7-10.4', '有彗星长十余丈，光芒亘天。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清仁宗嘉庆十七年八月', '1812.9.6-10.4', '彗星见（景东厅志、思乐县志）');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清仁宗嘉庆十七年九月初四日', '1812.10.8', '孛星见于西方，月余不见。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清仁宗嘉庆十七年九月', '1812.10.5-11.3', '彗星见于西北。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清仁宗嘉庆十七年秋', '1812.秋', '彗星见，光芒数丈，月余始没。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清仁宗嘉庆十七年九、十月间', '1812.10.5-12.3', '彗星见于天，两月乃灭。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清仁宗嘉庆十七年十月', '1812.11.4-12.3', '彗星见。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清仁宗嘉庆十七年', '1812', '彗星见于西北。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清仁宗嘉庆十八年春', '1813．春', '彗星见于西北，光芒数丈，至秋方没。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清仁宗嘉庆十八年六月', '1813.6.28-7.26', '彗见，至九月始灭。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清仁宗嘉庆十八年夏', '1813．夏', '彗星见。');
