INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清世祖顺治二年六月', '(1645.6.24-7.22)', '终夜流星往来如织。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清世祖顺治二年闰六月朔', '1645.7.23', '夜将半，天上小星散落如雪。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清世祖顺治二年闰六月十五日', '1645.8.6', '夜，星陨如雨。');
