INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明世宗嘉靖五年八月一日', '1526.9.7', '酉时，流星落西南，大如杯碗，光明烛地。明嘉靖山东');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明世宗嘉靖五年十月壬戌', '1526.11.26', '蔚州卫有星陨如升，天鼓鸣．');
