INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明世宗嘉靖二十年七月丙戌、', '1541.7.24', '有火毡大如斗，陨左军都督府中门左，良久灭。');
