INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历二十二年正月戊戌', '1594.3.10', '保定青山口，有飞星甚大，余光若彗，长二十余丈。');
