INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙十五年八月', '1676.9.8-io.6', '大星坠于西方，有火光数丈，移时方散。清康熙河北');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙十五年九月丁未', '1676.11.3', '流星如桃，出外屏，入坟墓，色青黄，有光，有尾迹。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙十六年正月十二日', '1677.2.13', '有星自南面西北，赤光腾天，有声如雷。清康熙河北');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙十六年囚月丁未朔', '1677.5.2', '流星如桃，出紫微垣东云中，往北行，不著色，映地有光。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙十六年夏', '1677．夏', '有大星流入东北，光赤如日。清康熙山东');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙十六年七月初二', '1677.7.13', '申时，有星自西南来，陨予平都山后，其声如雷，火光数十丈，照耀数里。清康熙四川');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙十六年八月甲寅', '1677.9.6', '流星如李，出常陈，色青赤，入氐。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙十六年九月十九日', '1677.10.15', '初昏，弧光团圆，如升，横飞过天。清康熙广东');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙十六年九月己亥', '1677.10.21', '酉时，流星如盏，自正北下行，色赤白，尾迹如蛇，有光。流星');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙十七年正月丙子', '1678.1.26', '流星如李，出参宿，色青白，入九游。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙十七年二月九日', '1678.3.1', '武定星陨，霈化、海丰天鼓鸣，星陨，火光烛地。清康熙山东');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙十七年五月庚申', '1678.7.9', '辰时，流星如弹丸，出西南，色赤，有光。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙十七年七月戊午', '1678.9.5', '酉时，流星如弹丸，出西北，色青。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙十七年七月', '1678.8.17-9.15', '泽州有大星随五色云陨，顷之，白气上升。清雍正');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙十七年九月辛丑', '1678.10.18', '流星如桃，出昴宿，入阁道，色青白，有光，有尾迹。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙十七年十一月', '1678.12.14-1679.1.11', '流星自东南飞坠，有声如雷。清乾隆江西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙十八年七月十二日', '1679.8.17', '星陨于霈化。清康熙山东');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙十八年七月己未', '1679.9.1', '沆星如桃，出勾陈，入文昌，色青黄，有光。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙十八年八月初', '1679.9.5-9.14', '夜，流星犯斗。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣袒康熙十八年八月十七日', '1679.9.21', '申时，有大星，由西北落东南，声如雷。清康熙湖南');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙十八年十月庚午', '1679.11.11', '流星如李，出右旗，后小星随之，色青赤，入候星。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙十八年十一月十四日', '167q.12.16', '戌时，天方阴晦，忽云际有光如火而白，大席，照地纤毫皆见，如中有声似雷非雷，殷殷不绝，稍顷作霹雳声，其光散为火炬，随声向东北而止。清康熙湖北');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙十九年庚申正月十五日', '1680.2.14', '夜，有星坠地，色赤如火。（补纂参武壤野史）清光绪浙江');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙十九年四月二十八月', '1680.5.26', '丑时，白气自西亘东，有声如雷，清乾隆福建');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙十九年五月壬辰', '1680.5.31', '流星如桃，出摄提，入房，色青黄，有光，有尾迹。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙十九年六月癸酉', '1680.7.11', '酉时，流星如盏，自西南向东北，声如雷，尾迹如匹布。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙十九年闰八月己酉', '1680.10.15', '流星如桃，出外屏，入建星，前小后大，色赤黄，有光，有尾迹。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙十九年十月三十日', '1680.12.20', '右犁星着地。清康熙上海');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙十九年十一月二十九日（晦）', '1681.1.19', '夜，妖星头红尾绿，自南飞过西北，坠下，大响一声，人皆闻之。清康熙广西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙十九年十二月甲午', '1681.1.28', '流星如桃，出钩陈，入大陵，色青，有光，有尾迹。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙二十一年正月戊辰', '1682.2.26', '流星如桃，出大陵，入壁，色青白，有光。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙二十一年六月乙巳', '1682.8.2', '流星如李，出天市垣，色青白，从心、尾之间。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙二十一年七月初六', '1682.8.8', '亥时，流星如毡，自西北起，徐行向东南，高不过屋，芒角四射，其光无微不照，随发天鼓三声，相传为天狗星：清康熙江苏');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('流星', '1682.8.17', '夜二鼓，吕蒙渡口，渔人见天一半异常，赤色，映物皆红，光如白昼，空中如霹雳震响，经久方歇，似一缕红丝收灭，天色乃晦，漠如故。清乾隆湖北');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙二十一年十一月戊申', '1682.12.3', '流星如李，出东井，色赤黄，入上台。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙二十一年冬至前一日', '1682.12.20', '黎明，有火星自西北流至西南，其声如雷。清康熙浙江');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙二十一年十一月二十六日', '1682.12.24', '戌时，陨星大如斗，声若雷震，烂若月光，须臾落于西南。清康熙山东');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙二十二年正月', '1683.1.27-2.25', '陨星。清乾隆河北');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙二十二年二月丁丑', '1683.3.2', '流星如桃，出明堂，入轸，色青白，有光，有尾迹。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙二十二年五月十一日', '1683.6.5', '夜，有大星西坠，下各有小星随之。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙二十二年六月二十六日', '1683.7.20', '有大星陨于诲，其声如雷。清康熙');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙二十二年八月八日', '1683.9.28', '有大星如斗，陨于东南。民国');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙二十二年冬月初一', '1683.12.18', '渠县礼义山铜鱼门侧，乡民闻响声如雷，云中有人坠下，其身正红，自南而北，移时乃灭。清康熙四川');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙二十三年二月己丑', '1684', '流星如桃，出七星，入地，小星随之，色青，有光。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('l', '1684.10.24', '寅时，地雷响于西南，星陨，光焰掩月，随天鼓鸣于东南。清康熙河北');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙二十三年十月晦', '1684.12.5', '夕，天鼓鸣，有火光自西北流至西南。清康熙浙江');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('990', '1685.4.11', '流星如李，出建星，色白，入垒壁阵。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙二十五年六月戊午', '1686.7.25', '泽州大阳镇天鼓鸣，一大星陨，明烛数里，占者谓：天堡星也。清雍正');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙二十五年六月二十八日', '1686.8.16', '戌时，有星自东南起，大如斗，明如日，穿南斗，入天河不见，既闻天鼓响四、五阵。清雍正山东');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙二十五年九月壬午朔', '1686.10.17', '流星如李，出胃宿，色赤，入东壁。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙二十五年十一月壬午', '1686.12.16', '流星如盏，出左枢，色白，至蜀没，尾长竟天。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙二十五年十二月戊寅', '1687.2.10', '流星如盏，出轸宿，色青黄，入骑官。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙二十六年七月癸未', '1687.8.14', '流星如李，出垒壁阵，色青白，入天纪，自东南至西北竟天，有光，有尾迹。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙二十七年正月', '1688.2.2-3.1', '陨星。清康熙河北');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙二十七年', '1688', '天鼓鸣，星陨。清康熙山东');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙二十八年二月乙卯', '1689.3.8', '流星如李，出东次将，色白、入氐，有光，有尾迹。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙二十八年二月十七日', '1689.3.8', '戌时，有流星，圆光如日，自东南流坠西北，掷地有声。清康熙山西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙二十八年夏', '1689．夏', '星陨，时辰刻，大如斗，自北而南，光照室，声若鼓，山鸱飞且鸣。清咸丰浙江');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙=十九年二月丁亥', '1690.4.4', '流星如盏，出河鼓，色黄，入尾。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('请圣祖康熙二十九年七月二十九日', '1690.9.2', '戌时，天鼓鸣，星陨东北。清康熙河北');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙二十九年七月', '1690.8.5．-9.2', '星陨有声。（仙潭文献）清同治浙江');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('精圣祖康熙二十九年八月初一', '1690.9.3', '星陨正南。清康熙河北');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙=十九年八月乙亥', '1690.9.19', '流星如桃，出参宿，入弧矢，色青。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙三十年十月丁未', '1691.11.15', '流星如李，出胃宿，色白，入天仓，有光，有尾迹。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙三十一年正月己卵', '1692.2.16', '流星如李，出贯索，色青，入亢，有光，有尾迹。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('蒲圣祖康熙三十一年六月朔', '1692.7.14', '大星如月，自西南流入东北，有声如雷。清康熙山东');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙三十一年六月', '1692.7.14-8.11', '陨星。民国山东');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙三十一年九月癸丑', '1692.10.16', '流星如李，出东井，色白，入天苑，有光，有尾迹。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙三十二年二月癸卯', '1693.4.4', '流星如桃，出房宿，入尾，色青。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙三十二年六月十七日', '1693.7.19', '有星坠于西北方，有声若雷，火光四映。清康熙河南');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙三十二年六月', '1693.7.3-8.1', '西南有声如雷，星陨于何家营。清乾隆河南');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙三十二年七月辛亥', '1693.8.10', '流星如李，出王良，色黄，入五车，有光，有尾迹。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙三十三年三月壬戌', '1694.4.18', '流星如桃，出女宿，入危，色赤，有光，有尾迹。');
