INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清世宗雍正五年八月庚子（十七日）', '1727.10.1', '有大星如斗，从东北经西南，其光烛天。清道光湖南');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清世宗雍正六年．', '1728', '星陨十里铺。清乾隆山东');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清世宗雍正八年六月十七日', '1730.7.31', '有流星自东南而西北，大如升口，红光如炬，肃肃有声。清雍正河北');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清世宗雍正八年九月', '1730.10.12-11.9', '大星陨青龙山后。清道光');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('请世宗雍正九年四月', '1731.5.6-6.4', '星陨，自西而东，光烛天，声大震。清嘉庆云南');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清世宗雍正十年四月八日', '1732.5.2', '夜，有流星从艮、震超，光烛天，落于洮西坤方。清乾隆甘肃');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清世宗雍正九年闰五月二十七日', '1731-1732', '夜，南官有白气一道，南行有声。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清也宗雍正十年闰五月二十七日', '1732.7.18', '夜，东北白气一道，南行有声，盖白祥也，亦鼓妖也。清道光河北');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清世宗雍正十年十月', '1732.11.18-12.16', '大雷，有大星色赤，坠地。（釜涌陈叟遗钞）清光绪广东');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清世宗雍正十一年八月二十七日', '1733.10.4', '夜，有大星缀小星无数，孛于西南，光明如昼，散有尾，夭鼓鸣，逾对方止。清乾隆湖北');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清世宗雍正十二年四月丁未', '1734.5.4', '亥时，东北方有流星如月。清乾隆安徽');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清世宗雍正十二年七月癸巳', '1734.8.18', '戌时，流星大如斗，自西至东北清乾隆安徽');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清世宗雍正十三年八月', '1735.9.16-10.15', '有大星陨于西方，火光照耀，声响若雷。清道光广西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清高宗乾隆元年五月壬戌', '1736.7.7', '午正，流星如弹丸，自西南方下行，色黄。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清高宗乾隆元年七月癸卯', '1736.8.17', '戌初初刻，流星如弹丸，出东北，高五十余度，下行至二十余度没，色白，有光，有尾迹，皆以昼见。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清高宗乾隆元年九月初十', '1736.10.14', '日始入，有星自西方坠，初青白色，后变红，半时始灭。清乾隆福建');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清高宗乾隆元年九月', '1736.10.5-11.2', '星陨于西南方，白气如云，经天不散。清乾隆福建');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清高宗乾隆元年十二月二十四日', '1737.1.24', '酉时，天鼓鸣，有星自东南陨于西北，隆隆有声。清乾隆山东');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清高宗乾隆二年三月十八日', '1737.4.17', '酉时，有火大径尺，烛天，自西而南坠下，已而有声轰轰然，或日s天铳响也。城乡各处共见之。清乾隆福建');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清高宗乾隆二年四月十八日', '1737.5.17', '酉时，有星大如月，光烛于地，自东流于西北。清乾隆江西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清高宗乾隆二年十月', '1737.11.22-12.20', '天鼓鸣，星陨。清乾隆山东');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清高宗乾隆五年四月十九日', '1740.5.14', '有星如电，光自南孛于东北。清乾隆湖北');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清高宗乾隆五年十一月二十七日', '1741.1.14', '夜，星坠东方，光芒有角。清乾隆福建');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清高宗乾隆六年九月二十一日', '1741.10.30', '星陨，自东南而西北，声若雷鸣。清乾隆河北');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清高宗乾隆六年十月', '1741.11.8-12.7', '有星大于斗，色赤，坠于饶平县城之北。清乾隆广东');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清高宗乾隆七年二月', '1742.3.7-4.4', '有星见于东南，流光烛地。清乾隆河北');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清高宗乾隆七年八月二十七日', '1742.9.25', '晚，有流星自东过西，大如斗，红光彻地。清乾隆云南');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清高宗乾隆八年闰四月初八', '1743.5.31', '戌时，有星自东北流于西南，光明照地，顷之，声震如雷。清乾隆山西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清高宗乾隆八年六月乙卯', '1743.7.24', '未正，流星如核桃，出东北云中，下行，色黄，入云，微有尾迹，以昼觅。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清高宗乾隆十年正月十二日', '1745.2.12', '有大星晨坠，自东而西，顷之，有声。清乾隆山西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清高宗乾隆十年三月十五日', '1745.4.16', '星陨于东，顷之，无云而雷。清道光河北');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清高宗乾隆十一年十二月', '1747.1.11-2.8', '有星陨于河，声如霄，光如电。清乾隆江苏');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清高宗乾隆十二年三月二十二日', '1747.5.1', '寅时，有闻天上有声，仰视之，一道光辉，自西贯东，光芒射地。清乾隆福建');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清高宗乾隆十二年四月初九', '1747.5.17', '夜半，有白光如练，起西南隅，流向西北．至空中聚为五色，震响一声乃散。清嘉庆湖南');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清高宗乾隆十二年五月初七', '1747.6.4', '天鼓鸣，有光如电。（芮城县）清乾隆山西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清高宗乾隆十二年五月', '1747.6.8-7.7', '昼陨星，天鼓鸣。清乾隆山西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清高宗乾隆十二年十月戊辰', '1747.11.13', '二更，流星如鸡子，出阁道，东北行，色青，入云，有光，有尾迹。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清高宗乾隆十二年十一月二十五日', '1747.12.22', '哭鼓鸣，有光掘电。（芮城县）清乾隆山西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清高宗乾隆十二年冬', '1747．冬', '流星坠地，有火光，声如雷。清道光山东');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清高宗乾隆十三年八月初十', '1748.10.2', '夜，有流星大如月，自西而北，过处有声。清乾隆安徽');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清高宗乾隆十三年八月二十四日', '1748.10.16', '酉刻，有流星经天，状若宝盖，光四射，起东北，没西南，散作火光而灭，有声如雷。清乾隆安徽');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清高宗乾隆十三年十一月中旬', '1748.12.30-1749.1.8', '天鼓鸣，自东南起，旋有星大如斗，从东南落西方。清成丰陕西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清高宗乾隆十三年十一月', '1748.12.20-1749.1.18', '火星流于西方，有声。清乾隆山西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('请高宗乾隆十四年二月乙酉', '1749.3.24', '昏，流星如鸡子，出王良，下行，色赤，入云，有光，有尾迹。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清高宗乾隆十四年五月庚申', '1749.6.27', '晓，流星如鸡子，出织虫，西北行，色青，入云，有光，有尾迹。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清高宗乾隆十四年五月癸亥', '1749.6.30', '夕，有大星西陨，其光蓬勃如火。民国广西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清高宗乾隆十四年九月壬申', '1749.11.6', '流星如盆，出娄宿，色赤，入天苑，有光，有尾迹。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清高宗乾隆十五年正月壬申', '1750.3.6', '二更，流星如鸡子，出天枪，西北行，色青，入云，有光。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清高宗乾隆十五年五月戊午', '1750.6.20', '晓，流星如鸡子，出天船，下行，色青，入云，有光，有尾迹。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清高宗乾隆十五年八月戊子', '1750.9.18', '晓，流星如鸡子，出天狼，东行，色赤，入柳，有光，有尾迹。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清高宗乾隆十六年五月', '1751.5.25-6.22', '寿宁县，星坠，有声。清乾隆');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清高宗乾隆十六年八月丙申', '1751.9.21', '二更，流星如鸡子，出斗宿，下行，色青，入云，有光，有尾迹。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清高宗乾隆十七年正月十二', '1752.2.26', '夜初更，有星寸许，陨于东北，其声如雷，光芒数丈，木叶遭之尽脱。清乾隆福建');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清高宗乾隆十七年四月某日', '1752.5.14-6.11', '晡时，有红光，大如轮，从东北方流于西南。清嘉庆陕西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清高宗乾隆十七年六月初六', '1752.7.16', '戌时，有星大如斗，自东北迄西南，光芒数丈。清嘉庆浙江');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清高宗乾隆十七年六月丁巳', '1752.8.7', '昏，流星如鸡子，出女床，西北行，色赤，入天理，有光，有尾迹。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清高宗乾隆十七年六月戊午', '1752.8.8', '昏，流星如鸡于，出织女，东南行，色青，入河鼓，有光，有尾迹。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清高宗乾隆十七年六月', '1752.7.11-8.8', '夜半．有星大如月，尾长数丈，光焰飞腾，喷薄有声，自东北陨予西南，天地通明，经一时许，鸡犬林鸟皆惊。清同治湖北');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清高宗乾隆十七年八月初一', '1752.9.8', '申时，有星陨于西北。清乾隆陕西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清高宗乾隆十七年', '1752', '有星大如斗，赤而有芒，陨于东北。清乾隆山西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清高宗乾隆十八年六月乙酉', '1753.7.21', '晓，流星如鸡子，出河鼓，南行，色白，入云，有光，有尾迹。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清高宗乾隆十八年六月己酉', '1753.7.25', '昏，流星如鸡子，出东南云中，下行，色赤，入斗，有光。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清高宗乾隆十八年七月甲戌', '1753.8.19', '三更，流星如杯，出奎宿，东南行，色赤，入云，有光，有尾迹。流星');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清高宗乾隆十八年七月', '1753.7.30-8.27', '有星如月，从西坠，有声。清道光江苏');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清高宗乾隆十九年正月丁巳', '1754.1.29', '一更，流星如盏，出奎宿，西北行，色青。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清高宗乾隆十九年正月癸亥', '1754.2.4', '晓，流星如鸡子，出南河，下行，色赤，入云。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清高宗乾隆十九年闰四月十二日', '1754.6.2', '酉时，有星自东方流西，声震如雷。清乾隆江西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清高宗乾隆二十年五月初九', '1755.6.18', '夜半，有白光如练，阔数丈，长百丈余，起西北，流向东南，火芒烛天，散后，有声如雷，良久乃止。清乾隆湖南');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清高宗乾隆二十年五月甲午', '1755.6.30', '昏，流星如鸡子，出亢宿，东南行，色青，入云。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清高宗乾隆二十年六月二十四日', '1755.8.1', '有星陨，光如曳火。清光绪山东');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清高宗乾隆二十一年六月甲子', '1756.7.24', '一更，流星如鸡子，出河鼓，西北行，色赤，入贯索。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清高宗乾隆二十一年七月辛未', '1756.7.31', '三更，流星如鸡子，出宗正，西行，色青。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清高宗乾隆二十一年十一月丙审', '1756.12.23', '四更，流星如鸡子，出文昌，西北行，色赤。');
