INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清高宗乾隆五十八年十二月甲戌', '1794.1.16', '夜，有声如雷，光如电，自箕分至舆鬼而灭，或谓即天狗。清光绪上海');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清高宗乾隆五十九年三月六日', '1794.4.5', '有物首阔尾狭，长五，六尺，色赤似火，从西向东，徐飞经坂美村而去。民国福建');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清高宗乾隆五十九年四月五日', '1794.5.3', '夜，星入月，大星陨于西南，有声如炮。清道光甘肃');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清高宗乾隆五十九年六月二十二日', '1794.7.18', '戌刻，有星自西北而南，其光散落，有声如雷。清光绪山东');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清高宗乾隆五十九年六月乙酉', '1794.7.26', '一更，流星如鸡子，出王良，下行，色赤，有光，有尾迹。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清高宗乾隆五十九年十月乙卯朔', '1794.10.24', '一更，流星如鸡子，出天秸，下行，色赤，入云，有光，有尾迹。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清高宗乾隆五十九年十月丙寅', '1794.11.4', '晓，流星如盏，出张宿，下行，色赤，入云，有光，有尾迹。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清高宗乾隆五十九年十月己巳', '1794.11.7', '四更，流星如鸡子，出五车，东北行，色赤，入云，有光，有尾迹。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清高宗乾隆五十九年', '1794', '有星，其光似灯，有声如雷，自西北起，曳至东南。清光绪河北');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清高宗乾隆六十年正月上旬', '1795.1.21-30', '天将晓，东南一星大如斗，光芒闪烁，坠而复上者三。清同治湖南');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清高宗乾隆六十年二月丁巳', '1795.2.23', '吾，流星如鸡子，出王良，下行，色赤，入云，有光，有尾迹。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清高宗乾隆六十年闰二月戊戌', '1795.4.5', '三更，流星如盏，出大角，西北行，色赤，入云，有光，有尾迹。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清高宗乾隆六十年三月', '1795.4.19-5.17', '有星坠于海。清道光台湾');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清高宗乾隆六十年四月', '1795.5.18-6.16', '有流星大如盂，自北而南，烛地光明有声。民国山东');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清高宗乾隆六十年七月十八日', '1795.9.1', '夜戌时，南方有星如斗，坠下响如炮。清同治广东');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清高宗乾隆六十年七月庚午', '1795.9.4', '二更，流星如鸡子，出昴宿，下行，色赤，入云，有光，有尾迹。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('1029', '1796.1.29', '亥时，有声如微雷，屋宇若将动，疑为地覆，忽光彩眩目，一流星大如盈月，芒四出，俱青赤色，长丈余，中作曲势，声隆隆然向西南去。清光绪上海');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清仁宗嘉庆元年五月五日', '1796.6.9', '夜，星陨如斗，光芒有声，自东南流西北。清道光广东');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清仁宗嘉庆元年五月', '1796.6.5-7.4', '夜，有大星陨于营西北，光芒有声。清光绪');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清仁宗嘉庆元年六月二日', '1796.7.6', '戊刻，大星坠地，巨声如雷，窗檑皆动。四川');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清仁宗嘉庆元年六月四日', '1796.7.8', '浊中见大星如斗陨。民国浙江');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清仁宗嘉庆元年七月十六日’', '1796.8.18', '午时，天鼓鸣，有大星如斗，自东南落于西北，有声，同时萧县无云，天雷。民国');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清仁宗嘉庆元年七月', '1796.8.3-31', '铜山天鼓鸣，有大星如斗，自东南落于西北，隐隐有声。清同治江苏');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清仁宗嘉庆元年秋', '1796．秋', '空中有声，如鼓鸣，又有星如火珠，自东北坠西南。清道光广酉');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清仁宗嘉庆元年', '1796', '天鼓鸣，夜陨星。清光绪山东');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清仁宗嘉庆三年三月朔', '1798.4.16', '星陨于安顺城东南，有声如雷。清光绪贵州');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清仁宗嘉庆三年十月十五日', '1798.11.22', '酉刻，有星自西南陨东北，初仅如线，渐如纶荇，光烛天，声如雷。清光绪山东');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清仁宗嘉庆四年六月二十三日', '1799.7.12', '有飞火大如席，自西而东，照地如月，草木毕见。清道光四川');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清仁宗嘉庆四年七月三十日', '1799.8.30', '夜，有星大如盎，旁有二星如盏，自北而南。清光绪山东');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清仁宗嘉庆四年', '1799', '亥时，有流星自东南飞西北，大如炬，有声。清光绪四川');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清仁宗嘉庆六年三月二十三日', '1801.5.5', '戌时，有星大如斗，光如月，其后微星不以数计，自北飞南，锵然有声，后闻天鼓大震，同时而见，闻者数省。清咸丰贵州');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清仁宗嘉庆六年四月', '1801.5.13-6.10', '有星见于北方，色赤如火状，西折如龙。清道光山东');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清仁宗嘉庆六年七月', '1801.8.9-9.7', '有星如鹅卵，尾拖地，白光数十丈，自北而南，落地有声。清光绪');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清仁宗嘉庆七年二月初五', '1802.3.8', '夜，星入月中。清光绪广东');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清仁宗嘉庆七年三月十一日', '1802.4.12', '晚，天忽开裂，有光照耀，中现红绿色，顷之，霹雳一声复合。清嘉庆湖南');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清仁宗嘉庆七年三月十八日', '1802.4.19', '戌刻，莆田有星大如斗，自东流入西，末迸裂如三毡，其光如月，森芒四射，如雷响殷殷然。清道光');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清仁宗嘉庆七年七月初八', '1802.8.5', '黄昏时，有流星自西北隅起，落于东南隅．＼初起时，形如鸡子，转瞬，光长丈余，大可数抱，飞过处，光焰烛天，楼阁屋字，无不膘彻。清光绪贵州');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清仁宗嘉庆七年七月', '1802.7.29-8.27', '天裂，有声如雷，红光如电，清咸丰广东');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清仁宗嘉庆七年十二月戊申', '1803.1.4', '有白气著天如布，西北行，有声灌如雷。清道光浙江');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清仁宗嘉庆八年六月', '1803.7.19-8.16', '有星如月，流北方。清道光山西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清仁宗嘉庆八年八月十四日', '1803.9.29', '卯刻，有星陨艮地，声如雷。清嘉庆河北');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清仁宗嘉庆八年九月', '1803.10.16-11.13', '大星陨。清道光广东');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清仁宗嘉庆九年二月十二日', '1804.3.25', '滕县城东石沟见火毡飞落。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清仁宗嘉庆九年三月初六', '1804.4.15', '1031夜，有大星自东南坠于东北，其色白，其长竟天，约两时许，其光始敛。清同治江苏');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清仁宗嘉庆九年四月', '1804.5.9-6.7', '大星陨于东北。（东川府续志）清道光');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清仁宗嘉庆九年七月十一日', '1804.8.15', '有星碗大，华彩缤纷，自西北角流至东南，散为五星，经时乃止。清道光湖南');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清仁宗嘉庆九年九月三十日', '1804.11.1', '五更，沅陵见天上有红光，自西而东，坠地有声如雷，巳而地动。清光绪');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清仁宗嘉庆九年', '1804', '申未间，见一星，自东南飞至西北，如世之火珠状，其光数丈长。清嘉庆');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清仁宗嘉庆十年七月初三', '1805.8.26', '夜子时，有星如盆，自东北西陨，光焰烛天。清道光甘肃');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清仁宗嘉庆十一年四月十三日', '1806.5.23', '有星大如数石瓮，自东北起，至近月，炸为十余，犹大如茶盏，火烛天，有声隆隆然，坠于西南丙没。清道光湖北');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清仁宗嘉庆十一年六月二十二日', '1806.8.6', '夜，天鼓再鸣，星陨。清道光甘肃');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清仁宗嘉庆十二年二胃初三', '1807．．3.11', '。有物如星，首大如碗，尾长三尺余，去地十余丈，自城西南流于东北，有声如放箭，其光烛暗室。清嘉庆云庸');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清仁宗嘉庆十二年四月十三日', '1807.5.20', '有星大如数石瓮，自东北起，忽炸为十余，犹大如茶盏，光烛天r肇霹豪投。清光绪湖北');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清仁宗嘉庆十一年四月。.。', '1804.5.8-6.5', '静…．i拳．}帮黄安有火大如毡，自东而西，落-酶池。辔');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清仁宗嘉庆十二年七月十二日', '1807.8.15', '戌刻，有陨星自东北至西南，流光如炬，声如雷。清光绪山东');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清仁宗嘉庆十二年七月', '1807.8.4-9.1', '星陨如灯，天鼓鸣。清光绪山东');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清仁宗嘉庆十二年冬', '1807．冬', '星陨。清嘉庆广东');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清仁宗嘉庆十二年', '1807', '有火大如毪，其光变变，由东而西，落于津。清道光湖北');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清仁宗嘉庆十二年十二月二十八日', '1808.1.25', '戌时，天鼓鸣东南，白光照地如月，顷刻寂然。清乾隆江苏');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清仁宗嘉庆十三年三月十六日', '1808.4.11', '戌时，宣化县有流星大如箕，南，光芒如昼，声响如雷。清道光广西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清仁宗嘉庆十三年七月', '1808.8.22-9.19', '东南天裂，长数尺，有物突出，大如车轮，红光烛天，有声如雷。清道光湖南');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清仁宗嘉庆十四年七月', '1809.8.11-9.9', '夜，有红光如电，阔数尺，蜿蜒自北而南，有声。清嘉庆江西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清仁宗嘉庆十四年十月', '1809.11.8-12.6', '夜，有光起西北，渐东南，下沉，其声隆隆如雷者三。民国山东');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清仁宗嘉庆十五年正月初八', '1810.2.11', '费昏时，有大星自东方飞至西南，嗤然如闻其声，化为白气一团，形如半月，差大而无光，久之乃灭，不知为何祥也。清光绪河北');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清仁宗嘉庆十六年五月', '1811.6.21-7.19', '天鼓鸣，有星陨，光散为白霞。（河阳县采访）清道光');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清仁宗嘉庆十六年九月二十三日', '1811.11.8', '酉时，星陨，彗星其声如雷。民国广西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清仁宗嘉庆十六年十月朔', '1811.11.16', '夜二更，天裂西北，其光烛地，有物如斗大，色赤，滚坠而散，声震如雷。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清仁宗嘉庆十六年十月初三', '1811.11.18', '夜中，赤眚自西南倏至东北，状如天裂，光明彻地，有声如雷。民国浙江');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清仁宗嘉庆十六年十月十四日', '1811.11.29', '夜，有流星自南而西，大如斗，照耀林木，1033流星声殷地若雷。清道光安徽');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清仁宗嘉庆十八年春', '1813．春', '星陨。民国云南');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('请仁宗嘉庆十八年夏', '1813．夏', '有星，自东而西，拖光如线，逾时始散。清嘉庆山西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清仁宗嘉庆十九年七月', '1814.8.15-9.13', '亥时，天忽开朗，划然而启，灿若长虹，横于南北，长丈余，中有一大星，两旁列小星数十，如贯珠然，大星投东而去，小星随隐，众目所睹。清道光广东');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('请仁宗嘉庆二十年九月乙卯', '1815', '1、有大星，自西北坠于东南，火光烛天，声若雷炮。（初九日）清道光甘肃');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清仁宗嘉庆二十一年闰六月十六日', '1816.8.9', '将晡，天鼓鸣，声若雷轰者三，光如炬，坠落半空，旋复收回，红光一缕如绒，倏变白色，移时乃灭。清道光江西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清仁宗嘉庆戊寅二月丁卯', '1818.3.16', '黎明，……又有红星陨于东南，空中若有鼓户。清光绪山西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清仁宗嘉庆二十三年六月', '1818.7.3-8.1', '陨星如瓜。清宣统');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清仨宗嘉庆二十三年十二月壬戌晦', '1819', '有星如月，自东升于天中乃没。清道光甘肃');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清仁宗嘉庆二十四年正月', '1819.1.26-2.23', '有星陨。（太和县采访）清道光');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清仁宗嘉庆二十四年七月十九日', '1819.9.8', '夜，有星陨于西方，大如箕，至地有声。清道光贵州');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清仁宗嘉庆二十四年十一月二十三日', '1820.1.8', '戌时，天鼓鸣，有大星流于西南。清同治');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清仁宗嘉庆二十五年七月二十四日', '182J．9.1', '夜，顺义乡泷溪有农者，夜守宿田间，仰见天开一路，箫鼓齐鸣，若迎仙状，顷刻遂没。清道光江西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清仁宗嘉庆庚辰七月二十五日', '1820.9.2', '初更，有大流星陨于南方，光如白昼。1034');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清仁宗嘉庆二十五年七月', '1820.8.9-9.6', '天汉中光长丈余，有声，移时渐没。清道光江苏');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清宣宗道光元年二月二十七日', '1821.3.30', '夜，流星自东来，大予盖，色赤黄，长十余丈，阔二尺许，西行，声如轻雷。清光绪贵州');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清宣宗道光元年六月', '1821.6.29-7.28', '夜，有星自西北流坠东南，色通明，形圆如匏，其光射人。清光绪福建');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清宣宗道光元年七月初四', '1821.8.1', '初昏，有大星，自天汉北飞入汉南，触地有声，火光迸天。清同治湖北');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清宣宗道光二年五月二十日1', '1822.7.8', '星陨。清道光河南');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清宣宗道光三年正月二十四日', '1823.3.6', '初昏，有星自东北陨于西南，红光烛天，声若隐雷。清光绪浙江');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清宣宗道光三年八月十五日', '1823.9.19', '夜，莆田有星自东而南流，光照地如月。清遒光');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清宣宗道光三年八月十七日', '1823.9.21', '有星大如圆笠，其光照地如月，渐没于西，声如雷。清道光');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清宣宗道光四年七月乙酉', '1824.8.18', '夜，有星大如斗，光如月，自东流西。清道光广东');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清宣宗道光四年八月初五', '1824.9.27', '子时，有星大如斗，色如血，从北流西，移时，天鼓呜。清同治四川');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清宣宗道光五年正月十五日', '1825.3.4', '有大星流于东北，后随小星无数，声如雷。清光绪山西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清宣宗道光五年五月', '1825.6.16-7.15', '大星陨于东。（开化府采访J清道光');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('1035流星', '1825.8.17', '亥时，一物如车轮，红光烛天，自西腾空转东，声响如雷。清光绪陕西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清宣宗道光五年（乙酉）八月十七日', '1825.9.28', '夜，会城三更时，月光如昼，天半有声如雷，随有光自西北隅来，委坠如艨带，晃耀异常，射人屋宇，飘至东南隅，其光四散而没，声亦旋止，或日：此天鼓星坠。清道光福建');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清宣宗道光乙酉十月二十六日', '1825.12.5', '夜，东南方星陨，飒飒有声，最后，有大星坠于地，其声如雷。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清宣宗道光五年', '1825', '有大星，头如箕，身如龙，长十余丈，自东而西，白光烛地。清咸丰湖北');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清宣宗道光六年正月十五日', '1826.2.21', '寅未刻，有流星色赤，自震坠至坤，隐隐有声。清道光山西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清宣宗道光六年二月二十八日', '1826.4.5', '涪州申时，有光势如长虹，具首尾，自西徂东，其行有声，蜿蜒迤逦而去。清道光四川');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清宣宗道光六年二月晦', '1826.4.6', '有物首如悬帆，光赤，尾长丈余，自东而西，旋没。清同治湖北');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清宣宗道光六年（丙戌）二月', '1826.3.9-4.6', '有流星大如斗，起于东南，落于西南，离地丈余。清道光河南');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清宣宗道光六年三月', '1826.4.7-5.6', '黄昏时，有流星大如斗，长丈余，自东而北，喷薄有声。清同治湖北');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清宣宗道光六年四月初一', '1826.5.7', '戌初，白气经天，自西徂东，约二、三尺，光如练，声如裂帛，数刻始散。清光绪湖北');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清宣宗道光六年五月初六', '1826.6.11', '戌时，大星陨，自中流于东。清道光云南');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清宣宗道光六年五月二十七日', '1826.7.2', '夜，天有声如雷，其光由东南坠地。清同治湖南');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清宣宗道光六年六月丙申', '1826', '有光如虹，自西徂东，其行有声。清光绪四川');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清宣宗道光六年夏', '1826．夏', '有光如虹，首西尾东，蜿蜒有声，傍山行去。清光绪四川');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清宣宗道光六年十月中', '1826.11.10-11.19', '有星流光，长二丈余，昏见东南方，去地如数尺。清道光江苏');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清宣宗道光六年十二月', '1826.12.29-1827.1.26', '有星六、七，从西飞过。清道光');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清宣宗道光七隼正月十五日', '1827.2.10', '戌刻，天上见火光一道，移时而灭。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清宣宗道光七年二月', '1827.2.26-3.26', '夜，飞星由北而西，大如碗。清同治湖北');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清宣宗道光七年清明前一日', '1827.4.4', '酉刻，孛星如盘，自东徂西，飞腾有声。清同治湖北');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清宣宗道光七年五月某日', '1827.5.26-6.23', '夜，有白光一道，头大如缶，尾拖十数丈，自东方起，流至西南方落地。清同治湖南');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清宣宗道光七年六月二十八日', '1827.8.20', '夜，星陨于韩家埭。清光绪浙江');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('流星', '1827.9.17', '夜二更，空中忽霹雳一声，目东而西，天开若划，须臾复合。清同治湖北');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清宣宗道光八年（戊子）七月十八日', '1828.8.28', '晡时，有大流星自东而西，白气横曳，长而且久。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清宣宗道光八年七月', '1828.8.11-9.8', '有大星入于南斗。清道光湖南');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清宣宗道光九年五月某日', '1829.6.2-30', '旁晚，日尚未入，有星从东南向西北横飞，有声如飞矢，初仅见光，及时下坠，大如斗，有尾，淡赤色，如龙而逝。清光绪浙江');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清宣宗道光九年（已丑）六月', '1829.7.1-30', '有大星，自西南流于东北，有声。清同治江西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清宣宗道光九年已丑七月五日', '1829.8.4', '夜，流星自东而西，大如碗，色赤，尾数丈，红绿色，光射地如月，有声震耳。清同治湖南');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清宣宗道光九年十一月十二日', '1829.1.27', '～有星陨东南，赤色，有光投地，声殷如雷，三投再起，疾流至西北坠，其光烛天。清光绪台滂');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清宣宗道光九年十二月十一日', '1830.1.5', '夜，忽有黑云起于东南隅，蔽月而昏，俄而黑云如霞，毫光四射，中有声如鼓如雷，后坠于西方，声震远迩。民国福建');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清宣宗道光九年', '1829', '邑南十五里，有星西流，头巨似车轮，长十余丈，离地数丈，天鼓鸣。清同治湖北');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清宣宗道光十年二月二十五日', '1830.3.19', '有大星流自西南，其光亘天。清道光湖南');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清宣宗道光十年五月二十五日', '1830.7.14', '初更，星陨，自北流于南，大如杯，有声如炮，初绿色，渐红，光照地如月。清同治湖南');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清宣宗道光十年六月初七', '1830.7.26', '夜，莆田有星自东北流西南，其光照地如月。清道光');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清宣宗道光十年八月', '1830.-9.17-10.16', '有火毽大如盂，自东南落西北，经天有声．清同治四川');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清宣宗道光十一年正月十三日', '1831.2.25', '天鼓鸣，既止，有火光大如斗，雁～。警清宣统甘肃');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清宣宗道光十一年七月戊午', '1831.8.15', '夜，陨星。清光绪山东');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('靖宣宗道光十一年七月', '1831.8.8-9.5', '有星大如缶，尾啦光一道，落于西南。清同治湖南');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('蒲宣宗道光十一年八月', '1831.9.6-10.5', '有白气如练，自南流北，所过有声，满城照耀如白昼。清同治湖北');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清宣宗道光十一年十月辛丑', '1831.11.26', '星昼陨，两日相荡，有声如雷。清光绪山东');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('请宣宗道光十二年二月十九日', '1832.3.20', '夜，有大星自东流西。大星状如巨碗，后有数小星随之，所过处闪白光，照地如月，既过，有声如雷。清道光福建');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清宣宗道光十二年三月十六日', '1832.4.16', '夜，有赤星大如斗，自东北起，霹雳一声向西坠．后小星无数随之。清同治安徽');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清宣宗道光十二年春', '1832．春', '随州有火龙自北而南，火光烛地。清光绪湖北');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清宣宗道光十二年七月二十六日', '1832.8．Z1', '平旦，天朗无云，有火毽坠于北方，雷声随之。清道光江西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清宣宗道光十二年八月二十二日', '1832.9.16', '夜，陨星西南，有声如雷。清道光台湾');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清宣宗道光十四年六月二十六日', '1834.8.1', '昏，大星自东南飞度西北，尾拖白气如练，隆然有声，红光烛地。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清宣宗道光十五年正月二十七日', '1835.2.24', '丑时，空中有霹雳声如雷，随有火光如电，射入署中。清道光四川');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清宣宗道光十五年七月二十二日', '1835.9.14', '亥刻，有白光，从天西南流至东北，如电，流星良久乃灭。清光绪江苏');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清宣宗道光十五年七月', '1835.8.24-9.21', '天橇星、尾星并见，大如杯碗。清道光广东');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清宣宗道光十五年八月初九', '1835.9.30', '夜，有星大如斗，声如霹雳，西坠，光芒烛天。清同治安徽');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清宣宗道光十五年九月二十', '1835.11.10', '夜，天东南忽开，赤光丈余，声如裂帛。清同治江西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清宣宗道光十六年二月初四', '1836.3.20', '夜，二更，空中有红光，形如兽，坠入东方，随有霹雳声，如雷震。清道光四川');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清宣宗道光十六年四月十九日', '1836.6.2', '酉刻，西南角白气一缕，向空飞，形如白蛇，盘旋天际，有声，渐向北而没。清光绪广东');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清宣宗道光十六年七月七日', '1836.8.18', '夜中，有星陨于西方，大如斗。清道光贵州');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清宣宗道光十六年七月', '1836.8.12-9.10', '有星自南流，尾迹逾尺，隐隐如雷声。清光绪湖南');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('滴宣宗道光十七年中秋', '1837.9.14', '夜，无裂有光，（自）西至东，历历有声。民国安徽');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清宣宗道光十八年春', '1838．春', '星陨。清光绪');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清宣宗道光十八年五月二十八日', '1838.7.19', '有星蓬蓬然，其行如蛇，自东北至西南而没，类枉矢。清道光湖北');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清宣宗道光十八年七月初旬', '1838.8.20-29', '昏时，有星头大丈余，以次递减，如葫芦连珠形，从东流坠西方。清同治四川');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清宣宗道光十八年十月二十日', '1838.12.6', '初夜，大星流南乡，光映四射。清道光江苏');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清宣宗道光十八年十月二十四日', '1838.12.10', '夜，星陨东南。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清宣宗道光十八年十一月', '1838.12.17-1839.1.14', '城西隅火流自天，色青赤，有声，如是者数次。清道光江苏');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清宣宗道光十九年正月初七', '1839.2.20', '初昏，葫芦星仍从西流东。清同治四川');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清宣宗道光十九年五月', '1839.6.11-7.10', '有大小星自东而西，声如雷。清宣统山东');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清宣宗道光十九年六月十九日', '1839.7.29', '夜，异星见。星如巨灯，傍有苍云二片，如门开阖，移时，西流而灭。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清宣宗道光十九年十二月十九日', '1840.1.23', '夜半，有火陨于城西南，声如炮震。清光绪山西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清宣宗道光二十年八月', '1840.8.27-9.25', '夜，空中有火如轮，往西北流，逾时而没。清同治江西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清宣宗道光二十年九月朔', '184.9.206', '天炮鸣，有声自西北起，火光烛天，至东南乃已。清道光甘肃');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清宣宗道光二十年秋', '1840．秋', '黄昏后，有星形如匹练，自东而西，光射窗檑。清光绪江苏');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清宣宋道光二十年十月十九日', '1840.11.12', '更初，大星流于东北，横亘如播蓬，火光似烛，移时光没，余焰如小星，自相冲击。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清宣宗道光二十年十月二十日', '1840.11.13', '甫昏，有流星大如盘，自西北望东南去。清道光江苏');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清宣宗道光二十一年四月十二日', '1841.6.1．', '陨星二。清光绪安徽');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清宣宗道宗二十一年四月', '1841.5.21-6.18', '邳州陨星二。清同治江苏');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清宣宗道光二十一年八月十六日', '1841.9.30', '三鼓后，大星陨西北，声如雷。清同治');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清宣宗道光二十一年秋', '1841．秋', '有赤光长三尺许，状如蛇虺，现于天半，自城西流向东南，逾晷始没。清同治江西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清宣宗道光二十一年十月十四日', '1841.11.26', '薄暮，有大星光如火，众小星随之，自东南至西北，有声如雷。民国浙江');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清宣宗道光二十一年十月二十日', '1841.12.2', '有大星陨于西北。（见冯登府诗注）民国');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清宣宗道光二十一年', '1841', '大星陨于西南，有声如雷。清咸丰浙江');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清宣宗道光二十二年四月', '1842.5.10-6.8', '戌刻，天鼓鸣，落将星。清光绪安徽');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清宣宗道光二十二年十二月二十四日', '1843.1.24', '戌时，有明星自东北行向西南，后随白气如练，天鼓响如雷。民国山东');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清宣宗道光二十三年三月', '1843.3.31-4.29', '落一巨星，无数小星从之，光焰烛天。清光绪江苏');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('流星', '1843.8.2', '西南角天开一道，声如裂帛j清光绪贵州');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清宣宗道光二十三年八月二十七日', '1843.10.20', '夭裂有声，流光数丈。清同治四川');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清宣宗道光二十四年秋', '1844．秋', '星陨西方。清光绪安徽');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清宣宗道光二十五年正月二十五日', '1845.3.3', '初昏，有大星自西南坠东北，声如雷。清光绪江苏');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清宣宗道光二十五年三月初二', '1845.4.8', '申刻，南方有红星大如斗，自天而下，煅雾随之，长百余丈，星没，烟雾乃腾空而上。清光绪四川');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清宣宗道光二十五年六月朔', '1845.7.5', '夜，天鼓鸣，有大星从东北陨于西南。清光绪山西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清宣宗道光二十六年四月朔', '1846.4.26', '晚，有星自南陨北，光如电，帛訇有声。清光绪山西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清宣宗道光二十六年七月十三日', '1846.9.3', '巳时，天无云而雷，西南方降一大赤星。1042清道光四川');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清宣宗道光二十六年七月', '1846.8.22-9.19', '有赤星大如月，光芒四射，自南而北，少顷，天鼓鸣。清光绪山西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清宣宗道光二十六年十二月', '1847.1.17-2.14', '天裂数丈，青碧荧荧如人目，有声砰訇若雷哗。民国浙江');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清宣宗道光二十七年六月朔', '1847.7.12', '见大星陨于东南，其巨如碗。清光绪山东');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('庸宣宗道光二十七年七月', '1847.8.11-9.8', '飞尘蔽天，忽红光一团，大如盆盅，向南而没，识者以为星陨。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清宣宗道光二十七年秋', '1847．秋', '陨星。清光绪河北');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清宣宗道光二十七年十二月初五', '1848.1.10', '夜，复有大星如火弹，自南而北，天鼓鸣。清光绪山西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('靖宣宗道光二十八年六月二十一日', '1848.7.21', '夜，有星大如斗，自空坠地，戛然有声。清光绪广西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清宣宗道光二十八年八月二十一日', '1848.9.18', '农二更，西北天半，划然有声，如葫芦下垂，旋复变为巨臼向上，大逾车轮，红黄射目，一时许方没。清同治四川');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清宣宗道光二十八年十月三十日', '1848.11.25', '西南有白气，蜿蜒数丈，其光如电，其声如雷。清光绪湖南');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清宣宗道光二十八年十一月己丑', '1848.12.14', '前数日，江夏历坛角……有星陨如虹。清宣统');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清宣宗道光二十八年', '1848', '初昏，有星自西南流于东北，城。清同治广东');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清宣宗道光=十九年七月十四日', '1849.8.31', '日入时，有流星见西方，声若雷，光照林木，俄散为七、八点，向东没。清同治湖南');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清宣宗道光二十九年十月十六日', '1849.11.30', '日落后，西南角一星大如碗，飞射烛天，旋分为二，又分为三、四，渐缓，渐小，向东灭。1043流星忘记其日，约申刻，天星一粒，自东斜西始坠，其坠时，声如炮响，远近皆闻初由一粒，散出无数火光，形似火箭，射至西方而没。清光绪广东');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清宣宗道光二十九年十二月初六', '1850.1.18', '二更时，天鼓鸣，有星陨，光芒约数丈许。清光绪山西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('请宣宗道光三十年四月晦', '1850.6.9', '夜，有天狗星过境，光芒万丈，自西南入于东北，声如雷。清光绪河北');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清宣宗道光三十年六月朔', '1850.7.9', '天狗坠东南方，大数围，色白，其声如雷，至地不见。清光绪浙江联永康县志》卷11页33清光绪浙江');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清宣宗道光三十年七月四日', '1850.8.11', '黄昏后，东北有大星如虹，贯房、心之次转瞬，又有一星稍短，将及角次，良久方没，时天市垣外，小星碎点动摇，如石击火。清同治广西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清宣宗道光三十年七月十七日', '1850.8.24', '夜，星陨南方，光芒异常。清道光、广西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('蒲宣宗道光三十年七月', '1850.8.8-9.5', NULL);
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清宣宗道光三十年十一月十二日', '1850.12.15', '夜，亥时，有星犬如斗，自辰至寅方，光如火，有声。清同治安徽');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清宣宗道光三十年十二月初九', '1851.1.10', '日方出时，忽如闪电，轰然有声，空际有物，上尖下圆而色红，向西飞去，响若迅雷。清同治四川');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清宣宗道光三十年十二月二十二日', '1851.1.23', '戊刻，有流星长丈余，从空横飞，光明如电，隐隐有声。清光绪云南');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清宣宗道光三十年十二月', '1851.1.2-1.31', '流星自南面北，声如巨雷。清光绪北京');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清文宗咸丰元年五月丁未', '1851.6.20', '有星自南陨于北。清光绪山东');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清文宗咸丰元年七月九日', '1851.8.5', '夜，有大星自北而南，移时不见。民国山东');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清文宗咸丰元年八月二十八日', '1851.9.23', '随州有天火，自西南流东北，其光烛地，有声如鼓。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清文宗咸丰元年八月', '1851.8.27-9.24', '有星孛于北斗，渐移而南，至天市垣，长六、七丈，光焰异常。民国山东');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清文宗咸丰元年九月十一日', '1851.11.3', '戌刻，闻天鼓一声，有星大如火毡，落于北方，阖郡皆见。清光绪浙江');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清文宗咸丰元年九月十八日', '1851.11.10', '辰刻，有星如月，行迟，有声。清同治湖北');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清文宗咸丰元年九月二十四日', '1851.11.16', '初鼓时，天裂有光，声如雷。清光绪山西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清文宗咸丰元年九月二十五日', '1851.11.17', '夜，初更，有二星西坠，有声。清同治安徽');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清文宗咸丰元年', '1851', '星陨。清光绪安徽');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('浦文宗咸丰二年正月十九日', '1852.3.9', '夜，有光如火，自西南至东北，声震如雷。清同治安徽');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清文宗咸丰二年七月', '1852.8.15-9.13', '有星如火，其光烛天，自东南飞来，陨于西北，有声。清同治江西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清文宗咸丰二年九月初十', '1852.10.22', '有星大如斗，自西坠东，光如电闪，有声如雷，须臾乃止。清咸丰浙江');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清文宗咸丰二年九月二十一日', '1852.11.2', '酉时，妖星起西南，流入东北，随小星无数，有雷震声。清同治河北');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清文宗咸丰二年九月', '1852.10.13-11.11', '流星有星大如月，起西南，落东北，有声如雷。清光绪河北');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清文宗咸丰二年十月丁巳', '1852', '天狗过境，起西南，大如盎，炎炎中天，至东北分为二星而灭，后有声如雷。清同治河北');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('请文宗咸丰二年十月十日', '1852.11.21', '夜，有大星如斗，自西南流东北，落时，有数十小星随之，其声如雷。清光绪河北');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清文宗咸丰二年十月十一日', '1852.11.22', '初昏时，流星自南而北，甚低，到处多落小星，天晴月朗，有风雷，晋州南北及乡邑无不见之。民国河北');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清文宗咸丰二年冬', '1852．冬', '南安有声如雷，火焰飞空而过，远近皆闻。清光绪');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('请文宗咸丰二年十二月', '1853.1.9-2.7', '初昏，有光如电，遥见火块自空而下。清光绪江苏');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清文宗咸丰二年', '1852', '夜间，往往有火从天坠，其大者如毯，小者如卵，至地即不见，寻有白气经天，其形如虹。清同治广西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清文宗咸丰三年二月二十七日', '1853.4.5', '是日清明，酉时，有星大于斗，其色如翠，其光如火，其动如水，由东北而南坠焉。清同治湖北');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清文宗咸丰三年三月九日', '1853.4.16', '夜，有星自南而北，红光烛天，声如雷。清同治江西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清文宗咸丰三年春', '1853．春', '夜半，赤气烛天，若有物，自北而南，坠地有声。清光绪山西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清文宗咸丰三年四月', '1853.5.8-6.6', '有星自东北入东南，大如掌，光如月。清同治台湾');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清文宗咸丰三年五月初十', '1853.6.16', '黄昏时，乾方有火团，晶彻透亮，自空而坠，俄有白气一股，自坠处直冲霄汉。清同治四川');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清文宗咸丰三年夏月晦', '1853.7.5', '夜，一星见于南维，光芒四射，花树皆有影，朗如月，转瞬星坠，但见白气一缕自上而下，宛若绳直。清光绪浙江');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清文宗咸丰三年六月十九日', '1853.7.24', '酉刻，有星大如斗，由西南坠东北，明如电光，秋毫毕现，时人称为天开眼。民国上海');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清文宗咸丰三年六月二十一日', 't853.7.26', '夜，流星自南往西而灭，大如斗，其光烛地。清光绪广东');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清文宗咸丰三年六月二十九日', '1853.8.3', '亥时，三台星旁天裂，陨星如日。清同治安徽');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清文宗咸丰三年七月三日', '1853.8.7', '太平火陨，大如斗。（太平续志）清光绪浙江');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清文宗咸丰三年八月十五日', '1853.9.7', '夜，大星如斗，红光，落于东方。清同治广西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清文宗咸丰三年九月二十六日', '1853.10.28', '夜三鼓，满地墨色，忽西南角有光如月影，略青，行正疾，坠地，至东北而上，仰视空中，有氆约大尺许，长倍之，首圆尾尖，由东北过西南而灭，有顷，闻大炮响如雷。民国福建');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('浦文宗咸丰三年九月', '1853.10.3-10.31', '有流星由东北至西南，声如雷。清光绪湖北');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清文宗咸丰三年十月', '1853.11.1-11.30', '有大星坠地如火，一月两见。民国贵州');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清文宗咸丰三年十一月', '1853.12.1-29', '有大星如火西坠，光夺月色。民国贵州');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清文宗成丰四年三月二十八日', '1854.4.25', '黄昏时，有一大星如斗，自东北至西南，‰t．光如匹练。清宣统上海');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清文宗咸丰四年四月十二日', '1854.5.8', '蓝山有流星如匹练，自西北陨于东南，声如雷。清光绪');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清文宗咸丰四年四月十三日', '1854.5.9', '夜，有星如鹅卵，陨于西南。清光绪广东');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清文宗咸丰四年夏', '1854．夏', '夜，有流星如练，长数丈，自西北贯于东北，声如雷。清同治湖南');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清文宗咸丰五年正月', '1855.2.17-3.17', '大星昼见，自北流南，光烛地，行有声。清同治湖南');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清文宗咸丰五年四月十八日', '1855.6.2', '申时，东方有红光数丈，落地，有声如雷。清同治安徽');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清文宗咸丰五年四月二十八日', '1855.6.12', '夜二更，有星起东方，经天有声。清宣统浙江');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清文宗咸丰五年五月初四', '1855.6.17', '黄昏，有星大如满月，后四小者从之，自东南方横行而西，甚速，光烛天地。小桃源、狮山白云观，见者十余人，山下居民不见其物，遂讹传：天开眼-。清宣统安徽');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清文宗咸丰五年六月初六', '1855.7.19', '晚，有星长数丈，自北而南，横亘中天，恍如硝磺喷出，听之有声。清同治湖南');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清文宗咸丰五年七月', '1855.8.13-9.10', '有星二，大如盂，色碧，先后自西南流入东北，暗室中光烛榱桶。清同治湖北');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清文宗咸丰五年八月', '1855.9.ii-io.io', '有星如月，自北至南，陨声如雷。清同治湖北');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清文宗咸丰六年正月二十六日', '1856.3.2', '夜，有大星自西南流至东北，隆隆有声。清光绪浙江');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清文宗咸丰六年二月二十六日', '1856.4.1', '天狗坠，自西北至东南，有声。清光绪上海');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清文宗咸丰六年三月二十日', '1856.4.24', '酉刻，有物头大数围，身尾长数丈，红绿辉映，金光闪烁，由西南半空飞落东北之野，杏无形影。清光绪云南');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清文宗咸丰六年四月初七', '1856.5.10', '有星如船，光芒丈余，自东而西，落地有声。清光绪云南');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清文宗咸丰六年五月', '1856.6.3-7.1', '有大星自西南流坠东北，光芒数丈，有声。清光绪江苏');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清文宗咸丰六年六月', '1856.7.2-31', '有大星陨于东南，天鼓鸣。清光绪江苏');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清文宗咸丰六年七月初八', '1856.8.8', '亥时，大星陨，其影大如月，自西南流于东北，坠地。清成丰江苏');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清文宗咸丰六年七月', '1856.8.1-8.29', '已初，天狗起东南，红光烛天，其声如雷。清光绪河北');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清文宗咸丰六年秋', '1856．秋', '有大星，自西南流，坠东北，芒数丈，有声。民国');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清文宗咸丰六年十月初七', '1856.11.4', '夜，腾越星陨。清光绪');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清文宗咸丰六年十有一月', '1856.11.28-12.26', '时近五更，遥闻东方忽声如霹雳，裂口十余丈，宽六尺许，中如电光闪烁，阅寸香时，顷合。清光绪');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清文宗咸丰六年', '1856', '有大星西南流，光芒数丈。清光绪江苏');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清文宗咸丰七年二月初九', '1857.6.4', '天鼓鸣，有红光坠地下。清光绪安徽');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清文宗咸丰七年五月十一日', '1857.6.2', '戌时，火星流于金泉。清光绪云南');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清文宗咸丰七年闫五月', '1857.6.22-7.20', '流星如月，光亦不逊，半空而行自北，尾带红丝数十丈，没后天鼓响，连年屡见。清光绪福建');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清文宗咸丰七年六月十五日', '1857.8.4', '夜，星陨至屋脊而灭。民国浙江');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清文宗咸丰七年夏', '1857．夏', '天狗星坠，光焰四散，须臾即灭。清同治江西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清文宗咸丰七年八月十四日', '1857.10.1', '天南飞一物，如白练，宽五尺许，长丈余，摇曳上升，旋落东北角，人以为蚩尤旗出。清同治湖北');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清文宗咸丰七年八月二十八日', '1857.10.15', '有一星大如斗，飞至东方，其色如火。清光绪广东');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清文宗成丰七年九月初六', '1857.io.23', '戌刻，有红光如斗，自东起，声如雷，逾时方止。清光绪河南');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清文宗成丰七年九月', '1857.10.18-11.15', '星大如斗，陨于萧（县）。清同治江苏');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清文宗咸丰七年秋', '1857．秋', '黄安有白光如电，烛暗室，有声。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清文宗咸丰七年十月', '1857.11.16-12.15', '昏时，有火氇大如盆，坠于西，轰然有声。清光绪河北');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清文宗咸丰八年正月', '1858.2.14-3.lI', '鄙阳有流星自南而北，大小不一，烛天有声。清光绪');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清文宗咸丰八年三月', '1858.4.14-5.12', '彗出，光数丈，尾指北斗，自北行南，至金星而没。清同治广东');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清文宗咸丰八年六月初一', '1858.7.11', '戌刻，天流火毯。清同治江西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清文宗咸丰八年六月初二', '1858.7.12', '有流星自南而北，大小不一，光烛天，有声。清同治j胡南');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清文宗咸丰八年六月某日', '1858.7.11-8.8', '午正，大镇王某庭中，忽有星降，自天距地，约五、六尺，有声，虚悬不坠，须臾仍向西南飞去。民国四川');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清文宗咸丰八年七月', '1858.8.9-9.6', '星陨自东南，有声，大如月，色苍赤，渐大如车输，至西北，黑烟从轮中出，缭绕四散。清同治湖南');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清文宗成丰八年十月初十', '1858.11.15', '夜，有火自北而南，声如裂帛，赤光烛地。清光绪湖北');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清文宗咸丰八年十二月二十二日', '1859.1.25', '夜，有星坠西北方，声如沉雷。（谈梅J清光绪广东');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清文宗成丰九年正月初二', '1859.2.4', '戌刻，有星自南而北，始发如炮，余音若雷，长约呼吸二十息，先有光如电，是为天炮。清同治四川');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清文宗咸丰九年二月十五日', '1859.3.19', '夜，有大星陨东方。清光绪广东');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清文宗咸丰九年三月壬辰', '1859.4.24', '天狗过境，左旋入于东北，声如雷。清同治河北');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清文宗咸丰丸年五月二十九日', '1859.6.29', '戌刻，大星陨予星、张之处，分裂数块，其光如灯。清光绪湖北');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清文宗咸丰九年六月初十', '1859.7.9', '未、申时，有赤光团结如球，从西北流向东南，江、广、闽、浙，数千里皆见。清同治江西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清文宗咸丰九年六月', '1859.6.30-7.29', '初昏，有大星如斗，尾随数小星，由西北流于东北陨，其响如炮，各乡闻见略同。清同治广东');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清文宗咸丰九年夏', '1859．夏', '有大星坠地，散为无数小星，入于海。清光绪浙江');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清文宗咸丰九年七月初四', '1859.8.2', '酉刻，有流星自乾方一团，红光绿焰，横飞半天，分百炷以过东，至寅、甲方而没，见之甚近，恍在屋上飞过。清同治广东');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清文宗咸丰九年七月初七', '1859.8.5', '申刻，有天炮鸣，由西而东，又有青光一道。民国福建');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清文宗咸丰九年七月', '1859.7.30-8.27', '有星复陨，其光十如斗，尾长丈余。清光绪广东');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清文宗咸丰九年八月', '1859.8.28-9.25', '流星见东北，光芒数十里。清同治湖南');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清文宗咸丰九年十一月十六日', '1859.12.9', '夜三鼓，流星大如斗，自东而西，殷殷有声如雷。民国四川');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清文宗咸丰十年正月', '1860.1.23-2.21', '夜，有大星南飞，芒如火箭，激越有声。民国四川');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清文宗咸丰十年三月二十六日', '1860.4.16', '晚，有两星光亮，降于城东。民国福建');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清文宗咸丰十年四月二十六日', '1860.6.15', '申刻，有星陨于东南。清光绪江苏');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清文宗咸丰十年五月十四日', '1860.7.2', '夜，有大星陨。清同治');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清文宗咸丰十年六月', '1860.7.18-8.16', '西北一大星，东南行，有声。清光绪浙江');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清文宗咸丰十隼夏', '1860．夏', '一大星，自西北移入东南，隆隆有声，光如匹练，横亘天际，片时始灭。清光绪浙江');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清文宗成丰十年七月五日', '1860.8.21', '夜，星陨。清光绪江苏');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清文宗咸丰十年七月二十日', '1860.9.5', '有星自东南向西北，光芒烛天，倏尔而没。j青同治江西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清文宗咸丰十年七月二十三日', '1860.9.8', '夜，临川北乡有火光，自西而东，照耀如日。清光绪江西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清文宗咸丰十一年四月', '1861.5.io-6.7', '有大星自西南昼陨，大如斗，落地声震。清光绪江苏');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清文宗咸丰十一年四月癸未', '1861.6.3', '大星陨于东南。清光绪贵州');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清文宗咸丰十一年五月二十五日', '1861.7.2', '夜，流星白芒，见北方，初起有声。清光绪四川');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清文宗咸丰十一年五月', '1861.6.8-7.7', '有星陨如火，光芒数丈，自西南流予东南。清光绪四川');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清文宗咸丰十一年六月九日', '1861.7.16', '日中，有声如雷，星陨大如盎。清光绪江苏');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清文宗咸丰十一年七月初七日', '1861.8.12', '星陨子沧江。清光绪云南');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清文宗咸丰十一年七月', '1861.8.6-9.4', '夜，十九都一带，有物色纯赤，大数十丈，自南而北，尾拖光焰，遍地如赤霞，见者莫不骇异。清同治湖南');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清文宗咸丰十一年秋', '1861．秋', '赤星陨于西方。天鼓鸣，戌刻，有星赤红，自东落于西方。清宣统辽宁');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清文宗咸丰十一年十月十五日', '1861.11.7', '夜，有火西流，声若雷。民国');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清穆宗同治元年四月', '1862.4.29-5.27', '流星起自西南，没于东北，光照耀如昼。清光绪山东');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清穆宗同治元年六月十五日', '1862.7.11', '午刻，空中有物，自东南来，目如炬，身带赤霞，约二丈许，飞舞西去。清同治湖北');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清穆宗同治元年六月', '1862.6.27-7.26', '星陨。民国河南');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清穆宗同治元年夏', '1862．夏', '昼有火毡，自东而西。清光绪湖北');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清穆宗同治元年七月初二', '1862.7.28', '1053流星夕，东北有大星坠下，先有声响，流光甚长。民国浙江');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清穆宗同治元年七月二十二', '1862.8.17', '夜，东北有彗星流入海中，光芒闪烁，声若雷鸣，潮为之沸。清光绪浙江');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清穆宗同治元年七月', '1862.7.27-8.24', '夜有星，自南而北，大如轮。清光绪河北');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清穆宗同治元年八月十九日', '1862.9.12', '夜，东北有星，大如月，色如炉铁，初出，声则凄凄然，光芒闪烁，人不能仰视，顷之，向北一泻数丈，欲坠复止，止辄动摇，直至半空，忽如银瓶乍破，倾出万斛明珠，缤纷满天，五色俱备，离地丈许始没，犹觉余霞散彩，屋瓦皆明。清同治湖北');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清穆宗同治元年九月二十四日', '1862.11.15', '晚，天裂有光，声如雷。清光绪山西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清穆宗同治元年九月', '1862.10.23-11.21', '星陨西南而有声。清光绪山西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清穆宗同治二年正月三日', '1863.2.20', '许瑶光定浙江诗的引言：癸亥正月三日，星陨营前，大如车艳，拍拍如鸟，青荧照数里，逾时始灭，军士惊讶，莫辨灾祥。民国浙江');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清穆宗同治二年正月初八', '1863.2.25', '申刻，有声如霹雳，起自西北，陨火如斗。清光绪浙江');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清穆宗同治二年四月二日', '1863.5.19', '有星如斗，自巽方起，没于亥。清光绪河北');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清穆宗同治二年四月三日（己卯）', '1863.5.20', '晚，有星大如斗，后连数小星如火球，起子东南，流于西北，落后有声如雷。清同治河北');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清穆宗同治二年四月', '1863.5.18-6.15', '有火星大如斗，自东南而西北，隐隐有雷声。清同治河北');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清穆宗同治二年六月一日', '1863.7.16', '晚，有星光起于东北，落于西南，大如斗。清同治河北');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清穆宗同治二年七月', '1863.8.14-9.12', '又有大火星落，如盂大，落处闻天鼓声。清同治河北');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清穆宗同治二年十月十一日', '1863.11.21', '夜三更，有红星大如斗，由西南飞至东北，后有小星无数随之，忽有霹雳声从空一震，山鹪皆呜，俗云天鼓响。清同治湖北');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清穆宗同治二年', '1863', '城东北星陨，有声，光芒四射。民国河南');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清穆宗同治三年四月初二', '1864.5.7', '有星大如斗，自东南向西北，火光耀目，隐隐如雷声，枥马皆惊。清光绪河北');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清穆宗同治三年四月初三', '1864.5.8', '星落如火氇，自东南飞至西北。清同治河北');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('请穆宗同治三年七月', '1864.8.2-31', '西南方天裂，有光如火，大星坠。清同治安徽');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清穆宗同治三年十月朔', '1864.10.30', '夜，有星陨南方，光芒如斗。清光绪广东');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清穆宗同治三年十月二十日', '1864.11.18', '戌时，有大星如毽，尾带火光数尺，由东北向西南落去，须臾闻天鼓声，屋瓦皆蝼，纸窗簌簌作响，远近闻之。清宣统山东');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清穆宗同治三年十二月十八日', '1865.1.16', '夜，子丑间，天鸣如雷数声，北流至东南。清同治河北');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清穆宗同治四年三月', '1865.3.27-4.24', '赤，如云自气东南直响西北。清光绪');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清穆宗同治四年四月初五', '1865.4.29', '有大星如斗，自东南坠西北，声震如雷。清光绪河南');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清穆宗同治四年四月十四日', '1865.5.8', '通渭、泰安，火光西现，如星陨。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清穆宗同治四年四月二十九日', '1865.5.23', '夜，有星陨于东南。清光绪上海');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清穆宗同治四年六月', '1865.7.23-8.20', '有火光如毡，自西南流坠东北角，有声瑚然。清同治江西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清穆宗同治四年七月四日', '1865.8.24', '戌刻，有流星大倍于月，自东北向西南去，色深碧，照地如白昼，星尾拖红光一道，长丈余，微有声，如鸟展翼欲坠，火星如爆竹余烬。清光绪上海');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('精穆宗同治四年七月十五日', '1865.9.4', '巳时，有黑烟一道，横空飞过，声响四，震若炮。清光绪山西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('请穆宗同治四年七月二十一日壬申', '1865.9.10', '午后，有大星曳尾，蜿蜓如蛇，长三尺许，自东南陨于西北，声如爆仃。清光绪陕西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清穆宗同治四年七月', '1865.8.21-9.19', '有大星陨，光如月。清光绪上海');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清穆宗同治四年九月初七', '1865.10.26', '夜，有大星白光数丈，自西而东，须臾，色忽赤绿，陨地有声如震。清光绪');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清穆宗同治四年十月癸亥', '1865', '天狗起于北方，入于东南。清同治河北');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清穆宗同治五年（丙寅）三月初二', '1866.4.16', '星陨，光长数十丈，照耀屋宇，飒飒有声。清光绪贵州');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清穆宗同治五年夏', '1866．夏', '有流星自西而东，大如斗，去地约三丈许，光烛暗室，随有声如雷。清光绪上海');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清穆宗同治五年七月初八', '1866.8.17', '夜，东北有赤星坠西南，隐隐如雷震二声。清同治安徽');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清穆宗同治五年七月十五日', '1866.8.24', '巳时，有大星如斗，自东南流西北。清光绪河北');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清穆宗同治五年七月十七日', '1866.8.26', '夜，有星自东而西，光如月。清光绪上海');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清穆宗同治五年七月十八日', '1866.8.27', '夜，有大星自东南移西北，光如匹练，微有声。及没，有红光曳尾，烛地如火。清光绪江苏');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清穆宗同治五年九月初七', '1866.10.15', '夜，有大星白光长数丈，自东南流西北，变赤绿色，坠地散．声如大炮，远近皆骇。清同治湖南');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清穆宗同治五年九月二十四日', '1866.11.1', '初鼓时，天裂，有光，声如雷。清光绪山西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清穆宗同治五年九月二十五日', '1866.11.2', '未时，有光若流星，起自东南，没于西北。清光绪河北');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清穆宗同治五年九月二十六日', '1866.11.3', '夜，人定后，有星如盘，环转不定，色青碧，动摇有声，光烛地，良久，掣为练向东北没，已而，有硫气。清同治湖南');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清穆宗同治五年九月', '1866.10.9-11.6', '夜，陨火，天鼓鸣。清光绪山西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清穆宗同治五年十一月十二日', '1866.12.18', '夜，金龙见，火光遍身，自东南向西北去。清光绪山东');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清穆宗同治六年六月十二日', '1867.7.13', '戌初，有星自乾方下坠，横飞西南，初青色，后分二条红光而响，光照墙垣。清光绪广东');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清穆宗同治六年夏', '1867．夏', '有大星自西北陨东南，尾有金光数丈，天鼓鸣。清宣统陕西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清穆宗同治六年七月初三', '1867.8.2', '戌刻，半空中飞红星，大如栲，似兽形，由北向南飞去。清光绪陕西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清穆宗同治六年七月十五日', '1867.8.14', '巳时，天狗星起东北，向西南，声如雷。清同治河北');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清穆宗同治六年七月十八日', '1867.8.17', '夜，流星如火。清光绪山西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清穆宗同治六年七月二十一日（壬申）', '1867.8.20', '未刻，有星曳尾，蜿蜒如蛇，长三尺许，自东南陨于西北，声如爆竹。清光绪陕西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清穆宗同治六年七月二十三日', '1687.8.22', '午时，天鸣如炮，自东南起，其形如练，西北行空约六、七里许，化为烟气而散。清光绪陕西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清穆宗同治六年九月九日', '1867.10.6', '酉刻，月色昏黄，天东南忽闪一凹，火光焦然，望如深坑，中有人物冲突，楼台倾倒状，更有流星似箭，旁出射击状，少顷渐如烟云，漫灭不见。清光绪');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清穆宗同治六年秋', '1867．秋', '天火流。清光绪广西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清穆宗同治六年己亥', '1867.11.18', '有星流入翼、轸。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清穆宗同治六年十一月初二', '1867.11.27', '夜，流星自东南向西北，如月，已而散乱奔流，声如激矢。清光绪湖南');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清穆宗同治六年十一月二十二日', '1807.12.17', '有若流星者自东来，长丈余，首圆而尾锐，色赤，迤而西，轰轰有声。清同治湖南');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清穆宗同治六年十二月初二', '1867.12.27', '大星孛于西，光芒万丈，作作有声，坠于响水堡城南角。民国陕西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清穆宗同治七年正月二十三日', '1868.2.16', '五鼓，有星自西北南奔，忽散如小星数十，若坠状，光气烛天，所过一道皆青、碧、杂色，顷之，天鼓鸣，有声如雷，起东北至西南止。清光绪陕西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清穆宗同治七年二月', '1868.2.23-3.23', '有星如斗，落西南隅，赤光灼天，空际有声。清光绪陕西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清穆宗同治七年四月', '1868.4.23-5.21', '夜，有流星如水。民国山东');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清穆宗同治七年五月十五日', '1868.7.4', '申刻，有流星大如斗，自东而西，过处有五色云，曲曲蛇行，已而天鼓大震，声i殷墙壁，逾时云气始散。清光绪河北');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清穆宗同治七年六月初八日', '1868.7.27', '二更时，有星如火，光炎万丈，坠西北隅，，月色为晦。清同治湖南');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清穆宗同治七年七月', '1868.8.18-9.15', '有星大如斗，自南坠西，声隆隆如雷，金：光眩目。流星清光绪');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清穆宗同治七年八月', '1868.9.16-10.15', '有火星陨于城外东北隅。清光绪陕西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清穆宗同治七年九月十五日（己丑）', '1868.10.30', '玉田有火光至空际，化为白气，长丈许，其中有声如鼓。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清穆宗同治七年九月=十九日', '1868.11.13', '大星坠子东方，随有白气如练，逾时始散。清光绪山西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清穆宗同治七年九月', '1868.10.16-11.13', '天狗流于西北，火光如炬。清光绪山东');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清穆宗同治七年十月十八日', '1868.12.1', '西宁县星陨，从正北向西北落，大如西瓜，有光。清宣统');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清穆宗同治八年二月一日', '1869.3.13', '白昼，有圆而红者，如火，大小三四，大者如轮，由东而西，随即天鼓鸣。民国山西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清穆宗同治八年三月七日', '1869.4.18', '有大星北去，声如雷。清光绪河北');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清穆宗同治八年春', '1869．春', '有星陨东北方，尾光甚长，声如地震。民国河北');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清穆宗同治八年六月二十日', '1869.7.28', '戌掰，星陨，自东至西，光芒数十丈，洒洒有声。、清光绪贵州');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清穆宗同治八年夏', '1869．夏', '有火自西南流西北，形如匹练，余光经时乃没。清同治江西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清穆宗同治八年十月十日', '1869.11.13', '夜，天见火块，自北而南，声如裂帛，赤光烛地。清同治江西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清穆宗同治八年十月', '1869.11.4-12.2', '天见光块，自北而南，声光赫然。清光绪湖北');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清穆宗同治八年十月十五日', '1869.11.18', '三更时，大星从西宁县东南坠于西北，有声如雷。清宣统');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清穆宗同治八年十一月初二', '1869.12.4', '忽如雷震一声，天自裂开，广约数十丈，空空洞洞，毫光闪烁，中坠一巨块，红如炉火，飞走四方，光芒照物，明如白昼，一时不见，旋一黑巨块接迹而来，顷刻天昏地暗。清光绪湖南');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清穆宗同治八年十二月二十二日', '1870.1.23', '黄昏，有大星西垂动摇，坠声如蒸爆，火光碎灿，旋有气缕腾龙矫搦空若白练，与余霞相错。清同治江西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清穆宗同治九年正月二十九日', '1870.2.28', '戌刻，大星殒于星、张之次，裂作数块，其光如灯。清同洽江西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清穆宗同治九年正月', '1870.1.31-3.1', '赣州西方，有星坠地，白光长数丈，缭绕如草书月字，逾刻灭。清同治江西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清穆宗同治九年三月初八', '1870.4.8', '酉末，西方有星化为二，去尺许，相同，光如火。清光绪湖北');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清穆宗同治九年四月十三', '1870.5.13', '夜，东南有流星动盗，顷刻，长大如柱，色白，直贯西北，形愈大，光如月。清光绪湖南');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清穆宗同治九年四月十四日', '1870.5.14', '夜半，天光如昼，有梗降天，如虹数仞，飞过有声如怒涛。清同治湖南');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清穆宗同治九年庚午七月庚辰', '1870.8.12', '戌刻，有星西北流，大如盏。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清穆宗同治九年七月十九日', '1870.8.15', '1061流星夜，有大星陨于东南。民国浙江');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清穆宗同治九年九月十二日', '1870.10.6', '申时，有火毡，其尾如彗，长数丈，自东南而西北，经天飞过，及地而灭，有寻其所灭之处者，远近皆然，相传为异。清光绪山东');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清穆宗同治九年九月十三日', '1870.10.7', '闻有声自北而南，似雷非雷，似炮非炮，遥见白气一条，坠落部南桥圩地方。清光绪广西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清穆宗同治九年九月', '1870.9.25-10.23', '有星大如斗，自东北流于西南，其声如雷，余焰闪烁，有数丈许。清光绪河南');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清穆宗同治九年十一月十一日', '1871.1.1', '戌时，大星自北陨南，有声，光芒烛天。清光绪山西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清穆宗同治九年十一月', '1870.12.22-1871.1.20', '有火光自东而南，声震林木。清光绪河南');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清穆宗同治九年十二月初三', '1871.1.23', '辰时，青天无云，雷鸣轰轰然，或云天鼓鸣，有人见西北方于轰声后，有流星坠下，火光灿然。清同治安徽');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清穆宗同治十年三月乙酉', '1871', '流星出于天市垣。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清穆宗同治十年三月', '1871.4.20-5.18', '星陨有光，从东南起，往西北，坠地有声。民国青海');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清穆宗同治十年六月十六日', '1871.8.2', '夜，有光自东移西，陨于地，大于鸡子，光焰甚长。清光绪上海');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清穆宗同治十年六月二十六日', '1871.8.12', '夜，东南有一星赤如火，徐徐挂下。清光绪上海');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清穆宗同治十年六月二十八日’4', '1871.8.14', '戌刻，有星坠东南，有光。清光绪湖北');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清穆宗同治十年七月十五日', '1871.8.30', '戌之初，有星自南海北流。（采访册）清光绪广东');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清穆宗同治十一年三月初三、四、五', '1872.4.10-12', '戌刻，火大星自东而西，坠地有声。清光绪湖北');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('1062', '1872.7.18', '夜，有星大如盆，自东流过佛山，光照地，其行有声。民国广东');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清穆宗同治十一年七月二十日', '1872.8.23', '酉时，西方流星大数尺，长一、二丈，白光耀户。清光绪湖南');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清穆宗同治十一年七月二十九日', '1872.9.1', '夜，流稷自东北过西南，长数十丈，光焰烛墙壁，作青白焰，声隐隐如雷。清光绪湖南');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清穆宗同治十一年八月二十七日', '1872.9.29', '夜，大姚西南方彗星陨，光大如斗。清光绪');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清穆宗治十一年八月', '12.9.3-10.1', '剑川有大星陨于西北，殷殷有声。清光绪');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清穆宗同治十一年十一月二十五日', '1872.12.25', '夜，有大星自肃州西北流于东南方，坠地有声。清宣统');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清穆宗同治十二年正月癸未', '1873.1.31', '天狗下坠，声如雷。清光绪山东');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清穆宗同治十二年正月十五日', '1873.2.12', '平旦，天裂口于正北方，精光闪烁，声如雷轰，移时始寂。清光绪湖北');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清穆宗同治十二年正月', '1873.1.29-2.26', '右星下陨，其声如雷。民国山东');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清穆宗同治十二年三月三日', '1873.3.30', '酉时，有星大如瓜，自西北流入东南，赤光四射，火珠错落，寻闻天鼓大震。清光绪湖北');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清穆宗同治十二年六月十三日', '1873.7.7', '夜，有流星光芒照地，坠予西南，其声如雷。有星自东南越西北而陨。清光绪广东');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清穆宗同治十二年六月二十日', '1873.7.14', '戌刻，有星大如盂，光如月，起东北，陨西南，逾刻，天鼓鸣。民国河北');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清穆宗同治十二年六月乙巳', '1873', '天狗流于西北，火光如炬，振振有声。清光绪山东');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清穆宗同治十二年秋', '1873．秋', '有流星，其光如昼，有声。民国广东');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清穆宗同治十三年六月初十', '1874.7.23', '夜，有星自东陨西，天鼓鸣。清光绪山西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清穆宗同治十三年六月', '1874.7.14-8.11', '有流星自西北方直入东南，光耀异常，后有一小星随之。清光绪安徽');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清穆宗同治十三年九月三十日', '1874.11.8', '三更时，有大星光如月，自南流至北，隐隐作雷声。民国浙江');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('请穆宗同治十三年', '1874', '星陨有声。清光绪广东');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('精德宗光绪元年正月二十四日', '1875.3.1', '日出时，有声如震雷，殷殷然，见有火光如炬坠下。民国云南');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪元年三月朔', '1875.4.6', '夜，有火光自西而东，声如雷。清光绪山东');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪元年四月二十九日', '1875.6.2', '夜，有巨星在天东南，尾长数丈，赤光烛天，俄而，有声如雷，猝然坠地而灭。盾闻山西通省及陕省东偏豫省河北一带，是夜俱有所见。星家以为天狗星坠地云。清光绪山西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪元年五月', '1875.6.4-7.2', '夜，有火毯自东北而西南，顷，光始息。民国河北');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪元年六月初五日', '1875.7.7', '酉刻，有星坠于乾、亥方，青光照耀墙垣，其大如斗，不知落何地。清光绪广东');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪元年夏', '1875．夏', '有大星飞流，色碧而芒寒。清光绪江苏');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪元年九月中旬', '1875.10.9-18', '日将晡，无云，忽雷声大震，火光南射，过处有白气如匹练。清光绪河南');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪元年秋', '1875．秋', '无云自雷，白气经天。民国河南');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪元年十月十八日', '1875.11.15', '夜，天鼓鸣，天火流，自南而北，大如斗，光芒数丈。清光绪广西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪元年十月二十三日', '1875.11.20', '星陨声如雷。清光绪山东');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪元年', '1875', '星入月中。清宣统广东');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪二年二月', '1876.2.25-3.23', '有流星火如瓜，其色碧，自东西流。清光绪安徽');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪二年八月十二日', '1376.9.29', '戌时，有火星由西方飞入月官，光华四散，声震如雷。民国广西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪二年十月三十日', '1876.12.15', '夜二更，有大星坠空，自东北下西南，烂然如满月，色略带青，后曳光线约二、三丈，瞬息即灭。清光绪上海');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪三年五月二日', '1877.6.12', '夜，有星光如昼，自东而西，顷刻始灭。民国陕西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪三年五周十四日', '1877.6.24', '夜，有火光自天而下，光烛数十里。清光绪山西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪三年七月初六日', '1877.8.14', '晡时，有大星色碧，将至地，向西斜飞，后有帚如彗。民国贵州');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪三年七月二十四旧', '1877.9.1', '有火，从西南流至东北，形如练，光芒长数丈，有声如雷。民国福建');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪三年九月', '1877.10.7-11.4', '星陨。民国福建');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪三年十一月初九', '1877.12.13', '平明，有大声震空中，自西而东，随光如i炭，焰长尺余，自东流于西方，盖妖而兼星陨也。清光绪甘肃');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('圈园', '1878.1.12', '夜，有火光如斗，自西北向东南，有声如雷。民国河南');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪五年六月二十一日', '1879.8.8', '寅刻，天鼓鸣三声，南北陨红星，大如斗。清光绪陕西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪五年十月某日', '1879.11.14-12.12', '初昏，有流星大如月，自东南而东北行，有声，经过之处，光如白练横空，许久方灭。民国辽宁');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪五年冬', '1879．冬', '玉田见火氆飞向东北，其声如雷。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪六年二月三日', '1880.3.13', '昧爽，东南角有声如雷，似天裂者，光如电，长十余丈，逾时始灭。民国山西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪六年二月初六', '1880.3.16', '戊刻，空中有火光，大如斗，流入西南。清光绪河南');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪六年四月', '1880.5.9-6.7', '有星自南而北，大如斗，明如月。民国陕西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪六年六月二十八日', '1880.8.3', '戍时，有光烛地，仰视大星如鹅卵，极明，至西北，有声而没。民国浙江');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪六年七月二日', '1880.8.7', '午后，星陨有光，声如鼓鸣。民国河南');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪六年七月', '1880.8.6-9.4', '有大星，陨于邑之东南，天明若晓。清光绪浙江');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪六年九月十八日', '1880.10.21', '空中有炮声，红光下坠，圆如镜，至半，分数团，落西隅。清光绪云南');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪六年十月二日', '1880.11.14', '戌刻，飞星入月。清光绪台湾');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪六年十一月二日', '1880.12.3', '空中有火光，声如雷。清光绪山东');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪六年十二月二十一日', '1881.1.21', '寅时，天鼓鸣，有大星陨于东方，其光荧荧。1066清光绪山西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪七年正月八日', '1881.2.6', '有星自天而下，首如箩，尾似帚，身象龙，腰长七、八尺，金光闪灼，离地丈余而飞，照地皆亮。民国云南');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪七年二月丁亥', '1881', '巳刻，正阳洞口五里，正谊各乡民见空中有如彗星，光芒四射，声若雷殷，自东北投于东南。清光绪四川');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪七年三月十六日', '1881.4.14', '夜，有星坠西北，光芒四射。清光绪广东');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪七年四月二十六日', '1881.5.23', '夜，天鼓鸣，天火流。民国广西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪七年五月', '1881.5.28-6.25', '有星尾如剑，长七、八尺，自东北移西北灭。清光绪广东');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪七年七月十六日', '1881.8.10', '戌时，星陨，其大如斗，处处皆见。清光绪云南');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪七年', '1881', '巧家东山之巅，有白光晶莹，从空坠下，声如雷鸣者三。清光绪');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪八年五月', '1882.6.16-7.14', '夜，有流星状如月，绕大坏飞，大放光明，所过处隐隐如雷有声，城乡皆见。民国福建');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪八年九月十一日', '1882.10.22', '酉正初刻，西方有流星一筒，如月绕东行，出贯索之左，入魏、赵、九河之间，碧色，有光，有尾迹。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪九年三月二十三日', '1883.4.29', '夜，楚雄天鼓鸣，自南而北，殷殷若雷，光明如昼。清光绪');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪九年五月初二', '1883.6.6', '夜，天鼓鸣，天裂，见五色光，移时乃隐。大星芒数尺，自东流于西北，前后小星随之，其光烛地，殷殷有声。清光绪广西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪九年五月初三', '1883.6.7', '三更，忽闻天上炮声大响，仰观天上，如门户大开，洞见无涯，五色祥云，照耀如昼，霎时即灭。民国广西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('流星', '1883.6.12', '夜，有物如练，自东南直抵西北，光彻筵宇，落时轰者三。民国广西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪九年五月十三日', '1883.6.17', '亥时，有流星自北而南，火光烛天，坠地有声如雷。清光绪云南');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪九年十一月二十一日', '1883.12.20', '夜戌时，有大星如碗，自东坠西，色碧，随有声隆隆如雷，约数刻方止。清光绪四川');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪九年十一月', '1883.11.30-12.28', '昏，见有孛星于东南，光如昼，渐升声如鸟拂，初入西北而陨，声訇然，俄而地震。清光绪四川');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪九年十二月十七日', '1884.1.14', '夜，有大星陨于东北，及地如转滚状。民国河南');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪十年二月九日', '1884.3.6', '夜，有星陨，大如斗。民国河南');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪十年八月二十三日', '1884.10.11', '有大红星落于南方，望之甚近。民国云南');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪十年十一月一日', '1884.12.17', '戍初，西北天际有白气，长三十余丈，旋见红光下坠，形如流星。清光绪湖北');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪十一年五月十八日', '1885.6.30', '戌刻，有火光如龙，声若雷，起西南向东北去。清光绪山东');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪十一年七月', '1885.8.10-9.8', '星陨，斗大，有火光，其声如雷。清光绪台湾');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('德清宗光绪十一年七月三十日', '1885.9.8', '夜，满天星河，天河内忽降一星，大如月，自东向西，奋飞而去，光辉闪烁，轧轧有声。清光绪四川');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪十一年秋', '1885．秋', '有大星北流，光芒尺许，高数丈，远近皆见之，俄闻天鼓鸣于北方。民国山东');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪十一年十月二十日', '1885.11.26', '夜，星陨。清光绪云南');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('1068', '1885.11.27', '夜，天星飞，自南而北。清光绪广东');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪十一年十月二十二日', '1885.11.28', '初昏，巨星陨于西北，色赤如火，光芒长约三、四丈。清光绪陕西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪十一年十一月十八日', '1885.12.23', '夜，有一星大如斗，西陨，声响，火光。清光绪广东');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪十一年十二月十一日', '1886.1.15', '星陨，向西飞落。民国云南');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪十二年六月十三日', '1886.7.14', '西南有流星陨，声如雷，光芒烛地。民国广东');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪十二年十月二十七日', '1886.11.22', '初昏，有流星赤色，大如斗，光芒烛天，自东北入西南，其声震地。清光绪广东');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('注：光绪广东《电白县志》卷29页25作', '12.22', NULL);
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪十三年九月十五日', '1887．10.31', '有火光起东北，声如雷。清光绪山东');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪十四年正月上句', '1888.2.12-21', '星陨，光如电。民国云南');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪十四年正月丁未', '1888', '夜，有流星正赤如火，大如碗，尾长数丈，坠城东南，火焰烛地。清光绪四川');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪十四年三月二十日', '1888.4.30', '夜，有大星落，其声甚响。民国广东');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪十四年四月二十二日', '1888.6.1', '夜，有星自东坠于西，竟天皆赤，坠地有声。清光绪广东');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪十四年五月初旬', '1888.6.10-19', '亥时，大星陨，光如闪电。民国云南');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪十四年六月初四日', '1888.7.12', '酉刻，有星白色，首大如斗，身长六、七流星丈，自城东横行，至西南殁，其行甚迟，亦不甚高，若从屋脊过者。贵州');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪十四年七月', '1888.8.8-9.5', '有火星大如斗，从西北起落予东南，天鼓震地半时许。民国山西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪十四年十月十四日', '1888.11.17', '夜，有星如丸，穿入月中。民国广西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪十五年七月七日', '1889.8.3', '戌时，外塔卧枉矢东流有声，其色黑。民国湖南');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪十六年七月间', '1890.8.16-9.13', '夜半，天有声，自东北至西南，开一缝，宽二、三尺，长数十丈，内有灯如电，形大如斗，忽缝合，映红线一条，俗称呼为天睁眼。民国山西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪十六年八月十三日', '1890.9.26', NULL);
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪十六年', '1890', '星入月中。民国广东');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪十七年五月十二日', '1891.6.18', '夜，星陨北方。清光绪山东');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪十七年七月五日', '1891.8.9', '夜半，有流星飞入河，共光烛地，长丈许，万桥湾地方报有星陨。民国湖北');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪十七年七月', '1891.8.5-9.2', '天鼓鸣，星陨。清宣统山东');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪十七年八月十四日', '1891.9.16', '夜，有大星自西南驰陨东北，众小星随之，光夺目如激电，须臾，天鼓大震，声从西南激向东北。民国山西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪十七年十月二十八日', '1891.11.29', '上午六点钟，天鼓鸣，白气冲天，逾时始灭。民国河南');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪十七年十二月二十一日', '1892.1.20', '夜，星陨。民国甘肃');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪十八年正月二十日', '1892.2.18', '白昼，天有大星如火，坠自西，声如雷，蜿蜒者久之。1一清宣统甘肃');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪十八年十月', '1892.11.19-12.18', '将晚，空际有火光如炬，自西南而东北，火坠光灭，余烟蜿蜒，经时不散，隐隐有雷声。清光绪河南');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪十八年十二月二十一日', '1893.2.7', '薄暮，有星大如盂，自西南飞向东北隅，陨。民国四川');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪十九年六月十五日', '1893.7.27', '夜，一火星向东南角降下，落于猪场。清光绪贵州');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪十九年六月', '1893.7.13-8.11', '有流星见于邑之东方。民国四川');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪十九年夏', '1893．夏', '有星如月，坠于江干，城中皆见。清光绪四川');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪二十年四月十三日', '1894.5.17', '夜，庄浪大星有柄，自东北飞陨西南，光如燃炬。清宣统');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪甲午年', '1894', NULL);
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪二十一年五月八日', '1895.5.31', '有大星从天陨她，声如雷鸣。民国广西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪二十一年七月', '1895.8.20-9.18', '有大星落地如炮响，四小星随之。民国广西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪二十一年十月六日', '1895.11.22', '亥时，有星如火，自西南飞东北落，有声如雷，天无云而自震。民国云南');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪二十一年十月十五日', '1895.12.1', '星响如雷。清光绪广东');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪二十一年十二月三十日', '1896.2.12', '大通县天东连响数声如炮，大星如斗，坠于西南方，后随青烟一道。清宣统');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪二十二年正月十九日', '1896.3.2', '酉正二刻，巽方亮星急行如箭落坤方，声似雷。1071流星民国辽宁');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪二十二年三月二十四日', '1896.5.6', '午时，有星昼徒。民国广西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪二十二年三月二十九日', '1896.5.11', '夕，有火星由东流西，连响三声。民国广西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪二十二年四月戊子', '1896.6.4', '南乐有火光径尺，明如月，自西南往东北，尾长丈许，忽炸为火星四散。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪二十三年正月十二日', '1897.2.13', '夜，大星自庄浪西南，飞陨东北，光如燃炬，须臾，空中有声。清宣统');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪二十三年五月五日', '1897.6.4', '戌时，天有火光，大如斗，圆如毯，自旧城东北起，向西南行数十里没。清宣统山东');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪二十三年五月十日', '1897.6.9', '夜，大星落。民国广东');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪二十三年五月上旬', '1897.5.31-6.9', '黄昏时，有大星如卵，其赤如火，自天而坠，到半空丽分为二，各大十围，照耀如昼，将及地，复合为一。民国山东');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪二十三年五月十九日', '1897.6.18', '有大星自京南流于西北，天鼓鸣。民目河南');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪二十三年五月二十九日', '1897.6.28', '黄昏，有星光大如斗，向西北陨，天鼓鸣，声如雷，片时始断，民国河南');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪二十三月八月初旬', '1897.8.28-9.6', '夜四更，空际有震响声，天顶忽裂，阔丈余，金光灿烂，能照秋毫，居民未寝者，见庙脊绘像须眉宛然，行人早起，亦-相传告，俗以为天门开云。民国四川');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪r酉八月某日', '1897.8.28-9.25', '申刻，晴无片云，有大声若雷，自东北晦达西南，约五分钟。随声起白气一道，亦自东北而西南，状如浮云，又仿佛如银汉，久而始灭。民国安徽');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪二十三年九月辛亥', '1897.10.20', NULL);
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪二十三年十一月二十五日', '1897.12.18', '1072夕，有火自东北流向西南，轰然有声，随有白烟一道，屈曲如虬龙，久而后灭。民国安徽');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('、清德宗光绪二十四年七月', '1898.8.17-9.15', '有大流星，巨如碗，自未向戌而陨。民国河南');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪二十四年九月二十日', '1898.11.3', '天鼓鸣，鸣毕望之，有黑气一道，内带氇一只，色近蓝，顷刻，形踪全消。民国山西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪二十四年立冬后十日', '1898.11.17', '有星大如斗，自东北流入西南方而没，其声如雷，鸟鹊皆散。民国渐江');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('靖德宗光绪二十四年十一月初四', '1898.12.16', '天鼓鸣，星陨。民国河南');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('请德宗光绪二十五年二月十七日', '1899.3.28', '夜，星陨有声。民国广西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪二十五年三月十八日', '1899.4.27', '黄昏，有星起子东北，大如圆月，火光四射，御风而行，直抵晋祠西南，落于空际，其声如雷。清光绪山西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('靖德宗光绪二十五年五月十二日', '1869.6.19', '戌时，有一大星，西北起，光芒闪灼，长数丈，逶迤东南而去。清宣统陡西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪二十五年五月十七日', '1899.6.24', '夜，陨星而雷，见。（小方壶斋诗存）民国江苏');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪二十五年五月二十四日', '1899.7.1', '申刻，有流星状如喇叭，自西如东，有声。民国陕西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪二十五年六月二日', '1899.7.9', '天啸，有光如白练，声如雷。民国湖北');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪二十五年已亥七月十七日', '1899.8.22', '夜，遵义见有星，大几埒月，向北坠去。民国贵州');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪二十五年七月', '1899.8.6-9.4', '昼星陨，自北而南。清光绪山东');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪二十五年十一月丁未', '1899.12.5', '夜，阴晦，火光流空中，长数丈，其明如月。清光绪河南');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪二十五年十一月六日', '1899.12.8', '夜，有流星，白光照地，自县东而西。民国浙江');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪二十五年十一月乙未', '1899', '夜，南乐有火光流空中，其明如月。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪二十五年十一月十九日', '1899.12.21', '有大星流于东井。（鹤塘随笔）民国江苏');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪二十六年正月朔日', '1900.1.31', '有八星互为缠绕，向北角出入，光芒四射。民国福建');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪二十六年六月二十六日', '1900.7.22', '夜，有星如杯大，光如桶，自东飞至西。民国贵州');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪二十六年夏月', '1900.5.28-6.26', '初昏时，有流星如火，自南向北飞去，声如响箭，光照一室，尾有余焰，若喷珠然。民国四川');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪二十六年七月初九', '1900.8.3', '亥初，有星如火，焰色正赤，东起西没，经天有声，桑植、龙山同时皆见，均报县府有案。民国湖南');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪二十六年七月', '1900.7.26-8.24', '东南方有星下坠。民国淅江');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪二十六年十月', '1900.11.22-12.21', '初昏，有光如炬，大如斗，东南向坠。民国河南');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪二十七年四月十七日', '1901.6.3', '寅时，星陨有声。民目四川');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪二十七年五月望', '1901.6.30', '夜二鼓，空中有物如火毡，大如巨盘，出东转南分为三，向西流，光四射而灭。民国四川');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪二十七年九月四日', '1901.10.15', '初更时，有大星陨于西南，光芒如斗，声震若雷。清光绪河南');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪二十七年十二月二十八日', '1902.2.6', '夜，有光南流如昼，天鼓鸣。清宣统河南');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪二十八年正月初七日', '1902.2.14', '黎明，有星陨于西北。清光绪河南');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪二十八年九月初五', '1902.10.6', '亥时，大星自西北陨于南。清光绪山东');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪二十九年正月二十九日', '1903.2.26', '有星陨予山丹县西北。清宣统');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪=十九年五月', '1903.5.27-6.24', '大星自西而东，其行有声。民国陕西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪二十九年六月', '1903.7.24．-8.22', '某夕，彗星见，俗谓长尾星，约二丈，红首绿身，由南向北，直射而下。民国湖南');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('请德宗光绪二十九年七月二十三日', '1903.9.14', '有星陨于山丹东北。清宣统');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪二十九年十月二十一日', '1903.12.9', '有大星陨于东方，白色，从空直下，光大丈余，没烟波中。民国四川');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('请德宗光绪三十年五月十三日', '1904.6.26', '夜，有流星降于南方，火光烛天。民国云南');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪三十年七月二十三日', '1904.9.2', '有星陨于山丹县东北。清宣统');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪三十年八月二十二日', '1904.10.1', '戌时，有星如月，陨于牝方。民国河南');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪三十年九月一日', '1904.10.9', '晨，大星落于西方。民国陕西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪三十年九月', '1904.10.9-11.6', '有流星，经五寸许，后随二星差小，光焰荧荧，自东徂西。民国四川');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪三十年十一月', '1904.12.7-1905.1.5', '星陨有声。民国江西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪三十年', '1904', '有大星如斗，自东而西，有声如雷随之。民国河南');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪三十年十二月', '1905.1.6-2.3', '县东天上突然裂开。毫光彻地。现出金红色丈余。移时轰轰发声，似开炮然。远近皆闻。民国湖南');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪三十一年五月十九日', '1905.6.21', '流星夜，火球坠地有声。民国湖北');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪三十一年七月二十六日', '1905.8.26', '酉时，有星如十一、二夜月，从西南方坠地，飞行有蒂。清宣统青海');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪三十一年九月', '1905.9.29-10.27', '贵德见大星西坠，发光，有声。民国青海');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪三十一年十月十六日', '1905.11.25', '申刻，灵州一大星如斗，从东方起，落于正西，其声如雷，落后，光芒冲入天中，其形蜿蜒，越时始灭。清宣统');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪三十一年十一月九日', '1905.12.5', '申、酉之际，有大星陨于西北，赤气一缕，倏变白气而散。清光绪甘肃');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪三十一年十一月十六日', '1905.12.12', '辰刻，靖远山丹有星大如斗，红如日，又有白光一道，自西南流于东北，扶摇直上，移时始灭。清宣统');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪三十一年', '1905', '有大星陨于西，白光一道，经时而散。民国甘肃');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪三十二年二月', '1906.2.23-8.24', '大星又落于西方。民国陕西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪三十二年三月二十日', '1906.4.13', '辰，有红光一团如火，由东北角起，后有白光一条，长丈余，随之后自西北落下，有声如雷。民国云南');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪三十二年十一月十六日', '1906.12.31', '昼间，中卫有大星，由南方出，陨于北方，红光婉蜒者时许，渐变青白光散。清宣统');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪三十三年三月', '1907.4.13-5.11', '有星陨于蒙山之麓，声如雷。清宣统山东');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪三十三年四月二十八日', '1907.6.8', '酉刻，南方上有电光一线，弯曲如龙，或见为火星，疾如箭，飞向正南方。民国四川');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪三十三年七月十一日', '1907.8.19', '1076酉刻，有大星南方来，贯中天，长三丈许，望之，如火龙然，移时方灭。民国河南');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪三十四年六月二十五日', '1908.7.23', '亥时，有流星自西北来，至东南而没，大如碗，尾长丈余，光射十里外。清宣统山东');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪三十四年六月杪，', '1908.7.25-27', '晚，九句钟，见正北方有火毡，光耀照人见影，自天坠地，俄顷始灭。民国山东');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪三十四年夏', '1908．夏', '有大星类月状，自北而南落。民国河北');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪三十四年七月中旬', '1908.8.7-16', '遵义县有星见，白光自北流南，占星者指为蚩尤旗。民国贵州');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪三十四年七月=十四日', '1908.8.20', '戌正二刻，正北偏西有流星如盂大，出文昌、内阶之间，往东南行，入河鼓，五色有光，有尾迹。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪三十四年七月', '1908.7．．28-8.26', '漉星自紫微垣西行，状如匹布，光耀人目，至大梁而坠。民国江苏');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪三十四年八月下旬', '1908.9.16-24', '天将晚，有星陨，自西北而东南，大如轮，宇宙皆明。民国河北');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪三十四年十月初四', '1908.io.28', '戌时，天有流火大如斗，自北而南，至河不见。清宣统山东');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪三十四年十一月朔', '1908.11.24', '夜，西北天上火光烛地，舟子皆见殆，开天门欤！民国浙江');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪三十四年十一月初三', '1908.11.26', '戌时，有星大如箕，自西北而东南，其光如电，食顷，有声如雷。民国测西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('流星', '1908.12.23-1909.1.21', '麻城天鼓鸣，天坼一丈余，光明如雪，移时乃止。清宣统');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清末帝宣统元年八月十五日', '1909.9.28', '三区雍和团刘生槐，夜行至朱砂堡，忽闻空中有声甚烈，警视，则碧天有痕，划然中开，一片金光蜿蜒，向西至灭。时他乡人皆有见之，未见有金光。民国湖南');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清末帝宣统元年八月二十四日', '1909.10.7', '申刻，有星光如火，大如箕，从辰巽方流经天顶，射入戌乾方，如箭冲天，声如雷震，郡民皆闻，随有黑气如大柱直竖接天顶，良久乃散。清宣统广东');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清末帝宣统元年八月', '1909.9.14-10.13', '大星陨于东南。民国浙江');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清末帝宣统元年九月某日', '1909.10.14-11.12', '未时，有星陨于西方，声如雷震，自气横空。清宣统广东');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('请末帝宣统元年十一月八日', '1909.12.20', '晚，红火亘天，天鼓鸣。民国河北');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清末帝宣统元年', '1909', '星陨。民国云南');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清末帝宣统二隼正月二十三日', '19io.3.4', '昧爽，大星陨东南，声如雷。民国河北');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清末帝宣统二年三月二日', '1910.4.11', '夜，有星自东北流西南。民国');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清末帝宣统二年六月', '1910.7.7-8.4', '仁怀县有星大如碗，坠下，距地数丈，自东横行，至西不见。民国贵州');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清末帝宣统二年七月二十三日', '1910.8.27', '初昏，有流星大如斗，自天市垣入亢、氐之度。民国河南');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('渍来帝宣统三年二月初一', '1911.3.1', '傍晚，有星大如箕，出自东南，落于西北，周围群星随之，其大如碗，光射数十里，明如白昼。（罗次访稿）民国');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清末帝宣统三年二月三日', '1911.3.3', '流星长十丈，大十围，尾似火星飞散，光烛暗室，行时有声。民国云南');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清末帝宣统三年二月初四', '1911.3.4', '戌时，有火光自东北向西南行，火灭，无云而雷。民国山东');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清末帝宣统三年二月', '1911.3.1-29', '有火毡大如斗，自西北至东南，隐隐有雷声。民国山东');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清末帝宣统三年三月八日', '1911.4.6', '申时，空中红光，自西南而东北，约数十里始灭，灭处无云，即有雷声。民国山东');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清末帝宣统三年六月', '1911.6.26-7.25', '有流星自西至东，炸裂有声。民国安徽');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清末帝宣统三年七月二十三日', '1911', '夜初更时，天上落一巨星，光芒四射，随处见之，惟所落地点，见者各处不同。民国责州');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清末帝宣统三年十月', '1911.11.21-12.19', '有星长七、八尺，大尺许，自南而北，似鸟飞过。民国云南');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清末帝宣统三年十一月', '1911.12.20-1912.1.18', '有星大如轮，自北而南，如鸟飞过。民国云南');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清末帝宣统三年十二月', '1912.1.19-2.17', '有大星自西南陨于东北，有雷声。民国山东');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('春秋周威烈王三年十一月', '约前423', '晋有火下于北方，其声如鼓。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('汉昭帝始元中', '约前83', '流星下燕万载官极，东去。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('汉灵帝中平中夏', '约187', NULL);
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明穆宗隆庆四年', '1570', '拖练星见于临县，长十余丈，自西北指东南，光采射人。清雍正');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙二年十一月十一日', '1663.12.11', '夜鸡鸣时，有异星见于东方，长数尺左右，朝拱向上，首尾如船，移时乃投，有声割然。清乾隆广东');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙七年七月', '1668.8.8-9.5', '妖彗见于西方，坠地有声。清道光河南');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙八年六月甲戌。', '1669.7.10', '流火，六月十三夜，有火光如昼，自西南来，着于明伦堂梁栋间，更许乃隐。清康照');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙十一年七月十四日', '1672.8.6', '夜，交河有白气，自西南向东北，其疾如飞，声如风。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙匹十六年十一月初三', '1707.11.26', '黄昏，天鸣如鼓，异光照地。清雍正上海');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙五十六年四月．十五日', '1717.5.25', '星变。异光烛野，起东南，亘西北，其形如船。清康熙浙江');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙五十六年四月十六日', '1717.5.26', '晚，西南白气起，直冲斗口，忽变如团月色，又转而黄，光射如昼，移时始散。清乾隆安徽');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清圣祖康熙五十六年四月中', '1717.5.21-5.30', '1080数人玩月，坐科甲坊下，忽电光闪烁，迷离不辨，仰视天上，开裂三丈有奇，一赤色飞鸟，悬空倒舞，经时乃没。民国四川');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清世宗雍正十二年', '1734', '夜半，火光自天而下，约数亩，六畜皆惊。清同治河北');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清高宗乾隆二年正月十二日', '1737.2.11', '酉时，大星带小星，有芒如雾。清乾隆广东');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清高宗乾隆八年十一月朔', '1743.12.7', '酉时，巨星有光，两旁各带一小星。清乾隆广东');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清高宗乾隆十七年四月二十九日', '1752.6.11', '卯时，有火光自西而东。清乾隆陕西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清高宗乾隆二十三年七月初七', '1758.8.10', '寅时，东西两星合，倏化光焰，长五尺许，逾时始灭。清乾隆广东');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清高宗乾隆五十年二月', '1785.3.11-4.8', '西北有星，光烛天地如月。清光绪江苏');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清宣宗道光十一年十月初八', '1831', '未刻，兑方天鼓鸣，同时有火光，清光绪江苏');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清宣宗道光十二年春', '1832．春', '有火龙自北而南，火光烛地。清同治湖北');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('夜，天门阴，光射地如白昼。', '1841.10.6', '清光绪');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清宣宗道光二十二年四月', '1842.5.10-6.8', '天矢星见于西方。民，国上海');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清宣宗道光二十九年八月二十日', '1849.10.6', '夜，见空中赤毽大小五，绵亘不断，坠而复上。清光绪');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清宣宗道光三十年十二月二十三日', '1851.1.24', '夜二鼓，天黑如漆，忽隆隆有声，天际有十数金圈，勾连缠绕，西南角涌一红珠，照地光明如昼，约数刻倏然不见，依旧昏黑如漆。清同治四川');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清文宗咸丰二年八月十七日', '1852.10.10', '有白气自空下坠大平桥街上，须臾红若火。流星清光绪浙江');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清文宗成丰三年正月二十二日', '1853.3.1', '子前，天黑云，微雨。迄子，忽声如裂帛，划然中开，宽数丈，约刻余，东南冉冉相合，南北直如金线，俄而金光散布，一天皆赤，下映于地。．民国山西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清文宗咸丰五年七月初四后越数日', '1855.8.16后数日', '有星赤色，大如盂，见东南，半刻乃没。清光绪广东');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清文宗咸丰六年三月二十六日', '1856.4.30', '辰刻，有一大星吐光如白练，星，其光烛地。清宣统云南');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清文宗咸丰六年十有一月', '1856.11..28-12.26', '时近五更，遥阐东方，3忽声如霹雳，裂口十余丈，宽六尺许，中如电光闪烁，阅寸香时顷合。清光绪');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清文宗咸丰九年十一月十一日', '1859.11.5', '夜半，天忽明，发巨声如雷。民国广东');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清文宗咸丰十一年二月', '1861.3.11-4.9', '黄昏时，声若雷震，红光烛天。清光绪上海');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清穆宗同治元年五月', '1862.5.28-6.26', '赤星大如盘，随日而西。民国陕西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清穗宗同治元年八月', '1862.8.25-9.23', '天灯见，．夜静时红光烛天，照耀如昼。清光绪山西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清穆宗同治元年十一月', '1862.12.21-1863.1.18', '普洱有物怪，长数丈，形似蟒，色赤，由东飞落西北。清光绪');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清穆宗同治初', '1862-1864', '谈者忘其年月，漳城外有人嚣宿渠垅，阉割然一声，天忽开裂，形如枣核，横亘子天，光彩闲烁，中深黑色不见底，糨.l起视，人多见者。清光绪甘肃');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清穆宗同治四年四月十五日', '1865.5.9', '天裂如十字，无云而雷。民国山东');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清穆宗同治十年六月二十六日', '1871.8.12', '有星昼见，过午官。清光绪湖北');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清穆宗同治十三年', '1874', '日昃，有星现于中天。清宣统广东');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪九年二月二十二日', '1883.3.30', '戌时，空中有火光，大如斗．。清宣统河南');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪十二年七月', '1886.7.31-8.28', '夜分，有物状如龙，无首尾，鳞甲长八、．九丈许，明光烛地，缘山谷高下蜿蜒而去。民国陕西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪十三年正月，初九', '1887.2.1', '晚，瞥见一大火球，旋绕三周，向西北飞去。民国河南');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪十八年四月', '1892.4.27-5.25', '瓮安猪场于夜半时，忽有一物，从西北方飞落，其形如鸟，有五彩，移时不见。民国贵州');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪二十年=月初五', '1894.3.11', '夜，天炮鸣，忽然四方皆亮如白日，旋即黑。清光绪贵州');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪二十年十二月二十八日', '1895.1.23', '夜雷，有光见南方如火，大于斗。、民国河南');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪二十三年五月戊午', '1897.6.13', '夕，南乐有火光，圃可径尺，飞往西南．');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪二十三年八月初旬t', '1897.8.28-9.6', '夜，四更，空际有震响声，天顶忽裂，阏丈余，金光灿，烂，能照秋毫，居民未寝者，见庙脊绘像蕴眉宛然，行人早起亦壕相传告，俗以为天门开云。民国四川');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪二十六年七月壬戊', '1900.8.16', NULL);
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪二十九年九月初旬', '1903.10.20-29', '一更向尽，忽群犬吠空，出视，见白虹二。道，自西南亘东北，南窄而北，宽，窄如过许，宽三倍，两端尖锐，飒飒有声，光亚白昼，刻许始没。民国辽宁');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清末帝宣统三年四月', '1911.4.29-5.27', '有星大如斗，长丈余。民国河南');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('宋神宗熙宁二年十一月', '1069.11.17-12.16', '每夕有赤气见西北隅，如火，至人定乃灭。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('金卫绍王大安三年十月己卯', '1211.11.71', '每夜初更，正东、西北天明如月初出，经月乃灭。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明世宗嘉靖四年七月十八日', '1525.8.23', '赤气亘天，自是月十八日起至次年九月，日未出之光及既入之后，皆有赤气如虹，虽雨也然。清乾隆广东');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明世宗嘉靖四年十二月至五年夏秋', '1525.12.14-1526.10.5', '日落之时，赤红异色蔽天，西北方如火光凝薄，或至经夜不散。清康熙湖北');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明世宗嘉靖三十一年九月', '1552.9.18-10.17', '日将晡，西方赤气亘天不散者百余日。清康熙浙江');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明嘉靖三十一年秋末', '1552．秋末', '日晡时，西有赤气直天，至暝不散。清康熙浙江');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历五年九月晦', '1577.11.9', '日入时赤气障天。清康熙江西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明毅宗崇祯六年', '1633', '日出没有赤气，状如炎风。清康熙河南');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明毅宗崇桢九年，', '1636', '日出入赤气如血。清康熙河北');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明毅宗崇祯十年自秋徂冬至次年春杪', '1637．秋-1638．春', '旦晚赤气弥天，月色亦赤。明崇祯江苏');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明毅宗崇祯十年九月初', '1637.10.16-20', '赤气朝夕见，自本年九月初渐见，朝东夕西，大至逾天之半。清康熙河北');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明毅宗崇祯十年九月中旬', '1637.10.26-11.5', '每晨暮，天色赤黄，名日日空．');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明毅宗崇祯十年九月', '1637.10.16-11.15', '每晨夕，天色赤黄。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明毅宗崇祯十年十月', '1637.11.16-12.15', '日落红甚，有黑赤光相荡。明崇祯湖南');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明毅宗崇祯十一年夏', '1638．夏', '夕时，有赤气如火亘天，凡三月。清顺治山东');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明毅宗崇祯十一年秋冬间', '1638．秋、冬', '每日晡，西方赤气亘天。清乾隆河南');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明毅宗崇祯十一年冬', '1638．冬', '旦晚赤气弥天。清康熙江苏');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明毅宗祟祯十二年九月', '1639.9.27-10.25', '连年日晡时，红光烛天。清乾隆浙江');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明毅宗崇祯十三年五月至七月', '1640.6.19-9.15', '每晨，天红如赭。清康熙');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明毅宗崇祯十六年冬月', '1643.12.11-1644.1.9', '每夕天西半尽赤。清康熙山东');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清世祖顺治九年正月', '1652.2.10-3.9', '每夕红气亘天如虹。清康熙湖北');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清高宗乾隆五十年腊月下旬', '1785.1.20-29', '上乡薄暮东南方火光烛天，亘十余里。清同治江西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清宣宗道光十七年元旦', '1837.2.5', '约四更许，忽东南如晚霞映日，火光灼天，少顷复暗又复明，如此者三天。清同治湖南');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清文宗咸丰二年正月初旬', '1852.2.20-3.1', '将旦，满天红光如火，移时乃散。清光绪陕西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清文宗咸丰二年十一月二十七日', '1853.1.6', '日入，忽见赤光烛地，照见人影。清光绪江苏');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清文宗咸丰三年正月十九日', '1853.2.26', '寅时，天红如血。民国山西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清文宗咸丰三年正月二十日', '1853.2.27', '天赤如血，日无光，雨土。清光绪河北');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清文宗咸丰三年正月二十一日', '1853.2.28', '晚昏暗，咫尺不辨人，寅初天色殷红，雨赤土二三分，有血瘢痕如钱，辰刻日色始如常。民国山西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清文宗咸丰三年正月二十二', '1853.2.29', '自朝至暮，天色昏红似镫光。清光绪山西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪二年秋冬', '1876．秋．冬', '日落时，日旁现黄丹色，至来春始淡。民国山西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪四年四月五日至二十八日', '1878.5.6-29', '日月出入时有赤色。清光绪上海');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪九年七月至十月', '1883.8.3-11.29', '日入红光烛天，天晓亦有光稍淡。清光绪江苏');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪九年八月', '1883.9.1-30', '日落后西边天红，一年皆然。清光绪云南');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪九年九月', '1888.10.1-30', '有红光见于天，朝东晚西，数月方息。清光绪安徽');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪九年九月至十年三月', '1883.10.1-1884.4.24', '日入后红光烛天。清光绪上海');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪九年十月初一日前一日', '1883.10.30', '亥初，天赤如火，从此朝暮也如之，翌年正月止。民国河北');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪九年十月朔', '1883.10.31', '日入后西南赤气上蒸，更余始尽，如是者数月。民国云南');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪九年十月', '1883.10.31-11.29', '日将出，天光如赭，凡五十余日。清光绪陕西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪九年冬月', '1883.11.30-12.28', '暮天赤色，有黄气见于西方，数月不绝。清光绪湖北');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪癸未（九年）冬', '1883．冬', '西南方见红光烛天，未、申时起，至戌、亥时始散。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪十年冬', '1884．冬', '红光烛天，晨夕两见，朝先日出而红于东方，晚乘日入而红于西方，数月始绝。清光绪浙江');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪二十年', '1894', '每夕天际彩霞万道，红光亘天，历八日之久。民国江苏');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清德宗光绪二十一年二月初三日', '1895.2.27', '丑时，大通县天红，辰时方止。清宣统');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('汉景帝后三年十月', '前141.11.16-12.14', '日月皆赤五日。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('汉景帝后三年十二月', '前140.1.14-2.11', '日如紫。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('汉元帝永光元年四月', '前43.5.7-6.5', '日色青白，亡景，正中时有景亡光。是夏寒。至九月，日乃有光。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('汉成帝河平元年二月癸未', '前28.4.4', '日朝赤，且入又赤，夜，月赤。');
