INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES (NULL, '1434', '安崇善启曰：“谨稽古事，汉武帝建元四年，天雨粟；宣帝地节三年，長安雨黑粟，元康四年雨黑黍；光武建武二十一年，陈留雨谷。今正月初，交雪雨下黑色之物，状如黑粟，令上林园、沈藏库种之。”从之。');
