INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES (NULL, '1470', '全罗、庆尚两道，雨土。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES (NULL, '1470', '大妃曰：“闻下三道雨土，然乎？”克增曰：“臣亦传闻之。但非灾异，故不启耳。”李崇元启曰：“昨日，臣闻僧守眉言，古有雨土，其年大有。信知其言，则此是丰年之祥，而何乃旱如是？”');
