INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES (NULL, '1478', '上曰：“蔡沉《书》序言帝王治乱之由详矣，予尝畏天之威，小心谨慎。然今有雨土之变，无乃未副天心欤？”昌孙曰：“今非徒雨土，又有地震，古人以地震为变之大。”');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES (NULL, '1478', '近日地震雨土，灾变相继，殿下方引咎自责，求言之命纔下，而复令张乐设宴，甚非惧灾谨天之意也。');
