INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES (NULL, '1516', '司谏文瑾启李长生事，又启朴祥等事曰：“臣闻，成庙因雨土求言，朱溪正深源上疏，请勿用世祖朝之臣。”');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES (NULL, '1516', '传曰：“近来灾变荐臻，顷又义州雨土，至为未安。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES (NULL, '1518', '京畿阳城雨土，大风拔树木，偃麻麦。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES (NULL, '1520', '全罗道光州等十三邑雨雪；顺天陨霜；海南、康津黄雾晦暝，雨雹雨土。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明世宗嘉靖元年正月丁卯', '1522', '雨黄沙。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES (NULL, '1523', '京城雨土，京畿骊州、杨根亦雨土');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES (NULL, '1524', '黄海道康翎县雨土。');
