INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES (NULL, '1526', '是日雨土雪。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES (NULL, '1527', '忠清道清安县下雪，全罗道康津雨土，黄海道谷山、瑞兴下雪霜，庆尚道醴泉下雪，平安道宁边、云山、熙川下霜。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES (NULL, '1527', '大雨雹，以风且雨土。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES (NULL, '1528', '夜，雨土、雨雹。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES (NULL, '1528', '黄海道海州，京畿富平、阳智、衿川等邑雨雹，江原道铁原府雨雹，忠清道庇仁、木川、新昌、镇川、堤川等县，全罗道高敞、兴德、井邑等县，長兴、锦山等郡雨土。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES (NULL, '1528', '忠清道青阳、鸿山、天安、沃川、清风、石城、扶余、怀德等邑雨雹，丹阳雨雹、雨土。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES (NULL, '1529', '雨土。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES (NULL, '1530', '领议政郑光弼、左议政沈贞、右议政李荇启曰：“近来时令不顺。···又闻咸镜道明川县境，雨血，非特洒之而已，至于人马足迹，几至盈焉。至为惊愕。···使之改纪。”');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES (NULL, '1530', '弘文馆副提学柳溥等上箚曰：今二月十七日，地震、雨血于明川，血色大紅，人马足迹几盈，不胜骇愕。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES (NULL, '1530', '京都，雨土，雨雹。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES (NULL, '1531', '雨土。');
