INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES (NULL, '1554', '传曰：“今见庆尚监司书状，有雨谷之灾。如此非常之变，不知缘何事以致之？罔知攸措。”');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES (NULL, '1555', '传于政院曰：“今见观象监单子，今年亦雨谷种。年年如是，未知厥由，忧惧罔极。”');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES (NULL, '1555', '咸镜道钟城，未时日色深黄，其散射处，黄色深浓，食顷还消，申时复作，移时还消。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES (NULL, '1555', '日出时，日色甚赤。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES (NULL, '1556', '庆尚道陕川日晕两傍，复有二日并生，日色或黄或黑，良久乃灭。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES (NULL, '1556', '日色黄薄，左右傍有戟气，色青白。');
