INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明穆宗隆庆二年三月', '1568', '陜西西宁卫天鼓鸣，汉中府南郑县雨土。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明穆宗隆庆二年四月', '1568', '天雨黑豆。');
