INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历五年六月', '1577.6.16-7.14', '天雨黑黍。清雍正湖南');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历十四年二月晦', '1586', '天雨土，即密室中无不飏入，几案间有积厚至一二寸者。清乾隆浙江');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历二十五年二月癸亥', '1597', '湖州雨黄沙。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明神宗万历二十五年', '1597', '夜，雨黑雨。清康熙浙江');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES (NULL, '1608', '顷日天乃雨土、雨草实、雨雹，又出流星白气之变，上帝仁爱所以警告殿下者，至深切矣。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES (NULL, '1609', '雨土于连山，咫尺不辨。');
