INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明光宗泰昌元年十二月壬申', '1620', '是岁山东省城及泰安肥城皆雨土。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明毅宗崇祯五年九月', '1632.10.14-11.11', '雨黑子如粟。明崇祯湖南');
