INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明毅宗崇祯七年二月', '1634.2.28-3.28', '文兴雨泥。清康熙');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明毅宗崇祯七年', '1634', '天雨黑子形如五谷。清顺治湖北');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明毅宗崇祯八年二月初一日', '1635.3.19', '日赤如血，雨灰。清康熙陕西');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明庄烈帝祟祯八年', '1635', '凤翔县雨灰三日。清康熙');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明毅宗崇祯十年', '1637', '山东雨黑水，新乡亦如之。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明毅宗崇祯十一年三月癸巳', '1638', '是月新乡雨黑水。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('清太宗崇德二年三月戊午', '1637', '是日天色黄暗雨土。');
INSERT INTO `javadatabase`(`年号干支纪年`, `公元纪年`, `天象记录`) VALUES ('明毅宗崇祯十一年三月朔', '1638.4.14', '白雾罩城三昼夜，天雨小珠，纯黑为粟。清嘉庆湖南');
